import ol from 'openlayers'
import $ from 'jquery'

import { cssClasses, keyCodes } from './globals'
import { Debug } from './Debug'

/**
 * @typedef {object} APIMapInteraction
 * @property {function} cancel ends the interaction. The result promise will not resolve.
 * @property {function} end ends the interaction properly. The result promise will resolve if possible.
 * @property {Promise} result a promise that represents the value of the interaction.
 */

// NOTE:
// Access to a source factory would be nice

/**
 * @typedef {object} APIOptions
 * @property {StyleLike} [drawStyle='#drawStyle']
 */

export class API extends ol.Object {
  /**
   * @param {G4UMap} map
   * @param {object} options
   */
  constructor (map, options = {}) {
    super()

    /**
     * @type {boolean}
     * @private
     */
    this.featureManipulationActive_ = false

    /**
     * @type {StyleLike}
     * @private
     */
    this.drawStyle_ = options.drawStyle || '#drawStyle'

    /**
     * @type {ol.format.WKT}
     * @private
     */
    this.wktParser_ = new ol.format.WKT()

    /**
     * @type {G4UMap}
     * @private
     */
    this.map_ = map

    /**
     * @type {?ol.interaction.Interaction}
     * @private
     */
    this.featureManipulationInteraction_ = null

    this.map_.once('ready', () => {
      this.layerConfigurator_ = this.map_.get('configurator').layerConfigurator_
    })

    $(this.map_.getViewport()).on('keydown', this.onKeyDown_.bind(this))
  }

  // //////////////  FEATURE MANIPULATION ////////////////

  endFeatureManipulationInternal_ () {
    if (this.featureManipulationInteraction_) {
      this.featureManipulationInteraction_.setActive(false)

      this.map_.removeInteraction(this.featureManipulationInteraction_)
    }

    $(this.map_.getViewport()).removeClass(cssClasses.crosshair)
    $(this.map_.getViewport()).removeClass(cssClasses.arrow)
    // and any other cursor changes

    this.featureManipulationActive_ = false

    this.dispatchEvent('endManipulation')
  }

  /**
   * cancel the current feature manipulation
   */
  cancelFeatureManipulation () {
    if (this.featureManipulationActive_) {
      this.endFeatureManipulationInternal_()
    }
  }

  /**
   * draw a feature
   * @param {object} [options={}]
   * @param {StyleLike} [options.style]
   * @param {string} [options.type='Point'] possible values are: 'Point', 'LineString', 'Polygon', 'MultiPoint',
   *  'MultiLineString', 'MultiPolygon' or 'Circle'
   * @returns {APIMapInteraction}
   */
  drawFeature (options = {}) {
    if (this.featureManipulationActive_) {
      this.endFeatureManipulationInternal_()
    }

    this.featureManipulationActive_ = true

    let collection = new ol.Collection()

    let styleConf = (options.style || this.drawStyle_) || {}

    let style = this.map_.get('styling').getStyle(styleConf)

    this.map_.get('styling').manageFeatureCollection(collection)

    this.featureManipulationInteraction_ = new ol.interaction.Draw({
      features: collection,
      type: options.type || 'Point',
      style: style
    })

    this.map_.addSupersedingInteraction('singleclick dblclick pointermove', this.featureManipulationInteraction_)

    $(this.map_.getViewport()).addClass(cssClasses.crosshair)

    return {
      cancel: () => {
        this.endFeatureManipulationInternal_()
      },
      end: () => {
        this.featureManipulationInteraction_.finishDrawing()
        this.endFeatureManipulationInternal_()
      },
      result: new Promise(resolve => {
        this.featureManipulationInteraction_.on('drawend', e => {
          resolve(e.feature)
          this.endFeatureManipulationInternal_()
        })
      })
    }
  }

  fitRectangle (coordinates, opt = {}) {
    if (!opt.hasOwnProperty('srId')) {
      opt.srId = 'EPSG:4326'
    }
    if (!opt.hasOwnProperty('constrainResolution')) {
      opt.constrainResolution = false
    }
    if (!opt.hasOwnProperty('padding')) {
      opt.padding = [0, 0, 0, 0]
    }
    if (ol.proj.get(opt.srId)) {
      this.map_.getView().fit(
        ol.extent.boundingExtent(
          [
            ol.proj.transform(
              [parseFloat(coordinates[0][0]), parseFloat(coordinates[0][1])],
              opt.srId,
              this.map_.get('mapProjection').getCode()
            ),
            ol.proj.transform(
              [parseFloat(coordinates[1][0]), parseFloat(coordinates[1][1])],
              opt.srId,
              this.map_.get('mapProjection').getCode()
            )
          ]
        ),
        opt
      )
    } else {
      Debug.error(`Unknown Projection '${opt.srId}'`)
    }
  }

  setVisibleBaseLayer (id) {
    this.map_.get('baseLayers').recursiveForEach((layer) => {
      layer.setVisible(layer.get('id') === id)
    })
  }

  onKeyDown_ (e) {
    if (this.featureManipulationActive_ && e.which === keyCodes.ESCAPE) {
      this.endFeatureManipulationInternal_()
    }
  }

  /**
   * Select a feature with a single click
   * @returns {APIMapInteraction}
   */
  selectFeature () {
    if (this.featureManipulationActive_) {
      this.endFeatureManipulationInternal_(null)
    }

    this.featureManipulationActive_ = true

    this.featureManipulationInteraction_ = new ol.interaction.Select()

    this.map_.addSupersedingInteraction('singleclick', this.featureManipulationInteraction_)

    $(this.map_.getViewport()).addClass(cssClasses.arrow)

    return {
      cancel: () => {
        this.endFeatureManipulationInternal_()
      },
      end: () => {
        this.endFeatureManipulationInternal_()
      },
      result: new Promise((resolve) => {
        this.featureManipulationInteraction_.getFeatures().on('add', /** ol.CollectionEvent */ e => {
          resolve(e.element)
          this.endFeatureManipulationInternal_()
        })
      })
    }
  }

  /**
   * Modify a given Feature. The end function needs to be called to indicate that a modifying process is completed.
   * @param {ol.Collection<ol.Feature>|ol.Feature[]|ol.Feature} feature
   * @param {Object} options
   * @param {StyleLike} [options.style]
   * @returns {APIMapInteraction}
   */
  modifyFeature (feature, options = {}) {
    options.features = new ol.Collection([feature])

    if (this.featureManipulationActive_) {
      this.endFeatureManipulationInternal_(false)
    }

    this.featureManipulationActive_ = true

    if (options.style) {
      options.style = this.map_.get('styling').getStyle(options.style)
    }

    this.featureManipulationInteraction_ = new ol.interaction.Modify(options)

    this.map_.addSupersedingInteraction('singleclick dblclick pointermove', this.featureManipulationInteraction_)

    $(this.map_.getViewport()).addClass(cssClasses.crosshair)

    let ended = false

    return {
      cancel: () => {
        this.endFeatureManipulationInternal_()
      },
      end: () => {
        ended = true
        this.endFeatureManipulationInternal_()
      },
      result: new Promise(resolve => {
        this.featureManipulationInteraction_.on('modifyend', () => {
          if (ended) {
            resolve(feature)
          }
        })

        this.featureManipulationInteraction_.once('change:active', () => {
          if (ended) {
            resolve(feature)
          }
        })
      })
    }
  }

  /**
   * This function creates a layer from the given layerOptions and adds it the map
   * @param {g4uLayerOptions} layerOptions
   * @returns {VectorLayer}
   */
  addFeatureLayer (layerOptions) {
    return this.layerConfigurator_.getFactory()
      .addLayer(this.map_.get('featureLayers'), layerOptions, 'featureLayer', true)
  }

  /**
   * This function creates a layer from the given layerOptions and adds it as a fixedFeatureLayer to the map
   * @param {g4uLayerOptions} layerOptions
   * @returns {VectorLayer}
   */
  addFixedFeatureLayer (layerOptions) {
    return this.layerConfigurator_.getFactory()
      .addLayer(this.map_.get('fixedFeatureLayers'), layerOptions, 'featureLayer', true)
  }

  /**
   * This function creates a base layer from the given layerOptions and adds it to the map
   * @param {g4uLayerOptions} layerOptions
   * @returns {ol.layer.Base}
   */
  addBaseLayer (layerOptions) {
    return this.layerConfigurator_.getFactory()
      .addLayer(this.map_.get('baseLayers'), layerOptions, 'baseLayer', true)
  }

  /**
   * This function creates a layer from the given layerOptions, adds it as a VectorLayere and returns a promise which
   * is resolved as soon as the layer is loaded fully.
   * @param {g4uLayerOptions} layerOptions
   * @returns {Promise.<VectorLayer>}
   */
  loadLayerFromServer (layerOptions) {
    layerOptions = layerOptions || {}
    layerOptions.visible = true
    layerOptions.source = layerOptions.source || {}

    let promise = new Promise((resolve, reject) => {
      let layer = this.addFeatureLayer(layerOptions)
      let source = layer.getSource()
      let loadEndHandler = () => {
        source.un('vectorloadend', loadErrorHandler)
        resolve(layer)
      }
      let loadErrorHandler = () => {
        source.un('vectorloaderror', loadEndHandler)
        reject('vector load error')
      }
      source.once('vectorloadend', loadEndHandler)
      source.once('vectorloaderror', loadErrorHandler)
    })

    return promise
  }

  /**
   * Creates a Feature from the given config
   * @param {FeatureConfig} config
   * @returns {ol.Feature}
   */
  createFeature (config) {
    return this.layerConfigurator_.getFactory().createFeature(config)
  }

  /**
   * Removes a layer from the map
   * @param {ol.layer.Base} layer
   */
  removeLayer (layer) {
    this.map_.getLayerGroup().removeLayer(layer)
  }
}

import { groupBy, map } from 'lodash/collection'
import { ListenerOrganizerMixin } from './ListenerOrganizerMixin'
import { GroupLayer } from './layers/GroupLayer'
import { uniq } from 'lodash/array'
import { debounce } from 'lodash/function'
import { mixin } from './utilities'

import ol from 'openlayers'

function groupByChain (col) {
  return {
    groupBy: (cb) => {
      return groupByChain(groupBy(col, cb))
    },
    forEach: (cb) => {
      return groupByChain(col.forEach(cb))
    },
    map: (cb) => {
      return groupByChain(map(col, cb))
    },
    asArray: () => {
      return col
    }
  }
}

export class Attributions extends mixin(ol.Observable, ListenerOrganizerMixin) {
  constructor () {
    super()
    /**
     * @type {string[]}
     * @private
     */
    this.attributions_ = []

    this.debouncedUpdate = debounce(() => this.update())
  }

  /**
   * @param {G4UMap} map
   */
  setMap (map) {
    if (this.map_) {
      this.attributions_ = []
      this.detachAllListeners()
    }

    this.map_ = map

    if (map) {
      map.asSoonAs('ready:layers', true, () => {
        setTimeout(() => {
          this.attachListeners(this.map_.getLayerGroup())
          this.update()
        }, 0)
      })
    }
  }

  getArray () {
    return this.attributions_
  }

  scanLayers () {
    return this.scanLayer(this.map_.getLayerGroup())
  }

  scanLayer (layer, layerTitle = null) {
    if (layer && layer.getVisible()) {
      if (layer.getLayers) {
        let silentGroup = !(layer instanceof GroupLayer)
        return layer.getLayers().getArray().reduce((arr, nextLayer) => {
          layerTitle = silentGroup ? layer.get('title') : null
          return arr.concat(this.scanLayer(nextLayer, layerTitle))
        }, [])
      } else {
        if (layer.getVisible()) {
          let label = layer.isBaseLayer
            ? this.map_.get('localiser').localiseUsingDictionary('Attribution baseLayerLabel')
            : (layerTitle || layer.get('title'))
          if (label) {
            return this.extractAttributions(layer, this.labelize(label))
          }
        }
      }
    }
    return []
  }

  extractAttributions (layer, label) {
    if (layer.getSource && layer.getSource()) {
      let attributions = layer.getSource().getAttributions()
      if (attributions) {
        return attributions.map(a => [label, a.getHTML()])
      }
      return []
    }
  }

  labelize (title) {
    return title.replace(/<[^>]*>/, ' ').replace(/\s{2,}/, ' ')
  }

  update () {
    this.attributions_ = groupByChain(this.scanLayers())
      .groupBy(([layerTitle, attribution]) => {
        return layerTitle
      }) // -> { layerTitle: [[layerTitle, attribution]*] }
      .map((val, key) => {
        return [key, val.map(p => p[1])]
      }) // -> [[layerTitle, attribution*]*]
      .groupBy(([layerTitle, attributions]) => {
        attributions = uniq(attributions)
        if (attributions.length === 1) {
          return attributions[0]
        } else {
          return attributions.join(' & ')
        }
      })
      .map((val, key) => {
        return [val.map(p => p[0]), key]
      })
      .map(([layerTitles, attributionText]) => {
        let text
        if (layerTitles.length === 1) {
          text = layerTitles[0]
        } else {
          text = layerTitles.slice(0, -1).join(', ') + ' & ' + layerTitles[layerTitles.length - 1]
        }
        text += ': ' + attributionText
        return text
      })
      .asArray()
    this.dispatchEvent('change', this.attributions_)
  }

  attachListeners (layer) {
    if (layer.getLayers) {
      layer.getLayers().forEach(l => this.attachListeners(l))
      this.listenAt(layer.getLayers())
        .on('add', e => {
          this.attachListeners(e.element)
          this.debouncedUpdate()
        })
        .on('remove', e => {
          this.detachFrom(e.element)
          this.debouncedUpdate()
        })
    }

    if (!(layer instanceof GroupLayer)) {
      this.listenAt(layer)
        .on('change:visible', () => {
          this.debouncedUpdate()
        })
        .on('change:source', () => {
          if (layer.getVisible()) {
            this.debouncedUpdate()
          }
        })
    }
  }
}

import ol from 'openlayers'

import {Attribution} from '../controls/Attribution'
import {ComposedControl} from '../controls/ComposedControl'
import {ArrowButtons} from '../controls/ArrowButtons'
import {CombinedZoom} from '../controls/CombinedZoom'
import {LayerSelector} from '../controls/LayerSelector'
import {GeolocationButton} from '../controls/GeoLocationButton'
import {MeasurementButton} from '../controls/MeasurementButton'
import {LanguageSwitcherButton} from '../controls/LanguageSwitcherButton'
import {LanguageSwitcherMenu} from '../controls/LanguageSwitcherMenu'
import {MobileControls} from '../controls/MobileControls'
import {InfoButton} from '../controls/InfoButton'
import {LinkButton} from '../controls/LinkButton'
import {PrintButton} from '../controls/PrintButton'
import {Logo} from '../controls/Logo'
import {HelpButton} from '../controls/HelpButton'
import {WindowDecorator} from '../controls/WindowDecorator'

import { asObject, checkFor } from '../utilities'
import { copyDeep } from '../utilitiesObject'

import {G4UMap} from '../G4UMap'

import {Debug} from '../Debug'
import {ActiveGroup} from '../controls/ActiveGroup'
import {MousePosition} from '../controls/MousePosition'
import {ScaleLine} from '../controls/ScaleLine'
import {OverviewMap} from '../controls/OverviewMap'
import { SingleDrawButton } from '../controls/SingleDrawButton'

/**
 *  Inside of this object the controls are configured. Normally they are referenced by their type, but if
 *  you have multiple controls of one type you can specify a name under which it is referenced. Then you
 *  have to specify the `controlType` in the configuration object.
 *
 *  Example:
 *  let `'example'` be the chosen name for an attribution control. Then we can reference it in `onMap` or
 *  any `contains` property as `'example'`. The configuration object is now named `'example'` but needs to
 *  have an `'controlType'` property which needs to be set to `'attribution'`.
 *
 *  ```
 *    "controls": {
 *      "onMap": ["example"],
 *      "example": {
 *        "controlType": "attribution",
 *        ...
 *      }
 *    }
 *  ```
 *
 * @typedef {object} ControlsConfig
 * @property {string[]} onMap All controls mentioned in this array will be added directly to the map.
 * @property {ArrowButtonOptions} [arrowButtons]
 * @property {AttributionOptions} [attribution]
 * @property {CombinedZoomOptions} [combinedZoom]
 * @property {GeoLocationButtonOptions} [geolocationButton]
 * @property {HelpButtonOptions} [helpButton]
 * @property {InfoButtonOptions} [infoButton]
 * @property {LanguageSwitcherButtonOptions} [languageSwitcherButton]
 * @property {LanguageSwitcherMenuOptions} [languageSwitcherMenu]
 * @property {LayerSelectorOptions} [layerSelector]
 * @property {LinkButtonOptions} [linkButton]
 * @property {LogoOptions} [logo]
 * @property {MeasurementButtonOptions} [measurementButton]
 * @property {ShiftableComposedControlOptions} [mobileControls]
 * @property {MousePositionOptions} [mousePosition]
 * @property {OverviewMapOptions} [overviewMap]
 * @property {g4uControlOptions} [printButton]
 * @property {ScaleLineOptions} [scaleLine]
 * @property {DrawButtonsOptions} [singleDrawButton]
 * @property {ComposedControlOptions} [toolbox]
 * @property {ComposedControlOptions} [layerMenu]
 */

/**
 * @typedef {Object} ControlFactoryOptions
 * @property {L10N} localiser
 * @property {G4UMap} map
 */

/**
 * A Factory that takes a control config as an object and returns the correspondig control. Modules can expand the
 * functionality of this class via the createControl method
 */
export class ControlFactory {
  /**
   * @param {ControlFactoryOptions} options
   * @public
   */
  constructor (options) {
    /**
     * @type {L10N}
     * @private
     * */
    this.localiser_ = options.localiser

    /**
     * @type {G4UMap}
     * @private
     */
    this.map_ = options.map

    /**
     * @type {Map.<string, ActiveGroup>}
     * @private
     */
    this.activeGroups_ = new Map()
  }

  /**
   * @returns {Positioning}
   */
  getPositioning () {
    return this.map_.get('controlPositioning')
  }

  /**
   * The factory function
   * @param {string} controlType
   * @param {g4uControlOptions} options
   * @returns {Control}
   * @public
   */
  createControl (controlType, options) {
    switch (controlType) {
      case 'mousePosition':
        options.projection = options.projection || this.map_.get('interfaceProjection')
        return new MousePosition(options)
      case 'attribution':
        return new Attribution(options)
      case 'zoom':
        return new CombinedZoom(options)
      case 'scaleLine':
        return new ScaleLine(options)
      case 'logo':
        return new Logo(options)
      case 'linkButton':
        return new LinkButton(options)
      case 'mobileControls':
        return new MobileControls(options)
      case 'overviewMap':
        let projection = options.projection || this.map_.get('mapProjection')
        options.view = new ol.View({projection: projection})
        return new OverviewMap(options)
      case 'infoButton':
        return new WindowDecorator({
          component: new InfoButton(options)
        })
      case 'arrowButtons':
        let mapConfig = this.map_.get('mapConfig')
        if (mapConfig.view) {
          if (mapConfig.view.center) {
            options.initCenter = ol.proj.transform(mapConfig.view.center,
              this.map_.get('interfaceProjection'), this.map_.get('mapProjection'))
          }
          if (mapConfig.view.zoom) {
            options.initZoom = mapConfig.view.zoom
          }
        }
        return new ArrowButtons(options)
      case 'layerSelector':
        return new LayerSelector(options)
      case 'geolocationButton':
        return new GeolocationButton(options)
      case 'languageSwitcherButton':
        return new LanguageSwitcherButton(options)
      case 'languageSwitcherMenu':
        return new LanguageSwitcherMenu(options)
      case 'printButton':
        return new PrintButton(options)
      case 'distanceMeasurementButton':
        options.className = options.className || 'g4u-distance-measurement'
        options.type = 'LineString'
        options.dimension = 1
        return new WindowDecorator({
          component: new MeasurementButton(options)
        })
      case 'areaMeasurementButton':
        options.className = options.className || 'g4u-area-measurement'
        options.type = 'Polygon'
        options.dimension = 2
        return new WindowDecorator({
          component: new MeasurementButton(options)
        })
      case 'helpButton':
        options.configControls = this.map_.get('mapConfig').controls
        return new WindowDecorator({
          component: new HelpButton(options)
        })
      case 'toolbox':
        options.className = options.className || 'g4u-toolbox'
        return new ComposedControl(options)
      case 'layerMenu':
        options.className = options.className || 'g4u-layermenu'
        return new ComposedControl(options)
      case 'singleDrawButton':
        return new SingleDrawButton(options)
    }
  }

  /**
   * Function to add a control by its name to a certain receiver
   * @param {ComposedControl|G4UMap} receiver
   * @param {string} controlName
   * @public
   */
  addControlTo (receiver, controlName) {
    /**
     * @type {g4uControlOptions}
     */
    let config = copyDeep(asObject(this.controlsConfig[controlName]))

    let controlType = config.controlType || controlName

    config.controlName = controlName

    config.localiser = this.localiser_

    if (!config.hasOwnProperty('importance') && receiver.getImportance) {
      config.importance = receiver.getImportance()
    }

    let control = this.createControl(controlType, config, receiver)

    let modules = this.map_.getModules()

    for (let i = 0, ii = modules.length; i < ii && (control === undefined); i++) {
      control = modules[i].createControl(controlType, config, receiver)
    }

    if (control) {
      if (config.activeGroup) {
        if (!this.activeGroups_.has(config.activeGroup)) {
          this.activeGroups_.set(config.activeGroup, new ActiveGroup())
        }
        this.activeGroups_.get(config.activeGroup).addControl(control)
      }

      if (!this.map_.controlsByName[controlName]) {
        this.map_.controlsByName[controlName] = [control]
      } else {
        this.map_.controlsByName[controlName].push(control)
      }

      receiver.addControl(control)

      if (config.contains) {
        this.addControlMultipleInternal_(control, config.contains)
      }

      if (control.rewire) {
        control.rewire()
      }

      if (receiver instanceof G4UMap) {
        this.getPositioning().addControl(control)
      }
    } else if (control === undefined) {
      throw new Error('Unrecognized control type ' + controlType +
        ', maybe you did forget to set the property type of the control?')
    }
  }

  /**
   * Adds multiple controls to one receiver
   * @param {ComposedControl|G4UMap} receiver
   * @param {string[]} controlNames
   * @private
   */
  addControlMultipleInternal_ (receiver, controlNames) {
    controlNames.forEach(controlName => {
      Debug.tryOrThrow(() => {
        this.addControlTo(receiver, controlName)
      })
    })
  }

  /**
   * Add all configured Controls to the map
   * @public
   */
  addControls () {
    /**
     * @type {ControlsConfig}
     */
    this.controlsConfig = this.map_.get('mapConfig').controls
    if (checkFor(this.controlsConfig, 'onMap')) {
      this.addControlMultipleInternal_(this.map_, this.controlsConfig.onMap)
    }
  }

  /**
   * Sets the localiser to pass on to the controls
   * @param {L10N} loc
   * @public
   */
  setLocaliser (loc) {
    this.localiser_ = loc
  }
}

import ol from 'openlayers'

import { GroupLayer } from '../layers/GroupLayer'
import { copyDeep } from '../utilitiesObject'
import { checkFor } from '../utilities'
import { Debug } from '../Debug'
import { LayerFactory } from './LayerFactory'
import { Attributions } from '../Attributions'

/**
 * @typedef {Object} LayerConfig
 * @property {AnyLayerConfig[]} baseLayers
 * @property {AnyLayerConfig[]} featureLayers
 * @property {AnyLayerConfig[]} fixedFeatureLayers
 * @property {AnyLayerConfig[]} queryLayers
 */

/**
 * This is part of the MapConfigurator class
 */
export class LayerConfigurator {
  /**
   * @param {G4UMap} map
   * @public
   */
  constructor (map) {
    /**
     * @type {G4UMap}
     * @private
     */
    this.map_ = map
    this.map_.on('change:layerConfig', this.configureLayers.bind(this))

    /**
     * @type {LayerFactory}
     * @private
     */
    this.layerFactory_ = new LayerFactory(map)
  }

  /**
   * configures the layers according to the layerConfig stored in the 'layerConfig' property stored in the map. If the
   * config changes this method is called automatically.
   * @public
   */
  configureLayers () {
    this.map_.set('ready:layers', false)

    if (!(this.map_.getLayerGroup() instanceof GroupLayer)) {
      this.map_.setLayerGroup(new GroupLayer())
    }

    let layers = this.map_.getLayerGroup()
    layers.getLayers().clear()

    /**
     * @type {LayerConfig}
     */
    let layerConfigCopy = copyDeep(this.map_.get('layerConfig'))

    /**
     * @type {MapConfig}
     */
    let mapConfig = this.map_.get('mapConfig')

    this.map_.set('layerIds', []) // in layerIds all ids are stored to check if one is double.

    this.layerFactory_.setMapConfigProjection(
      mapConfig.hasOwnProperty('mapProjection') ? ol.proj.get(mapConfig.mapProjection) : null
    )

    this.map_.set('loadingStrategy',
      mapConfig.hasOwnProperty('loadingStrategy') ? mapConfig.loadingStrategy : 'ALL'
    )

    this.map_.set('ignoreLayerAvailability',
      mapConfig.hasOwnProperty('ignoreLayerAvailability') ? mapConfig.ignoreLayerAvailability : false
    )

    // //////////////////////////////////////////////////////////////////////////////////////////
    //                                      Baselayers                                        //
    // //////////////////////////////////////////////////////////////////////////////////////////

    // baseLayers before the view, because they determine the projection of the view.

    /**
     * @type {GroupLayer}
     * @private
     */
    this.baseLayerGroup_ = this.map_.get('baseLayers') || new GroupLayer()

    this.baseLayerGroup_.getLayers().clear()

    if ((layerConfigCopy.hasOwnProperty('baseLayers')) && (layerConfigCopy.baseLayers instanceof Array)) {
      Debug.tryOrThrow(() => {
        this.layerFactory_.addLayers(this.baseLayerGroup_, layerConfigCopy.baseLayers, 'baseLayer')
      })

      if (this.baseLayerGroup_.getLayers().getLength() === 0) {
        if (layerConfigCopy.baseLayers.length !== 0) {
          let confCp = copyDeep(layerConfigCopy.baseLayers[0])
          confCp.alwaysVisible = true
          this.layerFactory_.addLayers(this.baseLayerGroup_, confCp, 'baseLayer', true)
        } else {
          Debug.warn('There is no baselayer available!')
        }
      }

      let visibleBaseLayer

      this.baseLayerGroup_.recursiveForEach(function (layer) {
        if (!(layer instanceof GroupLayer)) {
          visibleBaseLayer = visibleBaseLayer || layer.getVisible()
        }
      })

      if (!visibleBaseLayer && this.baseLayerGroup_.getLayers().getLength()) {
        let setFirstVisible = layer => {
          if (layer instanceof GroupLayer) {
            setFirstVisible(layer.getLayersArray()[0])
          } else {
            layer.setVisible(true)
          }
        }

        setFirstVisible(this.baseLayerGroup_)
      }

      if (!this.map_.get('baseLayers')) {
        this.baseLayerGroup_.getLayers().on('remove', e => {
          if (this.baseLayerGroup_.getLayers().getLength() > 0) {
            if (e.element.getVisible()) {
              this.baseLayerGroup_.getLayers().item(0).setVisible(true)
            }
          }
        })
      }

      this.map_.set('baseLayers', this.baseLayerGroup_)
      layers.getLayers().push(this.baseLayerGroup_)
    } else {
      Debug.warn('The mapConfig option \'baseLayers\' is not set or not an Array!')
    }

    // if no baselayer had a given projection, choose 'EPSG:3857'
    this.map_.set('mapProjection', this.layerFactory_.getMapProjection() || ol.proj.get('EPSG:3857'))

    // //////////////////////////////////////////////////////////////////////////////////////////
    //                                    FeatureLayers                                       //
    // //////////////////////////////////////////////////////////////////////////////////////////

    /**
     * @type {GroupLayer}
     */
    let featureLayers = this.map_.get('featureLayers') || new GroupLayer()

    featureLayers.getLayers().clear()

    this.layerFactory_.addLayers(featureLayers, layerConfigCopy.featureLayers || [], 'featureLayer')

    this.map_.set('featureLayers', featureLayers)
    layers.getLayers().push(featureLayers)

    // //////////////////////////////////////////////////////////////////////////////////////////
    //                                 FixedFeatureLayers                                     //
    // //////////////////////////////////////////////////////////////////////////////////////////

    /**
     * @type {GroupLayer}
     */
    let fixedFeatureLayers = this.map_.get('fixedFeatureLayers') || new GroupLayer()

    fixedFeatureLayers.getLayers().clear()

    if (checkFor(layerConfigCopy, 'fixedFeatureLayers')) {
      this.layerFactory_.addLayers(fixedFeatureLayers, layerConfigCopy.fixedFeatureLayers, 'featureLayer')

      this.map_.set('fixedFeatureLayers', fixedFeatureLayers)
      layers.getLayers().push(fixedFeatureLayers)
    }

    // //////////////////////////////////////////////////////////////////////////////////////////
    //                                      QueryLayer                                        //
    // //////////////////////////////////////////////////////////////////////////////////////////

    /**
     * @type {GroupLayer}
     */
    let queryLayers = this.map_.get('queryLayers') || new GroupLayer()

    queryLayers.getLayers().clear()

    if ((layerConfigCopy.hasOwnProperty('queryLayers')) && (layerConfigCopy.queryLayers instanceof Array)) {
      this.layerFactory_.addLayers(queryLayers, layerConfigCopy.queryLayers, 'queryLayer', true)

      this.map_.set('queryLayers', queryLayers)
      layers.getLayers().push(queryLayers)
    }

    // //////////////////////////////////////////////////////////////////////////////////////// //
    //                                   All layers loaded                                      //
    // //////////////////////////////////////////////////////////////////////////////////////// //

    let loadingLayers = []
    let isLoadingDelayed = false
    let isLoadingPrecise = false

    let forEachLayer = (layer) => {
      if (layer.getLayers) {
        layer.getLayers().on('add', e => {
          forEachLayer(e.element)
        })
        layer.getLayers().forEach(forEachLayer)
      }
      if (layer.isLoading) {
        if (layer.isLoading()) {
          if (!isLoadingDelayed) {
            isLoadingDelayed = true
            this.map_.dispatchEvent('layersloadstart')
            this.map_.set('allLayersLoaded', false)
          }
          isLoadingPrecise = true
          loadingLayers.push(layer)
        }
      }
      layer.on('loadcountstart', () => {
        if (!isLoadingDelayed) {
          isLoadingDelayed = true
          this.map_.dispatchEvent('layersloadstart')
          this.map_.set('allLayersLoaded', false)
        }
        isLoadingPrecise = true
        loadingLayers.push(layer)
      })
      layer.on('loadcountend', () => {
        loadingLayers.splice(loadingLayers.indexOf(layer), 1)
        if (loadingLayers.length === 0) {
          isLoadingPrecise = false
          this.map_.afterPostrender(() => {
            if (!isLoadingPrecise) {
              this.map_.dispatchEvent('layersloadend')
              isLoadingDelayed = false
              this.map_.set('allLayersLoaded', true)
            }
          })
          this.map_.render()
        }
      })
    }

    forEachLayer(this.map_.getLayerGroup())

    this.map_.set('allLayersLoaded', false)

    let layerLoadStarted = false

    this.map_.once('layersloadstart', () => {
      layerLoadStarted = true
    })

    this.map_.once('postrender', () => {
      this.map_.once('postrender', () => {
        if (!layerLoadStarted) {
          this.map_.set('allLayersLoaded', true)
        }
      })
    })

    /** Attributions */

    let attributions = this.map_.get('attributions')
    if (!attributions) {
      attributions = new Attributions()
      this.map_.set('attributions', attributions)
    }
    attributions.setMap(this.map_)

    this.map_.set('ready:layers', true)
  }

  /**
   * @returns {GroupLayer}
   */
  getBaseLayerGroup () {
    return this.baseLayerGroup_
  }

  /**
   * @returns {LayerFactory}
   */
  getFactory () {
    return this.layerFactory_
  }
}

import ol from 'openlayers'
import $ from 'jquery'

import {BaseLayerImage, ImageLayer} from '../layers/ImageLayer'
import {EmptyBaseLayer} from '../layers/EmptyBaseLayer'
import {BaseTileLayer} from '../layers/BaseTileLayer'
import {TileLayer} from '../layers/TileLayer'
import {GroupLayer} from '../layers/GroupLayer'
import {VectorLayer} from '../layers/VectorLayer'
import {SourceServerVector} from '../sources/SourceServerVector'
import {QueryVectorSource} from '../sources/QueryVectorSource'
import { copyDeep, mergeDeep, take } from '../utilitiesObject'
import { asObject, checkFor } from '../utilities'

import {Debug} from '../Debug'
import {ImageWMSSource, TileWMSSource} from '../sources/ImageWMSSource'
import { BaseSilentGroupLayer, SilentGroupLayer } from '../layers/SilentGroupLayer'
import { URL } from '../URLHelper'
import { QueryImageWMSSource, QueryTileWMSSource } from '../sources/QueryWMSSource'
import { ClusterSource } from '../sources/ClusterSource'

/**
 * @enum {string}
 */
export const SuperType = {
  BASELAYER: 'baseLayer',
  FEATURELAYER: 'featureLayer',
  QUERYLAYER: 'queryLayer'
}

/**
 * @enum {string}
 */
export const LayerType = {
  CATEGORY: 'Category',
  SILENTGROUP: 'SilentGroup',
  GEOJSON: 'GeoJSON',
  KML: 'KML',
  WMS: 'WMS',
  TILEWMS: 'TileWMS',
  WMTS: 'WMTS',
  OSM: 'OSM',
  STAMEN: 'Stamen',
  INTERN: 'Intern',
  EMPTY: 'Empty',
  XYZ: 'XYZ',
  BING: 'Bing'
}

/**
 * @typedef {SilentGroupLayerConfig|CategoryLayerConfig|EmptyLayerConfig|XYZLayerConfig|OSMLayerConfig
 *    |StamenLayerConfig|BingLayerConfig|WMTSLayerConfig|WMSLayerConfig|TileWMSLayerConfig|GeoJSONLayerConfig
 *    |KMLLayerConfig|InternalLayerConfig} AnyLayerConfig
 */

/**
 * Common config for all layers.
 * @public
 * @typedef {Object} g4uLayerOptions
 * @property {LayerType} type The layer type.
 * @property {string|number} id unique in the whole config
 * @property {string} [title]
 * @property {Boolean} [available] if set to false, the layer will not appear on the map or the layer selector.
 * @property {Boolean} [availableMobile] overwrites available in mobile mode
 * @property {Boolean} [visible=false] If set to `true` the layer will be visible on startup.
 * @property {Boolean} [alwaysVisible] overwrites visible, available and mobileAvailable
 * @property {StyleLike} [style]
 */

/**
 * The silent group can display a group of layers which appears as a single in the layer selector.
 * @typedef {g4uLayerOptions} SilentGroupLayerConfig
 * @property {"SilentGroup"} type
 * @property {AnyLayerConfig[]} layers
 */

/**
 * The category contains other layer(s) and will appear as a category in the layer selector.
 * @typedef {g4uLayerOptions} CategoryLayerConfig
 * @property {"Category"} type
 * @property {AnyLayerConfig[]} layers
 */

/**
 * The empty layer will show nothing (in case of a base layer a white background).
 * @typedef {g4uLayerOptions} EmptyLayerConfig
 * @property {"Empty"} type
 */

/**
 * A XYZ Layer (See http://openlayers.org/en/latest/apidoc/ol.source.XYZ.html).
 * @typedef {g4uLayerOptions} XYZLayerConfig
 * @property {"XYZ"} type
 * @property {SourceConfig} source
 */

/**
 * An OSM Layer (See http://openlayers.org/en/latest/apidoc/ol.source.OSM.html).
 * @typedef {g4uLayerOptions} OSMLayerConfig
 * @property {"OSM"} type
 * @property {SourceConfig} source
 */

/**
 * A stamen Layer (See http://openlayers.org/en/latest/apidoc/ol.source.Stamen.html).
 * @typedef {g4uLayerOptions} StamenLayerConfig
 * @property {"Stamen"} type
 * @property {SourceConfig} source
 */

/**
 * A bing maps Layer (See http://openlayers.org/en/latest/apidoc/ol.source.BingMaps.html).
 * @typedef {g4uLayerOptions} BingLayerConfig
 * @property {"Bing"} type
 * @property {SourceConfig} source
 */

/**
 * A WMTS Layer. Check the {WMTSSSourceConfig}.
 * @typedef {g4uLayerOptions} WMTSLayerConfig
 * @property {"WMTS"} type
 * @property {WMTSSSourceConfig} source
 */

/**
 * A WMS Layer. Check the {WMSSSourceConfig}.
 * @typedef {g4uLayerOptions} WMSLayerConfig
 * @property {"WMS"} type
 * @property {WMSSSourceConfig} source
 */

/**
 * A WMS Layer which is called like a tiled layer. Good for performance. Check the {WMSSSourceConfig}.
 * @typedef {g4uLayerOptions} TileWMSLayerConfig
 * @property {"TileWMS"} type
 * @property {WMSSSourceConfig} source
 */

/**
 * A GeoJSON Layer.
 * @typedef {g4uLayerOptions} GeoJSONLayerConfig
 * @property {"GeoJSON"} type
 * @property {VectorSourceConfig} source
 * @property {StyleLike} [style]
 */

/**
 * A KML Layer.
 * @typedef {g4uLayerOptions} KMLLayerConfig
 * @property {"KML"} type
 * @property {VectorSourceConfig} source
 * @property {StyleLike} [style]
 */

/**
 * A layer whichs contents are completly defined in the config file.
 * @typedef {g4uLayerOptions} InternalLayerConfig
 * @property {"Intern"} type
 * @property {InternalSourceConfig} source
 * @property {StyleLike} [style]
 */

/**
 * A source config.
 * @typedef {Object} SourceConfig
 * @property {Localizable} [attribution]
 * @property {URLLike} url
 */

/**
 * A vector source config.
 * @typedef {SourceConfig} VectorSourceConfig
 * @property {string} [loadingStrategy] "BBOX" or "ALL"
 * @property {number} [bboxRatio] only applies if loadingStrategy is BBOX. If bigger than 1 this much more will be
 *    loaded around a bbox.
 * @property {boolean} [localised=false] if set to true the loader will send accept-language headers.
 */

/**
 * A wmts source config. The config must contain a `config` object that must contain a `layer`` parameter. For other
 *    parameters see: http://openlayers.org/en/latest/apidoc/ol.source.WMTS.html#.optionsFromCapabilities. All other
 *    needed Parameters will be obtained automatically.
 * @typedef {SourceConfig} WMTSSSourceConfig
 * @property {object} config
 */

/**
 * A wms source config. The config must contain a `params` object that must contain a `LAYERS` parameter. For other
 *    parameters see: http://openlayers.org/en/latest/apidoc/ol.source.ImageWMS.html -> Constructor options -> params.
 * @typedef {SourceConfig} WMSSSourceConfig
 * @property {object} params
 */

/**
 * An internal source whose features are defined directly in the config file.
 * @typedef {SourceConfig} InternalSourceConfig
 * @property {FeatureConfig[]} features
 */

/**
 * @typedef {Object} FeatureConfig
 * @property {string|number} id
 * @property {StyleLike} [style]
 * @property {string} [geometryWKT]
 * @property {ol.geom.Geometry} [geometry]
 */

/**
 * This class constructs a layer according to the given {{LayerOptions}}
 */
export class LayerFactory {
  /**
   * @param {G4UMap} map
   */
  constructor (map) {
    /**
     * @type {G4UMap}
     * @private
     */
    this.map_ = map
  }

  /**
   * @param {ol.ProjectionLike} mapConfigProjection
   */
  setMapConfigProjection (mapConfigProjection) {
    this.mapProjection = mapConfigProjection
  }

  /**
   * @returns {ol.ProjectionLike}
   */
  getMapProjection () {
    return this.mapProjection
  }

  /**
   * Adds multiple layers according to their configs to the given layerGroup
   * @param {LayerGroup} layerGroup
   * @param {g4uLayerOptions[]} configs
   * @param {string} superType
   * @param {Boolean} skipIdCheck
   * @returns {ol.Layer.Base[]}
   */
  addLayers (layerGroup, configs, superType, skipIdCheck) {
    return configs.map(config => this.addLayer(layerGroup, config, superType, skipIdCheck))
  }

  /**
   * Adds a layer defined by a given config to a given layerGroup
   * @param {LayerGroup} layerGroup
   * @param {g4uLayerOptions} options
   * @param {string} superType
   * @param {Boolean} skipIdCheck
   * @returns {ol.layer.Base}
   */
  addLayer (layerGroup, options, superType, skipIdCheck) {
    let layer
    /**
     * @type {g4uLayerOptions}
     */
    let optionsCopy = copyDeep(options)

    if (skipIdCheck || this.configureLayerIsIdOk_(optionsCopy.id)) {
      // loading strategy

      if (!optionsCopy.type) {
        throw new Error(`Layer needs a type. Layer id: ${optionsCopy.id}. Layer title: ${optionsCopy.title}.`)
      }

      // availability
      this.configureLayerAvailability_(optionsCopy)

      // the title/name of the layer
      this.configureLayerTitle_(optionsCopy)

      if (superType === SuperType.BASELAYER) {
        optionsCopy.groupLayer = this.map_.get('layerConfigurator').getBaseLayerGroup()
      }

      if (optionsCopy.source) {
        this.configureLayerSourceAttribution_(optionsCopy.source)
      }

      // visibility
      this.configureLayerVisibility_(optionsCopy)

      layer = this.createLayer(optionsCopy.type, optionsCopy, superType, skipIdCheck)

      let forEachLayer = layer => {
        layerGroup.getLayers().push(layer)
      }

      if (layer instanceof ol.Collection) {
        layer.forEach(forEachLayer)
      } else {
        forEachLayer(layer)
      }
    }

    return layer
  }

  /**
   * @param {string} type
   * @param {string} superType
   */
  superTypeNotSupported (type, superType) {
    throw new Error(`${type} is not configured for superType '${superType}'`)
  }

  /**
   * Creates a layer or a collection of layers from the config
   * @param {string} layerType
   * @param {g4uLayerOptions} optionsCopy
   * @param {string} superType
   * @param {Boolean} skipIdCheck
   * @returns {ol.layer.Layer|ol.Collection.<ol.layer.Layer>}
   */
  createLayer (layerType, optionsCopy, superType, skipIdCheck) {
    if (superType === SuperType.QUERYLAYER) {
      if (!optionsCopy.hasOwnProperty('apiKey')) {
        Debug.error('Each query layer needs to have an apiKey.')
      }
      optionsCopy.source.projection = this.mapProjection
    }

    if (optionsCopy.source) {
      optionsCopy.source.localiser = this.map_.get('localiser')
    }

    let layer
    let layerConfigs
    let clusterOptions
    let localised = false
    if (optionsCopy.source) {
      optionsCopy.source.localiser = this.map_.get('localiser')
      localised = optionsCopy.source.localised
    }

    let style = take(optionsCopy, 'style')

    switch (layerType) {
      case LayerType.SILENTGROUP:

        layerConfigs = take(optionsCopy, 'layers')

        if (superType === SuperType.BASELAYER) {
          layer = new BaseSilentGroupLayer(optionsCopy)
        } else {
          layer = new SilentGroupLayer(optionsCopy)
        }

        this.addLayers(layer, layerConfigs, superType, true)

        layer.getLayers().forEach(childLayer => {
          childLayer.setVisible(true)
        })
        break
      case LayerType.CATEGORY:
        layerConfigs = take(optionsCopy, 'layers')

        layer = new GroupLayer(optionsCopy)

        this.addLayers(layer, layerConfigs, superType, skipIdCheck)

        // availability & parent ref
        let childrenAvailable = false
        layer.getLayers().forEach(childLayer => {
          childLayer.set('category', layer)
          childrenAvailable = childrenAvailable || childLayer.get('available')
        })

        let childrenCount = layer.getLayers().getLength()

        if (childrenAvailable !== false) {
          if ((layer.get('available') !== false) && (childrenCount > 0)) {
            // category is shown

            if (childrenAvailable === true) {
              layer.set('available', true)
            }

            layer.setVisible(true)

            if (superType === SuperType.BASELAYER) {
              layer.set('activateChildren', false)
            }

            return layer
          } else if (childrenAvailable === true) {
            // only children are shown
            return layer.getLayers()
          }
          // else neither are shown
        }
        break
      case LayerType.EMPTY:
        if (superType === SuperType.BASELAYER) {
          layer = new EmptyBaseLayer(optionsCopy)
        } else {
          optionsCopy.source = new ol.source.Vector()
          layer = new VectorLayer(optionsCopy)
        }
        break
      case LayerType.XYZ:
        optionsCopy.source.url = URL.extractFromConfig(optionsCopy.source, 'url', undefined, this.map_).finalize()
        optionsCopy.source = new ol.source.XYZ(optionsCopy.source)

        if (superType === SuperType.BASELAYER) {
          layer = new BaseTileLayer(optionsCopy)
        } else {
          layer = new TileLayer(optionsCopy)
        }
        break
      case LayerType.OSM:
        optionsCopy.source.url = URL.extractFromConfig(optionsCopy.source, 'url', undefined, this.map_).finalize()
        optionsCopy.source = new ol.source.OSM(optionsCopy.source)

        if (superType === SuperType.BASELAYER) {
          layer = new BaseTileLayer(optionsCopy)
        } else {
          layer = new TileLayer(optionsCopy)
        }
        break
      case LayerType.STAMEN:
        optionsCopy.source = new ol.source.Stamen(optionsCopy.source)

        if (superType === SuperType.BASELAYER) {
          layer = new BaseTileLayer(optionsCopy)
        } else {
          layer = new TileLayer(optionsCopy)
        }
        break
      case LayerType.BING:
        optionsCopy.source = new ol.source.BingMaps(optionsCopy.source)

        if (superType === SuperType.BASELAYER) {
          layer = new BaseTileLayer(optionsCopy)
        } else {
          layer = new TileLayer(optionsCopy)
        }
        break
      case LayerType.WMS:
        if (optionsCopy.categoryButtons) {
          optionsCopy.source.params.LAYERS = []
        }

        optionsCopy.source.url = URL.extractFromConfig(optionsCopy.source, 'url', undefined, this.map_) // not finalized

        if (superType === SuperType.BASELAYER) {
          optionsCopy.source = new ImageWMSSource(optionsCopy.source)
          layer = new BaseLayerImage(optionsCopy)
        } else if (superType === SuperType.QUERYLAYER) {
          optionsCopy.visible = false
          optionsCopy.source = new QueryImageWMSSource(optionsCopy.source)
          layer = new ImageLayer(optionsCopy)
        } else {
          optionsCopy.source = new ImageWMSSource(optionsCopy.source)
          layer = new ImageLayer(optionsCopy)
        }

        if (layer.getSource().hasFeatureInfo()) {
          this.map_.asSoonAs('ready:ui', true, () => {
            if (this.map_.get('showWMSFeatureInfo')) {
              this.map_.get('showWMSFeatureInfo').addLayer(layer)
            }
          })
        }

        break
      case LayerType.TILEWMS:
        if (optionsCopy.source.tileSize) {
          optionsCopy.source.tileGrid = ol.tilegrid.createXYZ({ tileSize: optionsCopy.source.tileSize })
          delete optionsCopy.source.tileSize
        }

        optionsCopy.source.url = URL.extractFromConfig(optionsCopy.source, 'url', undefined, this.map_) // not finalized

        if (superType === SuperType.BASELAYER) {
          optionsCopy.source = new TileWMSSource(optionsCopy.source)
          layer = new BaseTileLayer(optionsCopy)
        } else if (superType === SuperType.QUERYLAYER) {
          optionsCopy.visible = false
          optionsCopy.source = new QueryTileWMSSource(optionsCopy.source)
          layer = new TileLayer(optionsCopy)
        } else {
          optionsCopy.source = new TileWMSSource(optionsCopy.source)
          layer = new TileLayer(optionsCopy)
        }

        if (layer.getSource().hasFeatureInfo()) {
          this.map_.asSoonAs('ready:ui', true, () => {
            if (this.map_.get('showWMSFeatureInfo')) {
              this.map_.get('showWMSFeatureInfo').addLayer(layer)
            }
          })
        }

        break
      case LayerType.WMTS:
        let sourceOptions = take(optionsCopy, 'source')

        // url gets extracted in setWMTSSourceFromCapabilities

        if (superType === SuperType.BASELAYER) {
          layer = new BaseTileLayer(optionsCopy)
        } else {
          layer = new TileLayer(optionsCopy)
        }

        this.setWMTSSourceFromCapabilities(layer, sourceOptions)

        break
      case LayerType.GEOJSON:
        this.configureLayerSourceLoadingStrategy_(optionsCopy.source)
        optionsCopy.source.url = URL.extractFromConfig(optionsCopy.source, 'url', undefined, this.map_) // not finalized

        optionsCopy.source.defaultStyle = this.map_.get('styling').getStyle(style || '#defaultStyle')

        optionsCopy.source.type = 'GeoJSON'

        optionsCopy.source.bboxProjection = optionsCopy.source.bboxProjection || this.map_.get('interfaceProjection')

        optionsCopy.source.styling = this.map_.get('styling')

        clusterOptions = take(optionsCopy.source, 'cluster')

        if (superType === SuperType.QUERYLAYER) {
          optionsCopy.visible = false
          optionsCopy.source = new QueryVectorSource(optionsCopy.source)
        } else {
          optionsCopy.source = new SourceServerVector(optionsCopy.source)
        }

        if (clusterOptions) {
          clusterOptions = asObject(clusterOptions)
          optionsCopy.source = new ClusterSource(optionsCopy.source, clusterOptions)
        }
        layer = new VectorLayer(optionsCopy)
        break
      case LayerType.KML:
        this.configureLayerSourceLoadingStrategy_(optionsCopy.source)
        optionsCopy.source.url = URL.extractFromConfig(optionsCopy.source, 'url', undefined, this.map_) // not finalized

        optionsCopy.source.defaultStyle = this.map_.get('styling').getStyle(style || '#defaultStyle')

        optionsCopy.source.type = 'KML'

        optionsCopy.source.bboxProjection = optionsCopy.source.bboxProjection || this.map_.get('interfaceProjection')

        optionsCopy.source.styling = this.map_.get('styling')

        clusterOptions = take(optionsCopy.source, 'cluster')

        if (superType === SuperType.QUERYLAYER) {
          optionsCopy.visible = false
          optionsCopy.source = new QueryVectorSource(optionsCopy.source)
        } else {
          optionsCopy.source = new SourceServerVector(optionsCopy.source)
        }

        if (clusterOptions) {
          optionsCopy.source = new ClusterSource(optionsCopy.source, asObject(clusterOptions))
        }
        layer = new VectorLayer(optionsCopy)
        break
      case LayerType.INTERN:

        if (optionsCopy.source.hasOwnProperty('features')) {
          for (let i = 0; i < optionsCopy.source.features.length; i++) {
            optionsCopy.source.features[i] = this.createFeature(optionsCopy.source.features[i])
          }
        }

        optionsCopy.source = new ol.source.Vector(optionsCopy.source)

        if (superType === SuperType.QUERYLAYER) {
          this.superTypeNotSupported(layerType, superType)
        } else {
          layer = new VectorLayer(optionsCopy)
        }
        break
    }

    for (let module of this.map_.getModules()) {
      if (layer) {
        break
      }
      layer = module.createLayer(optionsCopy.type, optionsCopy, superType, skipIdCheck)
    }

    if (!layer) {
      throw new Error(`layer with type '${optionsCopy.type}' could not be created.`)
    }

    // styling
    if (layer.setStyle) {
      layer.setStyle(this.map_.get('styling').getStyle(style))
      this.map_.get('styling').manageLayer(layer)
    }

    // if layer is a baselayer, check for mapProjection
    if (superType === SuperType.BASELAYER && optionsCopy.source) {
      if (layer.getSource().getProjection()) {
        if (!this.mapProjection) {
          this.mapProjection = layer.getSource().getProjection()
        } else if (this.mapProjection && this.mapProjection !== layer.getSource().getProjection()) {
          Debug.warn('The baseLayers are not in mapProjection or a baseLayers has a different projection than' +
             ' another! This might cause reprojection issues.')
        }
      }
    } else if (superType === SuperType.QUERYLAYER) {
      this.map_.get('urlApi').addApiLayer(layer, optionsCopy.apiKey)
    }

    // if layer is being localised, refresh on language change
    if (localised) {
      this.map_.get('localiser').on('change:language', () => layer.getSource().refresh())
    }

    return layer
  }

  /**
   * A function that checks if a layer id is setted and not a duplicate of another.
   * Throws an error if this is the case.
   * @param {string|number} id
   * @private
   */
  configureLayerIsIdOk_ (id) {
    /**
     * @type {string[]|number[]}
     */
    let layerIds = this.map_.get('layerIds')

    if (id === 0 || (id && (typeof id === 'string' || !isNaN(id)))) {
      for (let j = 0, jj = layerIds.length; j < jj; j++) {
        if (id === layerIds[j]) {
          Debug.error(`Each layer needs a unique id! Otherwise the layer won't be shown. Layer id: ${id}`)
          return false
        }
      }
      layerIds.push(id)
      this.map_.set('layerIds', layerIds)
      return true
    } else {
      Debug.error(`Each layer needs a unique id! Otherwise the layer won't be shown. Layer id: ${id}`)
      return false
    }
  }

  /**
   * @param {g4uLayerOptions} config
   * @returns {g4uLayerOptions}
   * @private
   */
  configureLayerAvailability_ (config) {
    // if available is set to false explicitely, the layer won't be available
    config.available = (config.available !== false)

    if (!config.alwaysVisible) {
      config.available = config.hasOwnProperty('availableMobile') ? config.availableMobile : config.available
      if (this.map_.get('ignoreLayerAvailability')) {
        config.available = true
      }
    }
    return config
  }

  /**
   * @param {SourceConfig} sourceConfig
   * @returns {SourceConfig}
   * @private
   */
  configureLayerSourceAttribution_ (sourceConfig) {
    if (checkFor(sourceConfig, 'attribution')) {
      sourceConfig.attributions = [
        new ol.Attribution({
          html: this.map_.get('localiser').selectL10N(sourceConfig.attribution)
        })
      ]
    }
    return sourceConfig
  }

  /**
   * @param {SourceConfig} sourceConfig
   * @returns {SourceConfig}
   * @private
   */
  configureLayerSourceLoadingStrategy_ (sourceConfig) {
    sourceConfig.loadingStrategy = sourceConfig.hasOwnProperty('loadingStrategy')
      ? sourceConfig.loadingStrategy
      : this.map_.get('loadingStrategy')
    return sourceConfig
  }

  /**
   * @param {g4uLayerOptions} config
   * @returns {g4uLayerOptions}
   * @private
   */
  configureLayerTitle_ (config) {
    if (!config.hasOwnProperty('title')) {
      config.title = 'No title given'
    } else {
      config.title = this.map_.get('localiser').selectL10N(config.title)
    }
    return config
  }

  /**
   * @param {g4uLayerOptions} config
   * @returns {g4uLayerOptions}
   * @private
   */
  configureLayerVisibility_ (config) {
    if (config.alwaysVisible === true) {
      config.visible = true
    } else {
      config.visible = (config.visible === true)
    }

    return config
  }

  /**
   * @param {FeatureConfig} featureConf
   * @returns {ol.Feature}
   */
  createFeature (featureConf) {
    /**
     * @type {FeatureConfig}
     */
    let featureConfCopy = copyDeep(featureConf)

    let id = take(featureConfCopy, 'id')

    let style = take(featureConfCopy, 'style')

    let format = new ol.format.WKT()
    let wkt = take(featureConfCopy, 'geometryWKT') || take(featureConfCopy, 'geographyWKT')
    featureConfCopy.geometry = format.readGeometry(wkt)
      .transform(this.map_.get('interfaceProjection'), this.map_.get('mapProjection'))

    let feature = new ol.Feature(featureConfCopy)

    if (style) {
      this.map_.get('styling').styleFeature(feature, style)
    }

    if (id) {
      feature.setId(id)
    }

    return feature
  }

  setWMTSSourceFromCapabilities (layer, sourceOptions) {
    const url = URL.extractFromConfig(sourceOptions, 'url', undefined, this.map_)
    const capUrl = url.clone().addParam('Service=WMTS').addParam('Request=GetCapabilities')
    $.ajax({
      url: capUrl.finalize(),
      dataType: 'text xml',
      crossDomain: true,
      success: data => {
        const wmtsCap = (new ol.format.WMTSCapabilities()).read(data)
        const capOptions = ol.source.WMTS.optionsFromCapabilities(wmtsCap, take(sourceOptions, 'config'))
        capOptions.urls = capOptions.urls.map(newUrl => {
          const u = url.clone()
          u.url = newUrl
          return u.finalize()
        })
        sourceOptions = mergeDeep(sourceOptions, capOptions)
        layer.setSource(new ol.source.WMTS(sourceOptions))
      }
    })
    layer.setSource(new ol.source.ImageCanvas({
      state: 'ready',
      canvasFunction: () => {} // not loading any canvas
    }))
  }
}

import ol from 'openlayers'
import $ from 'jquery'
import proj4 from 'proj4/dist/proj4'

ol.proj.setProj4(proj4)

import {Styling} from '../Styling'
import {LayerConfigurator} from './LayerConfigurator'
import {UIConfigurator} from './UIConfigurator'

import { copyDeep } from '../utilitiesObject'
import { checkFor } from '../utilities'

import {API} from '../API'

import {Debug} from '../Debug'

/**
 * @typedef {Object} MapConfig
 * @property {string} [proxy] A proxy url. It should have an {url} string which will be replaced with the proxied url.
 *    Example: 'proxy.php?csurl={url}'.
 * @property {Boolean} [userActionTracking=false] if checked the map tracks certain user actions and
 *    fires 'userActionTracking' events that contain this information. Tracked actions are: move, layer activation,
 *    clicks on features, printing, measurements.
 * @property {L10NOptions} [languageSettings] settings regarding the display language.
 * @property {string} [interfaceProjection='EPSG:4326'] the projection the interface will use to interact with the
 *    user or the api.
 * @property {string} [mapProjection] the projection that is used internally. Will be infered from map data if not set.
 * @property {string} [measurementProjection] the projection measurements will calculated in.
 * @property {boolean} [enableContextMenu=false] enables the contextMenu outside of textinput fields
 * @property {g4uViewOptions} view View options.
 * @property {number} [scaleIcons] a default scaling for all used feature icons
 * @property {number} [hitTolerance=0] a default hit tolerance that will be used for all interactions
 *    (except show wms feature info).
 * @property {boolean} [manageStyles=true] set this to false to disable style managing. This disables scaleIcons,
 *    mobileScaleIcons, feature hiding and adjustable style opacity
 * @property {MobileLayoutOptions} [mobileLayout] special layout options for mobile use.
 * @property {ProjectionConfig[]} [additionalProjections] if any other projections than 'EPSG:4326' or 'EPSG:3857' are
 *    needed they can be specified here.
 * @property {APIOptions} [api] API-Options
 * @property {FeaturePopupOptions} [featurePopup] Options regarding the feature popup.
 * @property {FeatureTooltipOptions} [featureTooltip] Options regarding the feature tooltip.
 * @property {ShowWMSFeatureInfoOptions} [showWMSFeatureInfo] Options regarding WMS GetFeatureInfo.
 * @property {Object.<string, boolean>} [interactions] Specifies which map interactions should be turned on by default.
 *    Possible interactions are: 'doubleClickZoom', 'dragPan' (to which the {KineticOptions} are applied), 'dragRotate',
 *    'dragZoom', 'keyboardPan', 'keyboardZoom', 'mouseWheelZoom', 'pinchRotate', 'pinchZoom'
 * @property {KineticOptions|boolean} [kinetic] This influences the DragPan behaviour. If set to false no kinetic
 *    options are applied, if not set, the defaults are used.
 * @property {MoveOptions} [move] Options regarding the behaviour of movements on the maps.
 * @property {PositioningOptions} [positioning] Options regarding the positioning of the controls.
 * @property {string} [loadingStrategy='ALL'] Global default loading strategy. Can have the values 'BBOX' or 'ALL'.
 * @property {boolean} [ignoreLayerAvailability=false] If set all layers are added to the map, regardless of their
 *    available config option.
 * @property {string} [cssFile] a cssFile to load and insert in the head dynamically.
 * @property {Color[]} [cssTemplate] if 3 colors are given, the colors used in the text of the loaded cssFile will be
 *    replaced by this colors. The colors in the cssFile need to be pure red, blue and green.
 * @property {Object.<string, StyleObject>} [styleMap] the style objects which will be mapped to certain identifiers. It
 *    is recommended that identifiers start with a #. The {{StyleObject}} with the identifier '#defaultStyle' will be
 *    used as a default Style in the whole Software.
 * @property {ControlsConfig} controls
 */

/**
 * @typedef {object} KineticOptions
 * @property {number} [decay]
 * @property {number} [minVelocity]
 * @property {number} [delay]
 */

/**
 * @typedef {Object} ProjectionConfig
 * @property {string} code
 * @property {string} definition
 */

/**
 * @typedef {Object} g4uViewOptions
 * @property {ol.Coordinate} center the initial center of the map
 * @property {ol.Extent} [extent] the max extent the center can lay in
 * @property {number} [resolution]
 * @property {number} [zoom]
 * @property {number} [rotation]
 * @property {number} [fit] an extent to fit the map initialy to, overwrites center settings
 * @property {ol.ProjectionLike} [projection]
 */

/**
 * @typedef {object} MobileLayoutOptions
 * @property {string[]} mediaQueries these will enable the mobile layout including removing the g4u-desktop class from
 *    the ol-viewport and adding the g4u-mobile class
 * @property {number} [scaleIcons=1] a value to scale all icons by
 * @property {boolean} [animations=true] if animations should be disabled
 * @property {number} [hitTolerance]
 */

/**
 * This class configures a map once the configureMap method is called.
 * configureMap initializes the map and can only be called once.
 * it delegates the configureUI and configureLayers to the {{UIConfigurator}} and {{LayerConfigurator}} classes.
 */
export class MapConfigurator {
  /**
   * @param {G4UMap} map
   * @public
   */
  constructor (map) {
    /**
     * @type {G4UMap}
     * @private
     */
    this.map_ = map

    /**
     * @type {LayerConfigurator}
     * @private
     */
    this.layerConfigurator_ = new LayerConfigurator(map)

    /**
     * @type {UIConfigurator}
     * @private
     */
    this.UIConfigurator_ = new UIConfigurator(map)

    map.set('layerConfigurator', this.layerConfigurator_)
    map.set('UIConfigurator', this.UIConfigurator_)

    this.configureMap()

    /**
     * @type {boolean}
     * @private
     */
    this.firstRun_ = false
  }

  /**
   * Delegate call to {{LayerConfigurator}}
   * @public
   */
  configureLayers () {
    this.layerConfigurator_.configureLayers()
  }

  /**
   * Delegate call to {{UIConfigurator}}
   * @public
   */
  configureUI () {
    this.UIConfigurator_.configureUI()
  }

  /**
   * @public
   */
  configureMap () {
    if (this.firstRun_) {
      Debug.error('configureMap is supposed to be called only once')
      Debug.warn('If you would like to change that, please think about something because of the asynchronous nature' +
        ' of "ready", "ready:ui" and "ready:layers"')
      throw new Error()
    }

    /**
     * @type {MapConfig}
     */
    let mapConfigCopy = copyDeep(this.map_.get('mapConfig'))

    if (!mapConfigCopy.enableContextMenu) {
      $(this.map_.getTarget()).on('contextmenu', e => {
        if (!$(e.target).is('input[type=text]') && !$(e.target).is('textarea')) {
          e.preventDefault()
        }
      })
    }

    this.map_.set('userActionTracking', mapConfigCopy.userActionTracking)

    let interfaceProjection = mapConfigCopy.hasOwnProperty('interfaceProjection')
      ? mapConfigCopy.interfaceProjection
      : 'EPSG:4326'

    this.map_.set('interfaceProjection', interfaceProjection)

    // //////////////////////////////////////////////////////////////////////////////////////// //
    //                                       Styling                                            //
    // //////////////////////////////////////////////////////////////////////////////////////// //

    // has to be done before configureLayers ... -> promise?

    let stylingOptions = {}

    if (mapConfigCopy.hasOwnProperty('manageStyles')) {
      stylingOptions.manageStyles = mapConfigCopy.manageStyles
    }

    if (mapConfigCopy.hasOwnProperty('styleMap')) {
      stylingOptions.styleConfigMap = mapConfigCopy.styleMap
    }

    let scaleIcons = mapConfigCopy.hasOwnProperty('scaleIcons') ? mapConfigCopy.scaleIcons : 1
    stylingOptions.scaleIcons = scaleIcons

    this.map_.set('scaleIcons', scaleIcons)
    this.map_.set('styling', new Styling(stylingOptions))

    // //////////////////////////////////////////////////////////////////////////////////////// //
    //                                      Projections                                         //
    // //////////////////////////////////////////////////////////////////////////////////////// //

    let additionalProjectionsConf = mapConfigCopy.hasOwnProperty('additionalProjections')
      ? mapConfigCopy.additionalProjections
      : []

    for (let i = 0, ii = additionalProjectionsConf.length; i < ii; i++) {
      proj4.defs(additionalProjectionsConf[i].code, additionalProjectionsConf[i].definition)
    }

    if (checkFor(mapConfigCopy, 'measurementProjection')) {
      try {
        this.map_.set('measurementProjection', ol.proj.get(mapConfigCopy.measurementProjection))
      } catch (e) {
        throw new Error('measurementProjection is not available, check for spelling or try to add it to' +
          ' additionalProjections with a proj4 definition.')
      }
    }

    this.configureLayers()

    let mapProjection = this.map_.get('mapProjection') // mapProjection is determined by the baseLayers

    // //////////////////////////////////////////////////////////////////////////////////////// //
    //                                    Creating View                                         //
    // //////////////////////////////////////////////////////////////////////////////////////// //

    /**
     * @type {g4uViewOptions}
     */
    let viewOptions = mapConfigCopy.view || {}

    viewOptions.projection = mapProjection

    if (checkFor(mapConfigCopy.view, 'center')) {
      viewOptions.center = ol.proj.transform(mapConfigCopy.view.center, interfaceProjection, mapProjection)
    }

    if (checkFor(mapConfigCopy.view, 'extent')) {
      viewOptions.extent = ol.extent.applyTransform(
        mapConfigCopy.view.extent,
        ol.proj.getTransform(interfaceProjection, mapProjection)
      )
    }

    let oldView = this.map_.getView()
    if (oldView) {
      viewOptions.center = oldView.getCenter() || mapConfigCopy.view.center
      viewOptions.resolution = oldView.getResolution() || mapConfigCopy.view.resolution
      viewOptions.rotation = oldView.getRotation() || mapConfigCopy.view.rotation
    }

    // creating the view
    let view = new ol.View(viewOptions)

    // setting the extent overwrites any settings about zoom and start coordinates
    if (!oldView && checkFor(mapConfigCopy.view, 'fit')) {
      view.fit(ol.proj.transformExtent(mapConfigCopy.view.fit, interfaceProjection, mapProjection), {
        size: this.map_.getSize()
      })
    }

    // //////////////////////////////////////////////////////////////////////////////////////// //
    //                                   Setting View                                           //
    // //////////////////////////////////////////////////////////////////////////////////////// //

    this.map_.setView(view)

    // //////////////////////////////////////////////////////////////////////////////////////// //
    //                                       Resize                                             //
    // //////////////////////////////////////////////////////////////////////////////////////// //

    $(window).on('resize', () => { // NOTE: could get depricated with 'change:size'
      this.map_.dispatchEvent('resize')
    })

    // //////////////////////////////////////////////////////////////////////////////////////// //
    //                              Generic global handlers                                     //
    // //////////////////////////////////////////////////////////////////////////////////////// //

    let $viewport = $(this.map_.getViewport())

    // applying a mousedown class to the viewport if the mouse is down
    let mousedownClass = 'mousedown'
    $viewport.on('mousedown', () => {
      $viewport.addClass(mousedownClass)
    })
    $viewport.on('mouseup', () => {
      $viewport.removeClass(mousedownClass)
    })

    // Sets the Keyboardfocus on the Map
    $viewport.focus()

    // Let the keyboardfocus stay with the Map
    $viewport.on('click', () => {
      $viewport.focus()
    })

    // //////////////////////////////////////////////////////////////////////////////////////// //
    //                             UI (Interactions and Controls)                               //
    // //////////////////////////////////////////////////////////////////////////////////////// //

    Debug.tryOrThrow(() => {
      this.configureUI()
    })

    this.map_.set('api', new API(this.map_, mapConfigCopy.api))

    for (let module of this.map_.getModules()) {
      module.configureMap(mapConfigCopy)
    }
  }
}

import $ from 'jquery'
import uniq from 'lodash/uniq'

import { cssClasses } from '../globals'
import { mixinAsClass } from '../utilities'
import { ListenerOrganizerMixin } from '../ListenerOrganizerMixin'

/**
 * This describes the floating directions of an element. It can be an array, then it will move from the center to the
 * first specified direction and after that it moves in the second direction. If it is set to 'fixed' it is not
 * positioned via Positioning.
 * @typedef {string[]|string} Float
 */

/**
 * @typedef {Object} HideableElement
 * @property {Control} control
 * @property {number} importance
 * @property {number} order
 * @property {Float} [float] first and second direction or special value 'fixed'
 * @property {HideableElement[]} [hideableChildren]
 */

/**
 * @typedef {HideableElement} PositionedElement
 * @property {string[]} float first and second direction or special value 'fixed'
 * @property {number} minWidth
 * @property {number} minHeight
 * @property {{width: number, height: number}} size
 */

/**
 * @typedef {object} PositioningOptions
 * @property {number} [padding=5]
 * @property {number} [spacing=10]
 * @property {HTMLElement} viewport
 */

export class Positioning extends mixinAsClass(ListenerOrganizerMixin) {
  /**
   * @param {PositioningOptions} options
   */
  constructor (options) {
    super()
    /**
     * @type {jQuery|HTMLElement}
     * @private
     */
    this.$viewport_ = $(options.viewport)

    /**
     * The padding between the controls
     * @type {number}
     * @private
     */
    this.padding_ = options.padding || 5

    /**
     * The space between the controls and the edges of the map
     * @type {number}
     * @private
     */
    this.spacing_ = options.spacing || 10

    /**
     * The hidden controle
     * @type {jQuery}
     * @private
     */
    this.hidden$Elements_ = []

    this.init()
  }

  init () {
    this.detachAllListeners()

    /**
     * @type {HideableElement[]}
     * @private
     */
    this.all_ = []

    /**
     * All Controls stored according their position on the map
     * @type {object}
     * @private
     */
    this.corners_ = {
      'left': {
        'top': {
          counterclockwise: [],
          clockwise: []
        },
        'bottom': {
          counterclockwise: [],
          clockwise: []
        }
      },
      'right': {
        'top': {
          counterclockwise: [],
          clockwise: []
        },
        'bottom': {
          counterclockwise: [],
          clockwise: []
        }
      }
    }

    /**
     * This number tracks the order in which controls are added
     * @type {number}
     * @private
     */
    this.order_ = 0
  }

  getArray_ (float) {
    let x, y, direction
    switch (float[0]) {
      case 'top':
        y = 'top'
        x = float[1]
        if (x === 'left') {
          direction = 'clockwise'
        } else if (x === 'right') {
          direction = 'counterclockwise'
        }
        break
      case 'right':
        x = 'right'
        y = float[1]
        if (y === 'top') {
          direction = 'clockwise'
        } else if (y === 'bottom') {
          direction = 'counterclockwise'
        }
        break
      case 'bottom':
        y = 'bottom'
        x = float[1]
        if (x === 'left') {
          direction = 'counterclockwise'
        } else {
          direction = 'clockwise'
        }
        break
      case 'left':
        x = 'left'
        y = float[1]
        if (y === 'top') {
          direction = 'counterclockwise'
        } else {
          direction = 'clockwise'
        }
    }

    return this.corners_[x][y][direction]
  }

  /**
   * Add a control to the positioning.
   * @param {Control} control
   * @param {Object} [parentMeta] the meta information of the parent control
   */
  addControl (control, parentMeta) {
    // check if control needs to be positioned
    if (control.get$Element().parents().hasClass('ol-viewport')) {
      if (!parentMeta || !parentMeta.control.isWindowed()) {
        // gather metainformation
        /** @type {HideableElement} */
        let metaElem = {
          control,
          order: this.order_++,
          importance: control.getImportance()
        }

        control.on('change:visible', e => {
          let index = this.hidden$Elements_.indexOf(control.get$Element())
          if (e.oldValue && index > -1) {
            this.hidden$Elements_.splice(index, 1)
          }
          this.positionElements()
        })

        // repositioning if collapsible elements changes size
        this.listenAt(control).on('change:size', () => {
          this.positionElements()
        })

        if (!parentMeta) {
          metaElem.float = metaElem.control.getFloat() || ['top', 'left']

          if (metaElem.float === 'fixed') {
            return
          }

          this.getArray_(metaElem.float).push(metaElem)

          this.all_.push(metaElem)

          if (metaElem.control.getControls) {
            metaElem.hideableChildren = []
            for (let child of metaElem.control.getControls()) {
              this.addControl(child, metaElem)
            }
          }
        } else if (!parentMeta.control.isWindowed()) {
          metaElem.importance = control.getImportance()
          parentMeta.hideableChildren.push(metaElem)
          this.all_.push(metaElem)
        }
      }
    }
  }

  /**
   * Gets the element in a corner
   * @param {string} x
   * @param {string} y
   * @returns {HideableElement}
   * @private
   */
  getCorner_ (x, y) {
    let cwi = 0
    let ccwi = 0

    let cwElem = this.corners_[x][y].clockwise[cwi++]
    let ccwElem = this.corners_[x][y].counterclockwise[ccwi++]

    while (cwElem && !Positioning.isElemVisible_(cwElem)) {
      cwElem = this.corners_[x][y].clockwise[cwi++]
    }

    while (ccwElem && !Positioning.isElemVisible_(ccwElem)) {
      ccwElem = this.corners_[x][y].counterclockwise[ccwi++]
    }

    if (cwElem || ccwElem) {
      if (!cwElem) {
        return ccwElem
      } else if (!ccwElem) {
        return cwElem
      } else {
        if (cwElem.order < ccwElem.order) {
          return cwElem
        } else {
          return ccwElem
        }
      }
    }
  }

  static isElemVisible_ (elem) {
    return elem.control.get$Element().is(':visible')
  }

  /**
   * Gets all elements at one edge
   * @param side
   * @returns {*|Array.<Element>}
   * @private
   */
  getEdge_ (side) {
    let x1, x2, y1, y2
    if (side === 'top') {
      x1 = 'left'
      x2 = 'right'
      y1 = y2 = 'top'
    } else if (side === 'right') {
      x1 = x2 = 'right'
      y1 = 'top'
      y2 = 'bottom'
    } else if (side === 'bottom') {
      x1 = 'right'
      x2 = 'left'
      y1 = y2 = 'bottom'
    } else if (side === 'left') {
      x1 = x2 = 'left'
      y1 = 'bottom'
      y2 = 'top'
    }

    let clockwise = this.corners_[x1][y1].clockwise.filter(Positioning.isElemVisible_)
    let counterclockwise = this.corners_[x2][y2].counterclockwise.filter(Positioning.isElemVisible_)

    let arr = []
    let c = this.getCorner_(x1, y1)
    if (c) {
      arr.push(c)
    }
    arr.push(...clockwise)
    arr.push(...counterclockwise)
    c = this.getCorner_(x2, y2)
    if (c) {
      arr.push(c)
    }
    return uniq(arr)
  }

  /**
   * Initializes all elements
   * @private
   */
  beforePositioning_ () {
    let elems = new Set(this.all_)

    this.hidden$Elements_.forEach($e => $e.removeClass(cssClasses.hidden))
    this.hidden$Elements_ = []

    /**
     * @param {PositionedElement} elem
     */
    let forEach = elem => {
      if (Positioning.isElemVisible_(elem)) {
        if (elem.control.beforePositioning) {
          elem.control.beforePositioning()
        }

        if (elem.hideableChildren) {
          for (let child of elem.hideableChildren) {
            forEach(child)
          }
        }

        elem.control.get$Element().position({ top: 0, left: 0 })
        if (elem.control.release) {
          elem.control.release('height')
          elem.control.release('width')
        }
        elem.size = this.measureExpandedElement_(elem)
      }

      elems.delete(elem)
    }

    for (let elem of elems) {
      forEach(elem)
    }
  }

  /**
   * called after positioning
   * @private
   */
  afterPositioning_ () {
    let elems = new Set(this.all_)

    /**
     * @param {PositionedElement} elem
     */
    let forEach = elem => {
      if (elem.control.afterPositioning) {
        elem.control.afterPositioning()
      }

      if (elem.hideableChildren) {
        for (let child of elem.hideableChildren) {
          forEach(child)
        }
      }

      elems.delete(elem)
    }

    for (let elem of elems) {
      forEach(elem)
    }
  }

  /**
   * Calculates summed length of all elements on one edge
   * @param {PositionedElement[]} edgeElems
   * @param {string} dim
   * @returns {number}
   * @private
   */
  calculateLength_ (edgeElems, dim) {
    if (edgeElems.length === 0) {
      return 0
    }

    let length = this.padding_ * 2

    let firstElement = true

    for (let elem of edgeElems) {
      length += elem.size[dim]
      if (firstElement) {
        firstElement = false
      } else {
        length += this.spacing_
      }
    }

    return length
  }

  calculateSide_ (side, availableSpace) {
    let dim = (side === 'top' || side === 'bottom') ? 'width' : 'height'
    let elems = this.getEdge_(side)
    let wantedSpace = this.calculateLength_(elems, dim)
    let changed = false

    while (wantedSpace > availableSpace) {
      if (this.squeezeElements_(elems, dim, wantedSpace - availableSpace)) {
        break
      }
      this.hideLeastImportant_(elems)
      elems = this.getEdge_(side)
      wantedSpace = this.calculateLength_(elems, dim)
      changed = true
    }
    return changed
  }

  positionElementsCorner_ (x, y) {
    let corner = this.getCorner_(x, y)
    if (corner) {
      let xLength = this.padding_
      let yLength = this.padding_
      let $elem = corner.control.get$Element()

      $elem.removeClass(cssClasses.hidden).css({[x]: xLength, [y]: yLength})

      xLength += $elem.outerWidth() + this.spacing_
      yLength += $elem.outerHeight() + this.spacing_

      let xDirection, yDirection

      if (x === 'left' && y === 'top') {
        xDirection = 'clockwise'
        yDirection = 'counterclockwise'
      } else if (x === 'right' && y === 'top') {
        xDirection = 'counterclockwise'
        yDirection = 'clockwise'
      } else if (x === 'right' && y === 'bottom') {
        xDirection = 'clockwise'
        yDirection = 'counterclockwise'
      } else if (x === 'left' && y === 'bottom') {
        xDirection = 'counterclockwise'
        yDirection = 'clockwise'
      }

      // x
      for (let elem of this.corners_[x][y][xDirection]
        .filter(el => Positioning.isElemVisible_(el) && el !== corner)) {
        $elem = elem.control.get$Element()
        $elem.css({[x]: xLength, [y]: this.padding_})
        xLength += $elem.outerWidth() + this.spacing_
      }

      // y
      for (let elem of this.corners_[x][y][yDirection]
        .filter(el => Positioning.isElemVisible_(el) && el !== corner)) {
        $elem = elem.control.get$Element()
        $elem.css({[x]: this.padding_, [y]: yLength})
        yLength += $elem.outerHeight() + this.spacing_
      }
    }
  }

  /**
   * (Re-)Position the controls on the map
   */
  positionElements () {
    let width = this.$viewport_.innerWidth()
    let height = this.$viewport_.innerHeight()

    this.beforePositioning_()

    // calculation
    let processSides = new Set(['top', 'left', 'bottom', 'right'])

    while (processSides.size) {
      if (processSides.has('top')) {
        if (this.calculateSide_('top', width)) {
          processSides.add('left')
          processSides.add('right')
        }

        processSides.delete('top')
      }

      if (processSides.has('right')) {
        if (this.calculateSide_('right', height)) {
          processSides.add('top')
          processSides.add('bottom')
        }

        processSides.delete('right')
      }

      if (processSides.has('bottom')) {
        if (this.calculateSide_('bottom', width)) {
          processSides.add('left')
          processSides.add('right')
        }

        processSides.delete('bottom')
      }

      if (processSides.has('left')) {
        if (this.calculateSide_('left', height)) {
          processSides.add('top')
          processSides.add('bottom')
        }

        processSides.delete('left')
      }
    }

    // positioning

    this.positionElementsCorner_('left', 'top')
    this.positionElementsCorner_('right', 'top')
    this.positionElementsCorner_('right', 'bottom')
    this.positionElementsCorner_('left', 'bottom')

    this.afterPositioning_()
  }

  /**
   * Tries to squeeze the elements on one edge to fit the space. Returns true if it did work, false otherwise.
   * @param {PositionedElement[]} elems
   * @param {string} side
   * @param {number} neededSpace
   * @private
   * @returns {boolean}
   */
  squeezeElements_ (elems, side, neededSpace) {
    let squeezableElements = []

    function insert (item, x = 0, y = squeezableElements.length) {
      if (y === x) {
        squeezableElements.splice(x, 0, item)
      } else {
        let p = Math.floor((x + y) / 2)
        if (item.importance <= squeezableElements[p].importance) {
          insert(item, x, p)
        } else {
          insert(item, p + 1, y)
        }
      }
    }

    /**
     * @param {PositionedElement[]} elems
     */
    function findSqueezables (elems) {
      for (let elem of elems) {
        if (elem.control.isSqueezable && elem.control.isSqueezable(side)) {
          insert(elem)
        }
        if (elem.hideableChildren) {
          findSqueezables(elem.hideableChildren)
        }
      }
    }

    findSqueezables(elems)

    for (let elem of squeezableElements) {
      neededSpace -= elem.control.squeezeBy(side, neededSpace)

      if (neededSpace <= 0) {
        return true
      }
    }

    for (let elem of squeezableElements) {
      elem.control.release(side)
    }

    return false
  }

  /**
   * Expands element to maximum size
   * @param {PositionedElement} elem
   * @returns {Array}
   * @private
   */
  expandElement_ (elem) {
    let expanded = []

    if (elem.hideableChildren) {
      for (let child of elem.hideableChildren) {
        expanded.push(...this.expandElement_(child))
      }
    }
    if (elem.control.getCollapsible && elem.control.getCollapsible()) {
      if (elem.control.getCollapsed()) {
        elem.control.setCollapsed(false, true)
        expanded.push(elem.control)
      }
    }

    return expanded
  }

  /**
   * measures size of the element
   * @param {PositionedElement} elem
   * @returns {{width: number, height: number}}
   * @private
   */
  measureExpandedElement_ (elem) {
    // let expanded = this.expandElement_(elem)
    let $elem = elem.control.get$Element()
    let size = {width: $elem.outerWidth(), height: $elem.outerHeight()}
    // for (let exp of expanded) {
    //   exp.setCollapsed(true, true)
    // }
    return size
  }

  /**
   * Hides the least important element of the given ones.
   * @param {PositionedElement[]} elems visible elements
   * @private
   */
  hideLeastImportant_ (elems) {
    let leastImportant = elems[0]
    for (let elem of elems.slice(1)) {
      if (elem.importance < leastImportant.importance) {
        leastImportant = elem
      }
    }

    let childHidden = false
    if (leastImportant.hideableChildren) {
      let hideableChildren = leastImportant.hideableChildren.filter(Positioning.isElemVisible_)
      if (hideableChildren.length > 1) {
        this.hideLeastImportant_(hideableChildren)
        childHidden = true
      }
    }

    if (childHidden) {
      leastImportant.size = this.measureExpandedElement_(leastImportant)
    } else {
      leastImportant.control.get$Element().addClass(cssClasses.hidden)
      this.hidden$Elements_.push(leastImportant.control.get$Element())
    }
  }
}

import ol from 'openlayers'
import $ from 'jquery'

import {Positioning} from './Positioning'

import {Move} from '../Move'
import {FeaturePopup} from '../FeaturePopup'
import {FeatureTooltip} from '../FeatureTooltip'

import {Shield} from '../html/Shield'

import {ControlFactory} from './ControlFactory'

import {Debug} from '../Debug'

import {copyDeep} from '../utilitiesObject'
import { checkFor, getConfig, urlDirname, urlJoin } from '../utilities'

import {MeasurementButton} from '../controls/MeasurementButton'
import {PrintButton} from '../controls/PrintButton'

import {cssClasses} from '../globals'

import {FeatureSelect} from '../interactions/FeatureSelect'

import {parseCSSColor} from 'csscolorparser'
import {ShowWMSFeatureInfo} from '../ShowWMSFeatureInfo'

import {History} from '../html/History'
import { debounce } from 'lodash/function'

/**
 * This class configures the UI of a map according to its mapconfig
 */
export class UIConfigurator {
  /**
   * @param {G4UMap} map
   */
  constructor (map) {
    /**
     * @type {G4UMap}
     * @private
     */
    this.map_ = map
    this.map_.on('change:mapConfig', this.configureUI.bind(this))

    /**
     * @type {boolean}
     * @private
     */
    this.initialized_ = false

    /**
     * Counts how many cssFiles are getting loaded
     * @type {number}
     * @private
     */
    this.pending_ = 0
  }

  /**
   * Fill the given css String with the rigth colors
   * @param {string} css
   * @param {Color[]} colors
   * @returns {string}
   */
  static templateCSS (css, colors) {
    // find the used index of the color template
    let colorTemplateIndex = (c) => {
      let channels = 0
      let index = -1
      for (let i = 0; i < 3; i++) {
        if (c[i] !== 0) {
          channels++
          index = i
        }
      }
      if (channels > 1) {
        return -1
      } else {
        return index
      }
    }

    let regExp = /(rgba?\([^)]*\))|(#[0-9a-f]{6})/g

    colors = colors.map(parseCSSColor)

    return css.replace(regExp, match => {
      let templateColor = parseCSSColor(match)
      let index = colorTemplateIndex(templateColor)
      if (index > -1) {
        let aFac = templateColor[3]
        let cFac = templateColor[index] / 120
        let newColor = [
          colors[index][0] * cFac,
          colors[index][1] * cFac,
          colors[index][2] * cFac,
          colors[index][3] * aFac
        ]
        return `rgba(${newColor[0]}, ${newColor[1]}, ${newColor[2]}, ${newColor[3]})`
      }
      return match
    })
  }

  /**
   * Fixes all urls in the css string to the right path
   * @param css
   * @param cssPath
   * @returns {string}
   */
  static fixUrls (css, cssPath) {
    let dirname = urlDirname(cssPath)
    return css.replace(/url\("([^"]*)"\)/g, (match, url) => {
      return `url("${urlJoin(dirname, url)}")`
    })
  }

  /**
   * Loads the from cssFile the given conf (if any is given) and inserts into a style tag in the head of the document
   * @param {MapConfig} conf
   * @returns {Promise}
   */
  loadCSS (conf) {
    return new Promise(resolve => {
      if (conf.hasOwnProperty('cssFile')) {
        const id = 'g4u-css'
        let $style = $('#' + id)

        if ($style.length === 0) {
          $style = $('<style>', {
            'id': id
          })
          $('head').append($style)
        }

        const same = this.cssFile_ === conf.cssFile
        if (!same) {
          $.ajax(conf.cssFile).done(data => {
            $style.empty()
            data = UIConfigurator.fixUrls(data, conf.cssFile)
            if (conf.hasOwnProperty('cssTemplate')) {
              this.map_.set('cssTemplateFile', data)
              $style.html(UIConfigurator.templateCSS(data, conf.cssTemplate))
            } else {
              $style.html(data)
            }
            resolve()
          }).fail(() => {
            Debug.error('Failed to load css file.')
            resolve()
          })
          this.cssFile_ = conf.cssFile
        } else {
          if (conf.hasOwnProperty('cssTemplate')) {
            $style.html(UIConfigurator.templateCSS(this.map_.get('cssTemplateFile'), conf.cssTemplate))
          }
          resolve()
        }
      } else {
        resolve()
      }
    })
  }

  /**
   * This functions is called the first time the configureUI function is called
   * @param {MapConfig} mapConfigCopy
   * @private
   */
  initialize_ (mapConfigCopy) {
    //
    // Control positioning
    //

    /**
     * @type {PositioningOptions}
     */
    let positioningOptions = mapConfigCopy.positioning || {}
    positioningOptions.viewport = this.map_.getViewport()

    this.map_.set('controlPositioning', new Positioning(positioningOptions))

    let debouncedPosition = debounce(() => this.map_.get('controlPositioning').positionElements())

    this.map_.on('ready', debouncedPosition)

    this.map_.asSoonAs('ready', true, () => {
      this.map_.on('resize', debouncedPosition)
      this.map_.on('ready:ui', () => {
        if (this.map_.get('ready:ui')) {
          debouncedPosition()
        }
      })
      this.map_.on('change:mobile', debouncedPosition)

      this.map_.on('ready:layers', () => {
        debouncedPosition()
        this.map_.getLayerGroup().forEachOn('change:visible', () => setTimeout(debouncedPosition, 200))
      })

      this.map_.getLayerGroup().forEachOn('change:visible', () => setTimeout(debouncedPosition, 200))
    })

    //
    //  Responsiveness / Mobile Layout
    //

    this.map_.set('mobile', false)
    $(this.map_.getTarget()).children().addClass(cssClasses.desktop)

    this.map_.set('scaleIcons', mapConfigCopy.scaleIcons)

    let mobileChangeHandler = this.getHandleMobileChange_()
    this.map_.on('change:mobile', mobileChangeHandler)

    this.map_.once('ready', () => {
      this.map_.set('desktopLayout', {
        animations: this.map_.get('move').getAnimations(),
        scaleIcons: this.map_.get('scaleIcons'),
        hitTolerance: this.map_.get('hitTolerance')
      })
      mobileChangeHandler()
    })

    let checkMobileLayoutQuery = () => {
      /**
       * @type {MobileLayoutOptions}
       */
      let mobileLayout = this.map_.get('mobileLayout')

      if (mobileLayout && mobileLayout.mediaQueries && window.matchMedia) {
        this.map_.set('mobile', mobileLayout.mediaQueries.some(query => {
          return window.matchMedia(query).matches
        }))
      }
    }

    //
    // Enabling/Disabling responsiveness
    //

    let oldResponsive

    let onChangeResponsive = () => {
      if (this.map_.get('responsive') !== oldResponsive) {
        if (this.map_.get('responsive')) {
          checkMobileLayoutQuery()
          this.map_.on('resize', checkMobileLayoutQuery)
        } else {
          this.map_.un('resize', checkMobileLayoutQuery)
        }
        oldResponsive = this.map_.get('responsive')
      }
    }

    this.map_.once('ready', onChangeResponsive)

    this.map_.on('change:responsive', onChangeResponsive)

    this.map_.on('ready:ui', onChangeResponsive)

    this.initialized_ = true
  }

  applyLayout (layout) {
    if (layout.hasOwnProperty('animations')) {
      this.map_.get('move').setAnimations(layout.animations)
    }

    if (layout.hasOwnProperty('scaleIcons')) {
      this.map_.set('scaleIcons', layout.scaleIcons)
      this.map_.get('styling').setGlobalIconScale(layout.scaleIcons)
      this.map_.getLayerGroup().recursiveForEach(l => {
        if (l.getVisible()) {
          l.changed()
        }
      })
    }

    if (layout.hasOwnProperty('hitTolerance')) {
      this.map_.set('hitTolerance', layout.hitTolerance)
    }

    let featurePopup = this.map_.get('featurePopup')
    let wmsFeatureInfo = this.map_.get('showWMSFeatureInfo')

    let restoreWmsFeatureInfoPoint = wmsFeatureInfo && wmsFeatureInfo.getPointVisible()

    if (featurePopup && featurePopup.getVisible()) {
      featurePopup.setVisible(false)
      setTimeout(() => {
        featurePopup.setVisible(true)
        if (restoreWmsFeatureInfoPoint) {
          wmsFeatureInfo.setPointVisible(true)
        }
      }, 0)
    }
  }

  getHandleMobileChange_ () {
    if (!this.handleMobileChange__) {
      this.handleMobileChange__ = () => {
        if (this.map_.get('mobile')) {
          $(this.map_.getTarget()).children().addClass(cssClasses.mobile)
          $(this.map_.getTarget()).children().removeClass(cssClasses.desktop)

          this.applyLayout(this.map_.get('mobileLayout'))
        } else {
          $(this.map_.getTarget()).children().addClass(cssClasses.desktop)
          $(this.map_.getTarget()).children().removeClass(cssClasses.mobile)

          this.applyLayout(this.map_.get('desktopLayout'))
        }
      }
    }
    return this.handleMobileChange__
  }

  /**
   * @public
   */
  configureUI () {
    this.map_.set('ready:ui', false)
    this.pending_++

    let mapConfigCopy = copyDeep(this.map_.get('mapConfig'))

    if (!this.initialized_) {
      this.initialize_(mapConfigCopy)
    }

    if (this.map_.get('history')) {
      this.map_.get('history').flush()
    }

    let curConfig

    // //////////////////////////////////////////////////////////////////////////////////////// //
    //                              Load CSS if neccessary                                      //
    // //////////////////////////////////////////////////////////////////////////////////////// //

    this.loadCSS(mapConfigCopy).then(() => {
      this.pending_--
      if (this.pending_ === 0) {
        // clear
        this.map_.removeControls()
        this.map_.controlsByName = {}
        this.map_.removeInteractions()

        this.map_.get('controlPositioning').init()

        // //////////////////////////////////////////////////////////////////////////////////////// //
        //                           Move Class (before mobileLayout)                               //
        // //////////////////////////////////////////////////////////////////////////////////////// //

        let moveOptions = copyDeep(getConfig(mapConfigCopy, 'move')) || {}
        moveOptions.map = this.map_

        this.map_.set('move', new Move(moveOptions))

        // //////////////////////////////////////////////////////////////////////////////////////// //
        //                                     HTML-Shield                                          //
        // //////////////////////////////////////////////////////////////////////////////////////// //

        if (!this.map_.get('shield')) {
          this.map_.set('shield', new Shield({ map: this.map_ }))
        }

        // //////////////////////////////////////////////////////////////////////////////////////// //
        //                                MobileLayoutMediaQuery                                    //
        // //////////////////////////////////////////////////////////////////////////////////////// //

        this.map_.set('mobileLayout', mapConfigCopy.mobileLayout)

        this.map_.set('responsive', this.map_.get('responsive') || !!this.map_.get('mobileLayout'))

        // //////////////////////////////////////////////////////////////////////////////////////// //
        //                                       MODULES                                            //
        // //////////////////////////////////////////////////////////////////////////////////////// //

        // should be before adding controls so the controls don't have to wait for any other ui elements

        for (let module of this.map_.getModules()) {
          module.configureUI(mapConfigCopy)
        }

        // //////////////////////////////////////////////////////////////////////////////////////// //
        //                                       Controls                                           //
        // //////////////////////////////////////////////////////////////////////////////////////// //

        if (!this.controlFactory) {
          this.controlFactory = new ControlFactory({
            map: this.map_,
            localiser: this.map_.get('localiser')
          })
        } else {
          this.controlFactory.setLocaliser(this.map_.get('localiser'))
        }

        this.controlFactory.addControls()

        let deactivate = control => {
          if (control.setActive) {
            control.setActive(false)
          }
          if (control.getControls) {
            control.getControls().forEach(deactivate)
          }
        }

        this.map_.on('change:mobile', () => this.map_.getControls().forEach(deactivate))

        // //////////////////////////////////////////////////////////////////////////////////////// //
        //                                     Interactions                                         //
        // //////////////////////////////////////////////////////////////////////////////////////// //

        if (checkFor(mapConfigCopy, 'interactions')) {
          if (checkFor(mapConfigCopy.interactions, 'doubleClickZoom')) {
            this.map_.addDefaultInteraction('dblclick', new ol.interaction.DoubleClickZoom())
          }

          if (checkFor(mapConfigCopy.interactions, 'dragPan')) {
            if (mapConfigCopy.kinetic !== false) {
              let decay = -0.002
              let minVelocity = 0.02
              let delay = 100
              if (typeof mapConfigCopy.kinetic === 'object') {
                decay = mapConfigCopy.kinetic.decay || decay
                minVelocity = mapConfigCopy.kinetic.minVelocity || minVelocity
                delay = mapConfigCopy.kinetic.delay || delay
              }
              this.map_.addDefaultInteraction('drag', new ol.interaction.DragPan({
                kinetic: new ol.Kinetic(decay, minVelocity, delay)
              }))
            } else {
              this.map_.addDefaultInteraction('drag', new ol.interaction.DragPan())
            }
          }

          if (checkFor(mapConfigCopy.interactions, 'dragRotate')) {
            // using default condition: ol.events.condition.altShiftKeysOnly
            this.map_.addDefaultInteraction('altShiftDrag', new ol.interaction.DragRotate())
          }

          if (checkFor(mapConfigCopy.interactions, 'dragZoom')) {
            // using default condition: ol.events.condition.shiftKeyOnly
            this.map_.addDefaultInteraction('shiftDrag', new ol.interaction.DragZoom())
          }

          if (checkFor(mapConfigCopy.interactions, 'keyboardPan')) {
            this.map_.addDefaultInteraction('keyboard', new ol.interaction.KeyboardPan())
          }

          if (checkFor(mapConfigCopy.interactions, 'keyboardZoom')) {
            this.map_.addDefaultInteraction('keyboard', new ol.interaction.KeyboardZoom())
          }

          if (checkFor(mapConfigCopy.interactions, 'mouseWheelZoom')) {
            this.map_.addDefaultInteraction('mouseWheel', new ol.interaction.MouseWheelZoom())
          }

          if (checkFor(mapConfigCopy.interactions, 'pinchRotate')) {
            this.map_.addDefaultInteraction('pinchRotate', new ol.interaction.PinchRotate())
          }

          if (checkFor(mapConfigCopy.interactions, 'pinchZoom')) {
            this.map_.addDefaultInteraction('pinchZoom', new ol.interaction.PinchZoom())
          }
        }

        this.map_.addDefaultInteraction('singleclick', new FeatureSelect({
          condition: e => ol.events.condition.singleClick(e) && $(e.originalEvent.target).is('canvas'),
          style: null,
          multi: true
        }))

        let moveInteraction = new FeatureSelect({
          condition: e => ol.events.condition.pointerMove(e) && $(e.originalEvent.target).is('canvas'),
          style: null,
          multi: true
        })
        this.map_.addDefaultInteraction('pointermove', moveInteraction)

        let $viewport = $(this.map_.getViewport())

        // if the map is left the features should get deselected
        $viewport.find('canvas').on('mouseleave', e => {
          if (!$(e.relatedTarget).is('canvas')) {
            if (moveInteraction.getFeatures().getLength() > 0) {
              moveInteraction.deselect(moveInteraction.getFeatures().getArray())
            }
          }
        })

        // hitTolerance
        let updateHitTolerance = () => {
          let hitTolerance = this.map_.get('hitTolerance')
          for (let interaction of this.map_.getInteractions().getArray()) {
            if (interaction.setHitTolerance) {
              interaction.setHitTolerance(hitTolerance)
            }
          }
        }

        this.map_.set('hitTolerance', mapConfigCopy.hitTolerance || 0)

        updateHitTolerance()
        this.map_.on('change:hitTolerance', updateHitTolerance)

        // //////////////////////////////////////////////////////////////////////////////////////// //
        //                                     Feature Popups                                       //
        // //////////////////////////////////////////////////////////////////////////////////////// //

        let featurePopup = this.map_.get('featurePopup')
        if (featurePopup) {
          featurePopup.setMap(null)
        }
        curConfig = getConfig(mapConfigCopy, 'featurePopup')
        if (curConfig) {
          featurePopup = new FeaturePopup(curConfig)
          featurePopup.setMap(this.map_)
          this.map_.set('featurePopup', featurePopup)
        } else {
          this.map_.set('featurePopup', undefined)
        }

        let featureTooltip = this.map_.get('featureTooltip')
        if (featureTooltip) {
          featureTooltip.setMap(null)
        }
        curConfig = getConfig(mapConfigCopy, 'featureTooltip')
        if (curConfig) {
          featureTooltip = new FeatureTooltip(curConfig)
          featureTooltip.setMap(this.map_)
          this.map_.set('featureTooltip', featureTooltip)
        } else {
          this.map_.set('featureTooltip', undefined)
        }

        let showWMSFeatureInfo = this.map_.get('showWMSFeatureInfo')
        if (showWMSFeatureInfo) {
          showWMSFeatureInfo.setMap(null)
        }
        curConfig = getConfig(mapConfigCopy, 'showWMSFeatureInfo')
        if (curConfig) {
          showWMSFeatureInfo = new ShowWMSFeatureInfo(curConfig)
          showWMSFeatureInfo.setMap(this.map_)
          this.map_.set('showWMSFeatureInfo', showWMSFeatureInfo)
        } else {
          this.map_.set('showWMSFeatureInfo', undefined)
        }

        // //////////////////////////////////////////////////////////////////////////////////////// //
        //                                    Custom History                                        //
        // //////////////////////////////////////////////////////////////////////////////////////// //

        this.map_.set('history', new History())

        // //////////////////////////////////////////////////////////////////////////////////////// //
        //                                     UserExit (2/2)                                       //
        // //////////////////////////////////////////////////////////////////////////////////////// //
        if (this.map_.get('userActionTracking')) {
          let map = this.map_

          this.map_.on('moveend', function () {
            map.dispatchEvent({
              type: 'userActionTracking',
              action: 'move'
            })
          })

          this.map_.get('baseLayers').getLayers().forEach(function (layer) {
            layer.on('change:visible', function () {
              if (layer.getVisible()) { // only if changed to visible
                map.dispatchEvent({
                  type: 'userActionTracking',
                  action: 'baseLayerChange',
                  value: layer.get('title')
                })
              }
            })
          })

          this.map_.get('featureLayers').recursiveForEach(function (layer) {
            layer.on('change:visible', function () {
              if (layer.getVisible()) { // only if changed to visible
                map.dispatchEvent({
                  type: 'userActionTracking',
                  action: 'featureLayerChange',
                  value: layer.get('title')
                })
              }
            })
          })

          if (this.map_.get('featurePopup')) {
            this.map_.get('featurePopup').on('change:visible', function () {
              if (this.getVisible()) { // only if changed to visible
                map.dispatchEvent({
                  type: 'userActionTracking',
                  action: 'popupOpen',
                  value: this.getFeature().get('name')
                })
              }
            })
          }

          for (let module of this.map_.getModules()) {
            module.enableUserActionTracking()
          }

          this.map_.getControls().forEach(function (control) {
            if (control instanceof PrintButton) {
              control.on('click', function () {
                map.dispatchEvent({
                  type: 'userActionTracking',
                  action: 'print'
                })
              })
            }

            if (control instanceof MeasurementButton) {
              if (control.getType() === 'LineString') {
                control.on('measurement', function () {
                  map.dispatchEvent({
                    type: 'userActionTracking',
                    action: 'measureLine'
                  })
                })
              } else if (control.getType() === 'Polygon') {
                control.on('measurement', function () {
                  map.dispatchEvent({
                    type: 'userActionTracking',
                    action: 'measureArea'
                  })
                })
              }
            }
          })
        }
        this.map_.set('ready:ui', true)
      }
    }).catch(function (reason) {
      Debug.error(reason)
    })
  }
}

export class ActivatableMixin {
  /**
   * @param {object} options
   * @param {boolean} options.active
   */
  initialize (options = {}) {
    /**
     * @type {boolean}
     * @private
     */
    this.activate_ = options.active === true

    /**
     * @type {boolean}
     * @private
     */
    this.active_ = false
  }

  activateOnMapChange () {
    this.setActive(this.activate_)
  }

  /**
   * @param {boolean} active
   */
  setActive (active) {
    let oldValue = this.active_
    if (oldValue !== active) {
      this.active_ = active
      let changeEvent = {
        type: 'change:active',
        oldValue: oldValue
      }
      this.dispatchEvent(changeEvent)
    }
  }

  /**
   * @returns {boolean}
   */
  getActive () {
    return this.active_
  }
}

/**
 * This class organizes controls in an active group in which only one control can bea active at a time.
 * This specified and configured via a property 'activeGroup' in the config object of the control and
 * a matching string for all controls which should be in this group
 */
export class ActiveGroup {
  /**
   * @param {Control[]} controls
   */
  constructor (controls = []) {
    /**
     * @type {Control[]}
     * @private
     */
    this.controls_ = controls
    for (let control of this.controls_) {
      this.addControl(control)
    }
  }

  /**
   * @param {Control} control
   */
  addControl (control) {
    this.controls_.push(control)
    control.on('change:active', () => {
      if (control.getActive()) {
        if (this.activeControl_) {
          this.activeControl_.setActive(false)
        }
        this.activeControl_ = control
      } else {
        this.activeControl_ = null
      }
    })
  }
}

import ol from 'openlayers'
import $ from 'jquery'

import { merge } from '../utilitiesObject'
import {Control} from './Control'
import {addTooltip} from '../html/html'

import '../../less/arrowbuttons.less'

/**
 * @typedef {g4uControlOptions} ArrowButtonOptions
 * @property {number} [pixelDelta=128] how many pixels should one click move the map
 * @property {ol.Extent|undefined} [initExtent=undefined] the initial Extent to move the map back to (center button)
 * @property {number} [animationDuration=100] duration of the animation
 * @property {boolean} [animated=true] if the move should be done with an animation or without
 * @property {{up: string, right: string, down: string, left: string, center: string}|{}} [labels={}]
 *  unicode labels for the buttons (only shown if pictures are not loaded)
 */

/**
 * ArrowButtons shows Buttons on the map which let you move in all 4 directions and a button to return to the initial
 * position of the map. It provides an Interface class with an produceOlControl returning an
 * {ol.control.Control} - Object. This can be added as normal controls to the map
 * (e.g. ``map.addControl(arrowButtons.produceOlControl)`` ).
 *
 * The Options are passed as an Object (e.g. ``{ initCenter : map.getView().getCenter(),
 *      initZoom : map.getView().getZoom() }``.
 */
export class ArrowButtons extends Control {
  /**
   * @param {ArrowButtonOptions} [options={}]
   */
  constructor (options = {}) {
    options.className = options.className || 'g4u-arrowbuttons'
    options.element = $('<div class="ol-unselectable ol-control"></div>')[0]
    options.singleButton = false

    super(options)

    let description = {
      'left': this.getLocaliser().localiseUsingDictionary('ArrowButtons leftward'),
      'right': this.getLocaliser().localiseUsingDictionary('ArrowButtons rightward'),
      'up': this.getLocaliser().localiseUsingDictionary('ArrowButtons upward'),
      'down': this.getLocaliser().localiseUsingDictionary('ArrowButtons downward'),
      'center': this.getLocaliser().localiseUsingDictionary('ArrowButtons centerward')
    }

    /**
     * @type {number}
     * @private
     */
    this.pixelDelta_ = options.pixelDelta || 128

    /**
     * @type {ol.Extent|undefined}
     * @private
     */
    this.initExtent_ = options.initExtent || undefined

    /**
     * @type {number}
     * @private
     */
    this.animationDuration_ = options.animationDuration || 100

    /**
     * @type {boolean}
     * @private
     */
    this.animated_ = options.animated || true

    /**
     * @type {string[]}
     * @private
     */
    this.directions_ = ['center', 'left', 'up', 'right', 'down']

    /**
     * @type {{left: number[], up: number[], right: number[], down: number[]}}
     * @private
     */
    this.vectors_ = {left: [-1, 0], up: [0, 1], right: [1, 0], down: [0, -1]}

    /**
     * @type {{up: string, right: string, down: string, left: string, center: string}}
     * @private
     */
    this.labels_ = merge(options.labels || {}, {
      left: '&#9665;',
      up: '&#9651;',
      right: '&#9655;',
      down: '&#9661;',
      center: '&#9675;'
    })

    // creating the HTML-Elements and registering Event-Handlers

    for (let direction of this.directions_) {
      // HTML

      let $button = $('<button>')
        .addClass(this.className_ + '-' + direction)
        .html(this.labels_[direction])

      addTooltip($button, description[direction])

      // Handler
      $button.on('click', () => {
        this.onClick_(direction)
        $button.blur()
      })

      // adding button
      this.get$Element().append($button)
    }
  }

  /**
   * @param {G4UMap} map
   */
  setMap (map) {
    super.setMap(map)

    if (map) {
      if (!this.initExtent_) {
        this.initExtent_ = map.getView().calculateExtent(map.getSize())
      }
    }
  }

  /**
   * This method is called when a button is clicked and makes a move on the map corresponding to the direction.
   * @param {string} direction
   * @private
   */
  onClick_ (direction) {
    let map = this.getMap()
    let view = map.getView()

    if (direction === 'center') {
      map.get('move').toExtent(this.initExtent_, { animated: this.animated_ })
    } else {
      let resolution = view.getResolution()
      let rotation = view.getRotation()

      // a vector that points in the direction the move should be going
      let dirVec = this.vectors_[direction]

      // this calculates the 'move'-vector out of the direction and pixelDelta (-> length, how many pixels per move)
      let delta = [dirVec[0] * resolution * this.pixelDelta_, dirVec[1] * resolution * this.pixelDelta_]

      // this rotates if needed
      ol.coordinate.rotate(delta, rotation)

      let oldPosition = view.getCenter()

      // adding the delta to the actual center

      let constrainedCenter = view.constrainCenter([oldPosition[0] + delta[0], oldPosition[1] + delta[1]])

      if (this.animated_) {
        // creating a pan-animation
        view.animate({
          center: constrainedCenter,
          duration: this.animationDuration_
        })
      } else {
        view.setCenter(constrainedCenter)
      }
    }
    $(map.getViewport()).focus()
  }
}

import $ from 'jquery'

import { Control } from './Control'
import { ListenerOrganizerMixin } from '../ListenerOrganizerMixin'
import { mixin } from '../utilities'
import { cssClasses } from '../globals'
import { addTooltip } from '../html/html'

import '../../less/attribution.less'

export class Attribution extends mixin(Control, ListenerOrganizerMixin) {
  constructor (options) {
    options.className = options.className || 'g4u-attribution'
    options.singleButton = false
    options.element = $('<div>').get(0)

    super(options)

    this.setTipLabel(this.getTipLabel() || this.getLocaliser().localiseUsingDictionary('Attribution tipLabel'))

    this.createStaticHTML(options)

    this.setCollapsed(options.collapsed === true)
  }

  setCollapsed (collapsed, silent) {
    let oldValue = this.collapsed_
    if (oldValue !== collapsed) {
      this.collapsed_ = collapsed
      this.dispatchEvent({
        type: 'change:collapsed',
        oldValue
      })
      if (!silent) {
        this.dispatchEvent('change:size')
      }
      this.get$Element().toggleClass(cssClasses.collapsed, collapsed)
    }
  }

  getCollapsed () {
    return this.collapsed_
  }

  createStaticHTML (options) {
    let $button = $('<button>')
      .on('click', () => {
        this.setCollapsed(!this.getCollapsed())
      })
    addTooltip($button, this.getTipLabel())

    this.$list_ = $('<ul>')

    this.get$Element()
      .append($button)
      .append(this.$list_)

    /**
     * @type {boolean}
     * @private
     */
    this.showPoweredBy_ = options.poweredBy !== false
    if (this.showPoweredBy_) {
      let content = (options.poweredBy === undefined)
        ? '<a href="https://github.com/KlausBenndorf/guide4you" target="_blank">Guide4You</a>'
        : this.getLocaliser().selectL10N(options.poweredBy)

      if (!Array.isArray(content)) {
        content = [content]
      }

      /**
       * @type {jQuery}
       * @private
       */
      this.$poweredBy_ = content.map(c => $('<li>')
        .append(c)
        .addClass(this.className_ + '-poweredby'))
    }
  }

  updateList (attributions) {
    this.$list_.empty()
    if (this.showPoweredBy_) {
      this.$list_.append(this.$poweredBy_)
    }

    for (let attribution of attributions) {
      this.$list_.append($('<li>').html(attribution))
    }

    if (!this.getCollapsed()) {
      this.dispatchEvent('change:size')
    }
  }

  updateRtl () {
    if (this.getMap().get('localiser').isRtl()) {
      this.$list_.prop('dir', 'rtl')
    } else {
      this.$list_.prop('dir', undefined)
    }
  }

  setMap (map) {
    if (this.getMap()) {
      this.detachAllListeners()
    }

    super.setMap(map)

    if (map) {
      map.asSoonAs('ready:layers', true, () => {
        if (this.getMap()) {
          const attributions = this.getMap().get('attributions')
          this.updateList(attributions.getArray())
          this.listenAt(attributions).on('change', () => {
            this.updateList(attributions.getArray())
          })
          this.updateRtl()
          this.listenAt(this.getMap().get('localiser')).on('change:language', () => {
            this.updateRtl()
          })
        }
      }, 0)
    }
  }
}

import ol from 'openlayers'
import $ from 'jquery'

import {ComposedControl} from './ComposedControl'
import { cssClasses } from '../globals'
import { copy } from '../utilitiesObject'

import '../../less/zoom.less'
import {mixin} from '../utilities'
import {RewireMixin} from './RewireMixin'

/**
 * @typedef {ComposedControlOptions} CombinedZoomOptions
 * @property {boolean} [buttons=true] visibility of the zoombuttons
 * @property {boolean} [slider=true] visibility of the zoomslider
 * @property {number} [buttonImportance] importance of the buttons
 * @property {number} [sliderImportance] importance of the slider
 * @property {string} [zoomInTipLabel]
 * @property {string} [zoomOutTipLabel]
 */

/**
 * This combines the two zoom controls (zoomslider and zoombuttons)
 */
export class CombinedZoom extends ComposedControl {
  /**
   * @param {CombinedZoomOptions} [options={}]
   */
  constructor (options = {}) {
    options.element = $('<div>').get(0)
    options.className = options.className || 'g4u-zoom'
    options.singleButton = false

    super(options)

    delete options.element

    /**
     * @type {boolean}
     * @private
     */
    this.hasButtons_ = options.buttons || !options.hasOwnProperty('buttons')

    /**
     * @type {boolean}
     * @private
     */
    this.hasSlider_ = options.slider || !options.hasOwnProperty('slider')

    if (this.hasButtons_) {
      this.zoomButtonOptions = copy(options)
      this.zoomButtonOptions.importance = options.buttonImportance || options.importance
      this.zoomButtonOptions.zoomInTipLabel = (options.hasOwnProperty('zoomInTipLabel'))
        ? this.getLocaliser().selectL10N(options.zoomInTipLabel)
        : this.getLocaliser().localiseUsingDictionary('Zoom zoomInTipLabel')

      this.zoomButtonOptions.zoomOutTipLabel = (options.hasOwnProperty('zoomOutTipLabel'))
        ? this.getLocaliser().selectL10N(options.zoomOutTipLabel)
        : this.getLocaliser().localiseUsingDictionary('Zoom zoomOutTipLabel')
    }

    if (this.hasSlider_) {
      this.zoomSliderOptions = copy(options)
      this.zoomSliderOptions.importance = options.sliderImportance
      this.zoomSliderOptions.className = 'g4u-zoomslider'
    }
  }

  /**
   * @param {G4UMap} map
   */
  setMap (map) {
    super.setMap(map)

    if (map) {
      if (this.hasSlider_) {
        this.addZoomSlider()
      }

      if (this.hasButtons_) {
        this.addZoomButtons()
      }
    }
  }

  addZoomButtons () {
    /**
     * @type {Zoom}
     * @private
     */
    this.zoomButtons_ = new Zoom(this.zoomButtonOptions)

    this.addControl(this.zoomButtons_, {wrap: false})

    let $zoomIn = this.zoomButtons_.get$Element().children('.g4u-zoom-in')
    let $zoomOut = this.zoomButtons_.get$Element().children('.g4u-zoom-out')

    this.get$Container().prepend($zoomIn)
    this.get$Container().append($zoomOut)

    this.zoomButtons_.get$Element().remove()
  }

  addZoomSlider () {
    /**
     * @type {ZoomSlider}
     * @private
     */
    this.zoomSlider_ = new ZoomSlider(this.zoomSliderOptions)

    this.addControl(this.zoomSlider_, {wrap: false})

    let $zoomSlider = this.zoomSlider_.get$Element()

    $zoomSlider.on('mousedown', function () {
      $zoomSlider.addClass(cssClasses.mousedown)
    })
    $zoomSlider.on('mouseup', function () {
      $zoomSlider.removeClass(cssClasses.mousedown)
    })

    let $zoomIn = this.get$Container().filter('.g4u-zoom-in')

    if ($zoomIn.length) {
      $zoomSlider.insertAfter($zoomIn)
    } else {
      this.get$Container().append($zoomSlider)
    }
  }
}

/**
 * @typedef {g4uControlOptions} ZoomOptions
 * @property {Localizable} [zoomInTipLabel]
 * @property {Localizable} [zoomOutTipLabel]
 */

/**
 * @extends Control
 */
export class Zoom extends mixin(ol.control.Zoom, RewireMixin) {
  /**
   * @param {g4uControlOptions} [options={}]
   */
  constructor (options = {}) {
    options.className = (options.hasOwnProperty('className')) ? options.className : 'g4u-zoom'

    options.zoomInTipLabel = (options.hasOwnProperty('zoomInTipLabel'))
      ? options.localiser.selectL10N(options.zoomInTipLabel)
      : options.localiser.localiseUsingDictionary('Zoom zoomInTipLabel')

    options.zoomOutTipLabel = (options.hasOwnProperty('zoomOutTipLabel'))
      ? options.localiser.selectL10N(options.zoomOutTipLabel)
      : options.localiser.localiseUsingDictionary('Zoom zoomOutTipLabel')

    super(options)
  }
}

/**
 * @extends Control
 */
export class ZoomSlider extends mixin(ol.control.ZoomSlider, RewireMixin) {
  /**
   * @param {g4uControlOptions} [options={}]
   */
  constructor (options = {}) {
    options.className = (options.hasOwnProperty('className')) ? options.className : 'g4u-zoomslider'

    super(options)
  }

  rewire () {
    super.rewire()

    this.get$Element().on('mousedown', function () {
      $(this).addClass(cssClasses.mousedown)
    })
    this.get$Element().on('mouseup', function () {
      $(this).removeClass(cssClasses.mousedown)
    })
  }
}

import $ from 'jquery'

import {Window} from '../html/Window'
import {Control} from './Control'
import {cssClasses} from '../globals'

import '../../less/toolbox.less'
import '../../less/layermenu.less'

/**
 * @typedef {g4uControlOptions} ComposedControlOptions
 * @property {string} [containerClassName]
 */

/**
 * This is a class ComposedControl which provides some functionality for controls which are composed out of several
 * other controls. It makes use of the composite pattern.
 */

export class ComposedControl extends Control {
  /**
   * @param {ComposedControlOptions} [options={}]
   */
  constructor (options = {}) {
    let $container = $('<div>')

    if (options.hasOwnProperty('element')) {
      $(options.element).append($container)
    } else {
      options.element = $container.get(0)
    }

    options.singleButton = false

    super(options)

    /**
     * @type {Control[]}
     * @private
     */
    this.controls_ = []

    /**
     * @type {jQuery}
     * @private
     */
    this.$container_ = $container

    if (options.hasOwnProperty('containerClassName')) {
      this.$container_.addClass(options.containerClassName)
    }

    /**
     * @type {string}
     * @private
     */
    this.classNameItem_ = this.getClassName() + '-item'
    /**
     * @type {string}
     * @private
     */
    this.classNameItemFirst_ = this.classNameItem_ + '-first'
    /**
     * @type {string}
     * @private
     */
    this.classNameItemLast_ = this.classNameItem_ + '-last'
  }

  /**
   * @returns {Control[]}
   */
  getControls () {
    return this.controls_
  }

  /**
   * @returns {jQuery}
   */
  get$Container () {
    return this.$container_
  }

  /**
   * @param {Control} control
   * @private
   */
  setWindowForControl_ (control) {
    let aWindow = new Window({$context: $(this.getMap().getViewport())})
    control.setWindow(aWindow, true)
  }

  /**
   * This method adds some helping css classes to the items
   * @param {jQuery} $item
   * @returns {jQuery}
   * @private
   */
  addClasses_ ($item) {
    $item.addClass(this.classNameItemLast_)

    if (this.$container_.children().length === 0) {
      $item.addClass(this.classNameItemFirst_)
    } else {
      this.$container_.children(':last-child').removeClass(this.classNameItemLast_)
    }

    return $item
  }

  /**
   *
   * @param {Control} control
   * @param {Object} options
   * @param {Boolean} [options.claim=true] if claim is set to false the control won't add anything to the container
   * @param {Boolean} [options.wrap=true] if wrap is set to true a span will be put arround the element
   *    if set to false the element of the control will be inserted directly
   * @param {Boolean} [options.cssPosition=true] if this is set to true the wrap or the element will get css classes
   *    indicating its position inside the container
   * @param {HTMLElement} [options.element] if this is set it will be put inside the container instead calling
   *    control.set$Target()
   */
  addControl (control, options = {}) {
    let map = this.getMap()
    if (map) {
      map.addControl(control)
    } else {
      throw new Error('composed controls needs to be added to the map before they can get any controls')
    }

    if (control.setWindow) {
      this.setWindowForControl_(control)
    }

    if (!(options.hasOwnProperty('claim')) || !options.claim) {
      if (!(options.hasOwnProperty('wrap')) || options.wrap) {
        let $wrap = $('<div>')

        if (options.hasOwnProperty('element')) {
          $wrap.append($(options.element))
        } else {
          control.set$Target($wrap)
        }

        $wrap.addClass(this.classNameItem_)

        if (!(options.hasOwnProperty('cssPosition')) || options.cssPosition) {
          this.addClasses_($wrap)
        }

        if (!control.getVisible()) {
          $wrap.addClass(cssClasses.hidden)
        }

        control.on('change:visible', () => {
          $wrap.toggleClass(cssClasses.hidden, !control.getVisible())
        })

        this.$container_.append($wrap)
      } else {
        $(options.element).addClass(this.classNameItem_)

        if (!(options.hasOwnProperty('cssPosition')) || options.cssPosition) {
          this.addClasses_($(options.element))
        }

        if (options.hasOwnProperty('element')) {
          this.$container_.append($(options.element))
        } else {
          control.set$Target(this.$container_)
        }
      }
    }

    control.on('change:visible', () => this.updateVisibility())
    control.on('change', e => this.dispatchEvent(e))
    control.on('change:size', e => this.dispatchEvent(e))

    this.controls_.push(control)

    this.changed()
    this.updateVisibility()
  }

  /**
   * @param {Control} control
   */
  removeControl (control) {
    if (control.get$Element().hasClass(this.classNameItem_)) {
      control.get$Element().remove()
    } else if (control.get$Element().parent().hasClass(this.classNameItem_)) {
      control.get$Element().parent().remove()
    }

    this.controls_.splice(this.controls_.indexOf(control), 1)
    this.getMap().removeControl(control)

    this.changed()
  }

  /**
   * @param {G4UMap} map
   */
  setMap (map) {
    if (this.getMap()) {
      this.controls_.forEach(control => {
        this.getMap().removeControl(control)
      })

      this.controls_ = []
    }

    super.setMap(map)
  }

  updateVisibility () {
    this.setVisible(this.controls_.some(c => c.getVisible()))
  }
}

import $ from 'jquery'
import {cssClasses} from '../globals'

/**
 * This class contains logic which should be part of any Control in the Software. Any custom controls
 * should inherit from Control which inherits from this class AND from ol.cont
 */
export class ControlLogicMixin {
  /**
   * @param {g4uControlOptions} options
   */
  initialize (options = {}) {
    /**
     * @type {string}
     * @private
     */
    this.className_ = options.hasOwnProperty('className')
      ? options.className
      : ''

    if (options.hasOwnProperty('target')) {
      let $target
      if (typeof options.target === 'string' && options.target[ 0 ] !== '#') {
        $target = $('#' + options.target)
      } else {
        $target = $(options.target)
      }
      this.set$Target($target)
    }

    if (options.hasOwnProperty('element')) {
      $(options.element).addClass(this.className_).addClass('ol-unselectable ol-control')
    }

    /**
     * @type {boolean}
     */
    this.singleButton = (options.singleButton === true)
    if (this.singleButton) {
      $(options.element).addClass(cssClasses.mainButton)
    }

    /**
     * @type {string|undefined}
     * @private
     */
    this.controlName_ = options.controlName

    /**
     * @type {boolean}
     * @private
     */
    this.windowed_ = options.hasOwnProperty('windowed')
      ? options.windowed
      : false

    /**
     * @type {L10N}
     * @private
     */
    this.localiser_ = options.localiser

    /**
     * @type {string|undefined}
     * @private
     */
    this.title_ = options.hasOwnProperty('title') ? this.getLocaliser().selectL10N(options.title) : undefined

    /**
     * @type {string|undefined}
     * @private
     */
    this.tipLabel_ = options.hasOwnProperty('tipLabel') ? this.getLocaliser().selectL10N(options.tipLabel) : undefined

    /**
     * @type {string[]| string}
     * @private
     */
    this.float_ = (options.float === undefined) ? [ 'top', 'left' ] : options.float

    /**
     * @type {number}
     * @private
     */
    this.importance_ = options.importance || 0

    /**
     * @type {boolean}
     * @private
     */
    this.visible_ = true
  }

  /**
   * Returns the floating directions of the control
   * @returns {string[]|string}
   */
  getFloat () {
    return this.float_
  }

  /**
   * @returns {number}
   */
  getImportance () {
    return this.importance_
  }

  /**
   * @returns {L10N}
   */
  getLocaliser () {
    return this.localiser_
  }

  /**
   * Returns true if the element consists only of a single button
   * @returns {Boolean} element it is created in
   */
  isSingleButton () {
    return this.singleButton
  }

  /**
   * @returns {boolean}
   */
  isWindowed () {
    return this.windowed_
  }

  /**
   * Returns the name of the control if given to the constructor
   * @returns {string}
   */
  getControlName () {
    return this.controlName_
  }

  /**
   * Returns the title of the control if given to the constructor
   * @returns {string}
   */
  getTitle () {
    return this.title_
  }

  /**
   * @param {Localizable} title
   */
  setTitle (title) {
    this.title_ = this.getLocaliser().selectL10N(title)
  }

  /**
   * Returns the tipLabel of the control if given to the constructor
   * @returns {string} title
   */
  getTipLabel () {
    return this.tipLabel_
  }

  /**
   * @param {Localizable} tipLabel
   */
  setTipLabel (tipLabel) {
    this.tipLabel_ = this.getLocaliser().selectL10N(tipLabel)
  }

  /**
   * Returns the element of the control
   * @returns {jQuery} element it is created in
   */
  get$Element () {
    return $(this.element)
  }

  /**
   * Returns the target of the control
   * @returns {jQuery} element it is created in
   */
  get$Target () {
    if (this.catched$Target_) {
      return this.catched$Target_
    } else if (this.getMap()) {
      this.catched$Target_ = $(this.getMap().getViewport()).children('.ol-overlaycontainer-stopevent')
      return this.catched$Target_
    }
  }

  /**
   * Returns the CSS-className of the control
   * @returns {string} CSS-className
   */
  getClassName () {
    return this.className_
  }

  /**
   * Sets a new target for the control
   * @param {jQuery} $target element it is moved to
   */
  set$Target ($target) {
    /**
     * @type {jQuery}
     * @private
     */
    this.catched$Target_ = $target
    this.setTarget($target[ 0 ])
    $target.append(this.get$Element())
  }

  /**
   * @param {boolean} visible
   */
  setVisible (visible) {
    if (visible !== this.visible_) {
      this.get$Element().toggleClass(cssClasses.hidden, !visible)
      this.visible_ = visible
      this.dispatchEvent('change:visible')
    }
  }

  /**
   * @returns {boolean}
   */
  getVisible () {
    return this.visible_
  }
}

import ol from 'openlayers'
import $ from 'jquery'

import {Control} from './Control'
import { cssClasses, keyCodes } from '../globals'
import {VectorLayer} from '../layers/VectorLayer'

import '../../less/drawbuttons.less'
import { mixin } from '../utilities'

/**
 * @typedef {g4uControlOptions} DrawButtonsOptions
 * @property {StyleLike} [editStyle='#defaultStyle']
 * @property {StyleLike} [finishedStyle='#defaultStyle']
 */

/**
 * Enables the user to draw, edit and erase geometries on the map
 */
export class MeasurementButton extends mixin(Control) {
  /**
   * @param {DrawButtonsOptions} options
   */
  constructor (options = {}) {
    options.element = $('<div>').get(0)
    options.className = options.className || 'g4u-drawbuttons'
    options.singleButton = false

    super(options)

    this.classNames_ = {
      new: this.getClassName() + '-new',
      edit: this.getClassName() + '-edit',
      erase: this.getClassName() + '-erase'
    }

    /**
     * @type {StyleLike}
     * @private
     */
    this.editStyle_ = options.editStyle || '#defaultStyle'

    /**
     * @type {StyleLike}
     * @private
     */
    this.finishedStyle_ = options.finishedStyle || '#defaultStyle'

    this.setTitle(this.getTitle() ||
      this.getLocaliser().localiseUsingDictionary('MeasurementButton dim' + this.dimension_ + ' title')
    )
    this.setTipLabel(this.getTipLabel() ||
      this.getLocaliser().localiseUsingDictionary('MeasurementButton dim' + this.dimension_ + ' tipLabel')
    )

    this.modeNew_ = false
    this.modeEdit_ = false
    this.modeErase_ = false
  }

  createHTML () {
    const $newButton = $('<button>')
      .addClass(this.classNames_.new)
      .on('click', () => this.toggleModeNew())

    this.get$Element().append($newButton)
  }

  toggleModeNew (value) {
    this.newInteraction_.setActive(value === undefined) ? !this.modeNew_ : value
    if (this.modeNew_) {
      this.toggleModeEdit(false)
      this.toggleModeErase(false)
      this.newInteraction_.setActive(true)
    } else {
      this.newInteraction_.setActive(false)
    }
  }

  toggleModeEdit (value) {
    this.modeEdit_ = (value === undefined) ? !this.modeEdit_ : value
    if (this.modeEdit_) {
      this.toggleModeNew(false)
      this.toggleModeErase(false)
      this.editInteraction_.setActive(true)
    } else {
      this.editInteraction_.setActive(false)
    }
  }

  toggleModeErase (value) {
    this.modeErase_ = (value === undefined) ? !this.modeErase_ : value
    if (this.modeErase_) {
      this.toggleModeNew(false)
      this.toggleModeEdit(false)
      this.eraseInteraction_.setActive(true)
    } else {
      this.eraseInteraction_.setActive(false)
    }
  }

  setupNewInteraction (map) {
    this.newInteraction_ = new ol.interaction.Draw({
      source: this.layer_.getSource(),
      type: 'Point',
      style: map.get('styling').getStyle(this.editStyle_)
    })

    this.newInteraction_.setActive(false)

    map.addSupersedingInteraction('singleclick dblclick pointermove', this.newInteraction_)

    this.newInteraction_.on('change:active', () => {
      this.modeNew_ = this.newInteraction_.getActive()
    })
  }

  setupEditInteraction (map) {
    this.editInteraction_ = new ol.interaction.Modify({
      source: this.layer_.getSource(),
      style: map.get('styling').getStyle(this.editStyle_)
    })

    this.editInteraction_.setActive(false)

    map.addSupersedingInteraction('singleclick dblclick pointermove', this.editInteraction_)

    this.editInteraction_.on('change:active', () => {
      this.modeEdit__ = this.editInteraction_.getActive()
    })
  }

  setupEraseInteractions (map) {
    this.eraseHoverInteraction_ = new ol.interaction.Select({
      layers: [this.layer_],
      style: map.get('styling').getStyle(this.editStyle_),
      condition: ol.events.condition.pointerMove
    })

    this.eraseHoverInteraction_.setActive(false)

    map.addSupersedingInteraction('pointermove', this.eraseHoverInteraction_)

    this.eraseHoverInteraction_.on('change:active', () => {
      this.modeErase_ = this.eraseHoverInteraction_.getActive()
    })

    this.eraseClickInteraction_ = new ol.interaction.Select({
      layers: [this.layer_],
      style: map.get('styling').getStyle(this.editStyle_)
    })

    this.eraseClickInteraction_.setActive(false)

    this.eraseClickInteraction_.on('select', e => {
      for (let f of e.selected) {
        this.layer_.getSource().removeFeature(f)
      }
    })

    map.addSupersedingInteraction('singleclick dblclick', this.eraseClickInteraction_)

    this.eraseClickInteraction_.on('change:active', () => {
      this.modeErase_ = this.eraseClickInteraction_.getActive()
    })
  }

  /**
   * @param {G4UMap} map
   */
  setMap (map) {
    if (this.getMap()) {
      this.getMap().getLayers().remove(this.layer_)
      this.getMap().removeInteraction(this.newInteraction_)
      this.getMap().removeInteraction(this.editInteraction_)
      this.getMap().removeInteraction(this.eraseInteraction_)
    }

    super.setMap(map)

    if (map) {
      this.get$Element()
        .append(this.getLocaliser().localiseUsingDictionary('MeasurementButton dim' + this.dimension_ + ' measured'))
        .append($('<span>')
          .addClass(this.className_ + '-value')
          .append(this.$valueDisplay_)
          .append('&nbsp;')
          .append(this.$unitPlaceholder_))
        .append('<br/>')
        .append(this.getLocaliser().localiseUsingDictionary('MeasurementButton doubleClickEndsMeasurement'))

      /**
       * @type {ol.proj.Projection}
       * @private
       */
      this.measurementProjection_ = map.get('measurementProjection')

      if (!this.measurementProjection_) {
        throw new Error('MeasurementButton needs a measurementProjection. This is a global option of the map.')
      }

      if (this.dimension_ === 1) {
        this.$unitPlaceholder_.replaceWith(this.measurementProjection_.getUnits())
      }
      if (this.dimension_ === 2) {
        this.$unitPlaceholder_.replaceWith(this.measurementProjection_.getUnits() + '&sup2;')
      }

      /**
       * @type {ol.TransformFunction}
       * @private
       */
      this.measurementTransform_ = ol.proj.getTransform(map.getView().getProjection(), this.measurementProjection_)

      /**
       * @type {ol.source.Vector}
       * @private
       */
      this.source_ = new ol.source.Vector({
        projection: this.measurementProjection_
      })

      /**
       * @type {VectorLayer}
       * @private
       */
      this.layer_ = new VectorLayer({
        source: this.source_
      })

      this.layer_.setStyle(map.get('styling').getStyle(this.style_))
      map.get('styling').manageLayer(this.layer_)

      map.getLayers().insertAt(1, this.layer_) // at 0 the baselayers are

      /**
       * @type {ol.interaction.Draw}
       * @private
       */
      this.drawInteraction_ = new ol.interaction.Draw({
        source: this.source_,
        type: this.type_,
        style: map.get('styling').getStyle(this.style_)
      })

      this.drawInteraction_.setActive(false)

      map.addSupersedingInteraction('singleclick dblclick pointermove', this.drawInteraction_)

      let curFeature = null

      this.drawInteraction_.on('drawstart', e => {
        this.clear()
        curFeature = e.feature
        this.dispatchEvent('measurement')
      })

      map.on('click', () => {
        if (this.getActive()) {
          let geometry = curFeature.getGeometry().clone()
          geometry.applyTransform(this.measurementTransform_)
          if (this.dimension_ === 1) {
            this.setValue(geometry.getLength())
          } else if (this.dimension_ === 2) {
            this.setValue(geometry.getArea())
          }
        }
      })

      $(map.getViewport()).parent().on('keydown', e => {
        if (e.which === keyCodes.ESCAPE && this.drawInteraction_.getActive()) {
          this.drawInteraction_.finishDrawing()
        }
      })

      this.drawInteraction_.on('drawend', () => {
        switch (this.atDrawEnd_) {
          case 'newMeasurement':
            break
          case 'closeWindow':
            this.setActive(false)
            break
          default:
            this.drawInteraction_.setActive(false)
        }
      })

      this.activateOnMapChange()
    }
  }

  /**
   * @returns {number}
   */
  getValue () {
    return this.value_
  }

  /**
   * @param {number} value
   */
  setValue (value) {
    this.value_ = value
    this.$valueDisplay_.html('' + Math.round(value))
    this.changed()
  }

  /**
   * @returns {string}
   */
  getType () {
    return this.type_
  }

  handleActiveChange_ () {
    let active = this.getActive()

    this.layer_.setVisible(active)

    if (active) {
      if (this.getMap().get('localiser').isRtl()) {
        this.get$Element().prop('dir', 'rtl')
      } else {
        this.get$Element().prop('dir', undefined)
      }
      let popup = this.getMap().get('featurePopup')
      if (popup) {
        popup.setVisible(false)
      }
      $(this.getMap().getViewport()).addClass(cssClasses.crosshair)

      this.clear()
      this.drawInteraction_.setActive(true)
    } else {
      $(this.getMap().getViewport()).removeClass(cssClasses.crosshair)

      this.drawInteraction_.setActive(false)
    }

    this.get$Element().toggleClass(cssClasses.active, active)
  }

  /**
   * Clears any measurement
   */
  clear () {
    this.source_.clear()
    this.setValue(0)
  }
}

import ol from 'openlayers'
import $ from 'jquery'

import {Control} from './Control'
import { addTooltip } from '../html/html'
import {VectorLayer} from '../layers/VectorLayer'
import {MessageDisplay} from '../MessageDisplay'
import {cssClasses} from '../globals'

import '../../less/geolocation.less'
import { ActivatableMixin } from './ActivatableMixin'
import { ListenerOrganizerMixin } from '../ListenerOrganizerMixin'
import { mixin } from '../utilities'

/**
 * @typedef {g4uControlOptions} GeoLocationButtonOptions
 * @property {boolean} [animated] if the move on the map to the geoposition should be animated
 * @property {StyleLike} [style='#defaultStyle']
 * @property {number} [maxZoom]
 * @property {boolean} [active=false]
 */

/**
 * This class provides a button to center the view on your current geoposition.
 */
export class GeolocationButton extends mixin(Control, [ActivatableMixin, ListenerOrganizerMixin]) {
  /**
   * @param {GeoLocationButtonOptions} [options={}]
   */
  constructor (options = {}) {
    options.className = options.className || 'g4u-geolocation'
    options.singleButton = true
    options.element = $('<button>').get(0)

    super(options)

    /**
     * @type {string}
     * @private
     */
    this.classNamePushed_ = this.className_ + '-pushed'

    /**
     * @type {boolean}
     * @private
     */
    this.animated_ = options.animated

    /**
     * @type {StyleLike}
     * @private
     */
    this.style_ = options.style || '#defaultStyle'

    /**
     * @type {number}
     * @private
     */
    this.maxZoom_ = options.maxZoom

    this.setTitle(this.getTitle() || this.getLocaliser().localiseUsingDictionary('GeolocationButton title'))

    this.setTipLabel(this.getTipLabel() || this.getLocaliser().localiseUsingDictionary('GeolocationButton tipLabel'))

    this.get$Element()
      .addClass(this.className_)
      .addClass(cssClasses.mainButton)
      .html(this.getTitle())

    addTooltip(this.get$Element(), this.getTipLabel())

    /**
     * @type {MessageDisplay}
     * @private
     */
    this.buttonMessageDisplay_ = new MessageDisplay(this.get$Element())

    this.get$Element().on('click touch', () => {
      if (ol.has.GEOLOCATION) {
        this.setActive(!this.getActive())
      } else {
        this.buttonMessageDisplay_.error(
          this.getLocaliser().localiseUsingDictionary('geolocation geolocation-not-possible'),
          this.getMap().get('mobile') ? {position: 'top middle'} : {}
        )
      }
    })

    this.layer_ = null
    let geolocationOptions = { tracking: false }
    if (options.hasOwnProperty('trackingOptions')) {
      geolocationOptions.trackingOptions = options.trackingOptions
    }
    if (options.hasOwnProperty('projection')) {
      geolocationOptions.projection = options.projection
    }
    this.followLocation_ = options.followLocation || false
    this.geolocation_ = new ol.Geolocation(geolocationOptions)
    this.geolocation_.on('error', () => {
      this.buttonMessageDisplay_.error(
        this.getLocaliser().localiseUsingDictionary('geolocation geolocation-not-possible'),
        this.getMap().get('mobile') ? {position: 'top middle'} : {}
      )
      this.setActive(false)
    })

    this.on('change:active', e => this.activeChangeHandler_(e))
  }

  /**
   * @param {G4UMap} map
   */
  setMap (map) {
    if (this.getMap()) {
      this.getMap().getLayers().remove(this.layer_)
    }

    super.setMap(map)

    if (map) {
      let projection = map.getView().getProjection()
      this.geolocation_.setProjection(projection)

      let layerOptions = {source: new ol.source.Vector({projection: projection}), visible: true}
      this.layer_ = new VectorLayer(layerOptions)

      this.layer_.setStyle(map.get('styling').getStyle(this.style_))
      map.get('styling').manageLayer(this.layer_)
      map.getLayers().insertAt(1, this.layer_) // 0 is where the baseLayers are

      this.activateOnMapChange()
    }
  }

  changeHandler_ (options) {
    let source = this.layer_.getSource()
    source.clear()
    let position = this.geolocation_.getPosition()
    source.addFeature(new ol.Feature({geometry: new ol.geom.Point(position)}))

    let circle = this.geolocation_.getAccuracyGeometry()
    source.addFeature(new ol.Feature({geometry: circle}))
    if (options.hasOwnProperty('initialRun') && options.initialRun) {
      this.getMap().get('move').toExtent(circle.getExtent(), {animated: this.animated_, maxZoom: this.maxZoom_})
    } else {
      if (this.animated_) {
        this.getMap().getView().animate({'center': position})
      } else {
        this.getMap().getView().setCenter(position)
      }
    }
    if (options.hasOwnProperty('stopTracking')) {
      this.geolocation_.setTracking(!options.stopTracking)
    }
  }

  /**
   * Show/Hide the geolocation on the map as point with a circle in the size of the accuracy around
   */
  activeChangeHandler_ () {
    let active = this.getActive()
    this.get$Element().toggleClass(this.classNamePushed_, active)
    if (active) {
      this.geolocation_.setTracking(true)
      let position = this.geolocation_.getPosition()
      if (position) {
        this.changeHandler_({'stopTracking': !this.followLocation_, 'initialRun': true})
        if (this.followLocation_) {
          this.listenAt(this.geolocation_).on('change', e => this.changeHandler_(e))
        }
      } else {
        this.listenAt(this.geolocation_).once('change', () => {
          this.changeHandler_({'stopTracking': !this.followLocation_, 'initialRun': true})
          if (this.followLocation_) {
            this.listenAt(this.geolocation_).on('change', e => this.changeHandler_(e))
          }
        })
      }
    } else {
      this.layer_.getSource().clear()
      this.detachFrom(this.geolocation_, 'change')
      this.geolocation_.setTracking(false)
    }
  }

  /**
   * Creates a circle around a given position with a given radius in meters in a desired projection
   * @param {ol.Coordinate} position
   * @param {ol.ProjectionLike} projection
   * @param {number} meters
   * @returns {ol.geom.Circle}
   * @private
   */
  static makeCircle_ (position, projection, meters) {
    // using assumption that earth is a sphere and that one degree in north/south direction
    // on any point of the earth is 90/10000 km

    let position4326 = ol.proj.transform(position, projection, 'EPSG:4326')

    let secondPosition4326 = [position4326[0], position4326[1] +
    meters / 1000 * 90 / 10000]

    let secondPosition = ol.proj.transform(secondPosition4326, 'EPSG:4326', projection)

    let radius = Math.abs(position[1] - secondPosition[1])

    return new ol.geom.Circle(position, radius)
  }
}

import $ from 'jquery'

import { Control } from './Control'
import { checkFor, finishAllImages, mixin } from '../utilities'
import stripJsonComments from 'strip-json-comments'
import { Debug } from '../Debug'

import '../../less/helpbutton.less'

import 'polyfill!Array.prototype.includes'
import { ActivatableMixin } from './ActivatableMixin'
import { cssClasses } from '../globals'

/**
 * @typedef {g4uControlOptions} HelpButtonOptions
 * @property {object} [configControls={}]
 * @property {string} fileName of the json with the helptexts
 * @property {boolean} [active=false]
 */

/**
 * Shows a help button. Loads an json file with the helptexts and images from the server.
 */
export class HelpButton extends mixin(Control, ActivatableMixin) {
  /**
   * @param {HelpButtonOptions} options
   */
  constructor (options) {
    options.element = $('<div>')[0]
    options.className = options.className || 'g4u-helpbutton'

    super(options)

    this.setTitle(this.getTitle() || this.getLocaliser().localiseUsingDictionary('HelpButton title'))
    this.setTipLabel(this.getTipLabel() || options.localiser.localiseUsingDictionary('HelpButton tipLabel'))

    /**
     * @type {object}
     * @private
     */
    this.configControls_ = options.configControls || {}

    /**
     * @type {string}
     * @private
     */
    this.documentationFileName_ = options.fileName
  }

  createContent_ () {
    let localiser = this.getMap().get('localiser')

    let documentationObject = JSON.parse(stripJsonComments(this.contentData_))

    if (checkFor(documentationObject, localiser.getCurrentLang())) {
      this.language = localiser.getCurrentLang()
    } else if (checkFor(documentationObject, localiser.getDefaultLang())) {
      this.language = localiser.getDefaultLang()
    } else {
      this.language = 'de'
    }

    let makeDocumentationTable = (documentation, language) => {
      let $table = $('<table>').addClass(this.className_ + '-table')
      if (localiser.isRtl()) {
        $table.prop('dir', 'rtl')
      }
      let documentationLocalized = documentationObject[language]
      let id
      let imgData
      let descrData
      let joinWith
      let imgElements
      let $row
      let $td
      let visibleControls = []

      if (this.configControls_ && this.configControls_.hasOwnProperty('onMap')) {
        let findContainedControls = controlNamesArr => {
          visibleControls = visibleControls.concat(controlNamesArr.map(name => {
            return this.configControls_.hasOwnProperty(name) && this.configControls_[name].hasOwnProperty('controlType')
              ? this.configControls_[name].controlType : name
          }))

          for (let controlName of controlNamesArr) {
            if (this.configControls_.hasOwnProperty(controlName)) {
              if (this.configControls_[controlName].hasOwnProperty('contains')) {
                findContainedControls(this.configControls_[controlName].contains)
              }
            }
          }
        }

        if (!this.getMap().get('mobile')) {
          findContainedControls(this.configControls_.onMap.filter(c => c !== 'mobileControls'))
        } else if (this.configControls_.hasOwnProperty('mobileControls') &&
          this.configControls_.mobileControls.hasOwnProperty('contains')) {
          findContainedControls(this.configControls_.mobileControls.contains)
        }
      }
      for (id in documentationLocalized) {
        if (documentationLocalized.hasOwnProperty(id) && documentationLocalized[id]) {
          imgData = documentationLocalized[id].img
          descrData = documentationLocalized[id].descr || ''
          joinWith = documentationLocalized[id].joinWith || ''

          if (visibleControls.includes(id)) {
            $row = $('<tr>')

            imgElements = `<td class="${this.className_}-img"><div class="${this.className_}-imgDiv">`
            if (imgData) {
              if ($.isArray(imgData)) {
                for (let j = 0, jj = imgData.length; j < jj; j++) {
                  imgData[j] = `<img class="${this.className_}-docuImg" src="images/doc/${imgData[j]}">`
                }
                imgElements += imgData.join(joinWith)
              } else {
                imgElements += `<img class="${this.className_}-docuImg" src="images/doc/${imgData}">`
              }
            }
            imgElements += '</div></td>'

            $row.append(imgElements)
            $td = $('<td>').addClass(this.className_ + '-descr')
            if ($.isArray(descrData)) {
              $td.append(descrData.join('<p>'))
            } else {
              $td.append(descrData)
            }
            $row.append($td)
          }
          $table.append($row)
        }
      }
      return $table
    }

    this.get$Element().append(makeDocumentationTable(documentationObject, this.language))
  }

  /**
   * @param {G4UMap} map
   */
  setMap (map) {
    super.setMap(map)

    if (map) {
      this.activateOnMapChange()
    }
  }

  /**
   * @param {boolean} active
   */
  setActive (active) {
    if (!this.loading_) {
      if (active) {
        if (!this.contentData_) {
          this.loading_ = true

          $.ajax(this.documentationFileName_, {dataType: 'text'})
            .fail(() => {
              let msg = 'Wasn\'t able to load the documentation file ' + this.documentationFileName_
              Debug.error(msg)
              throw new Error(msg)
            })
            .done(data => {
              this.contentData_ = data
              this.createContent_()

              finishAllImages(this.get$Element()).then(() => {
                this.loading_ = false
                this.dispatchEvent('loaded')
                super.setActive(active)
              })
            })
        } else {
          super.setActive(active)
        }
      } else {
        super.setActive(active)
      }
    } else {
      this.once('loaded', () => this.setActive(active))
    }
    this.get$Element().toggleClass(cssClasses.active, active)
  }
}

import $ from 'jquery'

import { Attribution } from './Attribution'
import {Control} from './Control'

import '../../less/infobutton.less'
import { URL } from '../URLHelper'
import { ActivatableMixin } from './ActivatableMixin'
import { mixin } from '../utilities'
import { cssClasses } from '../globals'

/**
 * @typedef {g4uControlOptions} InfoButtonOptions
 * @property {URLLike} contentURL url providing content to be shown
 * @property {boolean} [attribution=true]
 * @property {boolean} [active=false]
 */

/**
 * This control opens a window with showing some specified info text
 */
export class InfoButton extends mixin(Control, ActivatableMixin) {
  /**
   * @param {InfoButtonOptions} options
   */
  constructor (options = {}) {
    options.className = options.className || 'g4u-infobutton'
    options.element = $('<div>')[0]

    super(options)

    /**
     * @type {string}
     * @private
     */
    this.classNameAttributions_ = this.className_ + '-attributions'

    /**
     * @type {string}
     * @private
     */
    this.classNameContent_ = this.className_ + '-content'

    this.setTitle(this.getLocaliser().localiseUsingDictionary('InfoPage title'))
    this.setTipLabel(this.getLocaliser().localiseUsingDictionary('InfoPage tipLabel'))

    /**
     * @type {jQuery}
     * @private
     */
    this.$content_ = $('<div>').addClass(this.classNameContent_)

    this.get$Element()
      .append(this.$content_)

    /**
     * @type {boolean}
     * @private
     */
    this.attribution_ = options.attribution !== false

    /**
     * @type {URL}
     * @private
     */
    this.contentURL_ = URL.extractFromConfig(options, 'contentURL')

    if (this.attribution_) {
      /**
       * @type {jQuery}
       * @private
       */
      this.$attributions_ = $('<div>')

      this.get$Element()
        .append($('<h2>').html(this.getLocaliser().localiseUsingDictionary('InfoPage copyrightTitle')))
        .append(this.$attributions_)

      /**
       * @type {Attribution}
       * @private
       */
      this.attributionControl_ = new Attribution({
        target: this.get$Element()[0],
        localiser: this.getLocaliser(),
        collapsible: false,
        className: this.classNameAttributions_
      })
    }

    /**
     * @type {boolean}
     * @private
     */
    this.loaded_ = false
  }

  /**
   * @param {G4UMap} map
   */
  setMap (map) {
    let oldMap = this.getMap()

    if (oldMap && this.attribution_) {
      oldMap.removeControl(this.attributionControl_)
    }

    super.setMap(map)

    if (map) {
      if (this.contentURL_) {
        this.contentURL_.extractParamsFromMap(map)
      }

      if (this.attribution_) {
        map.addControl(this.attributionControl_)
      }

      this.activateOnMapChange()
    }
  }

  /**
   * @param {boolean} active
   */
  setActive (active) {
    if (active) {
      if (this.getMap().get('localiser').isRtl()) {
        this.get$Element().prop('dir', 'rtl')
      } else {
        this.get$Element().prop('dir', undefined)
      }
      if (!this.loaded_ && this.contentURL_) {
        $.get(this.contentURL_.finalize(), (data) => {
          this.$content_.html(data)
          super.setActive(active)
          this.loaded_ = true
        })
      } else {
        super.setActive(active)
      }
    } else {
      super.setActive(active)
    }
    this.get$Element().toggleClass(cssClasses.active, active)
  }
}

import $ from 'jquery'

import {Control} from './Control'
import { addTooltip } from '../html/html'
import {Debug} from '../Debug'

import '../../less/languageControls.less'

/**
 * @typedef {g4uControlOptions} LanguageSwitcherButtonOptions
 */

/**
 * A button to switch the language that is being used.
 * Works if only two languages are configured.
 */
export class LanguageSwitcherButton extends Control {
  /**
   * @param {LanguageSwitcherButtonOptions} options
   */
  constructor (options = {}) {
    options.element = $('<button>').get(0)
    options.singleButton = true
    options.className = options.className || 'g4u-languageswitcher'

    super(options)

    /**
     * @type {jQuery}
     * @private
     */
    this.$button_ = this.get$Element().addClass(this.className_ + '-button')

    let languages = this.getLocaliser().getAvailableLanguages()

    if (languages.length < 2) {
      Debug.info('You do not have any languages to switch between.')
      Debug.info('Could it be that you forgot to disable languageSwitcherButton?')
    } else if (languages.length > 2) {
      Debug.info('You have more than 2 languages to switch between.')
      Debug.info('In this case you need to use languageSwitcherMenu.')
    }

    this.$button_.on('click', () => {
      let targetLanguage = languages[1 - languages.indexOf(this.getLocaliser().getCurrentLang())]
      this.getLocaliser().setCurrentLang(targetLanguage)
    })
  }

  /**
   * @param {G4UMap} map
   */
  setMap (map) {
    if (map) {
      this.$button_.html(this.getLocaliser().getCurrentLang())
      addTooltip(this.$button_, this.getLocaliser().localiseUsingDictionary('LanguageSwitcherButton tipLabel'))
    }

    super.setMap(map)
  }
}

import $ from 'jquery'

import {Dropdown} from '../html/Dropdown'
import { addTooltip } from '../html/html'
import {Control} from './Control'
import { cssClasses } from '../globals'
import {Debug} from '../Debug'

import '../../less/languageControls.less'
import {ListenerOrganizerMixin} from '../ListenerOrganizerMixin'
import {mixin} from '../utilities'
import { ActivatableMixin } from './ActivatableMixin'

/**
 * @typedef {g4uControlOptions} LanguageSwitcherMenuOptions
 * @property {boolean} [showLongLanguage=false]
 * @property {boolean} [active=false]
 */

/**
 * A button to switch the language that is being used.
 */
export class LanguageSwitcherMenu extends mixin(Control, [ListenerOrganizerMixin, ActivatableMixin]) {
  /**
   * @param {LanguageSwitcherMenuOptions} options
   */
  constructor (options = {}) {
    options.className = options.className || 'g4u-languageswitchermenu'
    options.element = $('<div>').get(0)
    options.singleButton = false

    super(options)

    this.showLongLanguage_ = options.showLongLanguage || false

    this.setTitle(this.getLocaliser().getCurrentLang())

    /**
     * @type {jQuery}
     * @private
     */
    this.$button_ = $('<button>')
      .addClass(this.className_ + '-button')
      .addClass(cssClasses.mainButton)

    if (this.showLongLanguage_) {
      this.$button_.addClass(this.className_ + '-lang-long')
    }

    this.get$Element().append(this.$button_)

    let dropdownOptions = {'className': 'g4u-dropdown'}

    /**
     * @type {Dropdown}
     * @private
     */
    this.dropdown_ = new Dropdown(dropdownOptions)
    this.get$Element().append(this.dropdown_.get$Element())

    let languages = this.getLocaliser().getAvailableLanguages()

    if (languages.length < 2) {
      Debug.info('You do not have any languages to switch between.')
      Debug.info('Could it be that you forgot to disable languageSwitcherMenu?')
    } else if (languages.length === 2) {
      Debug.info('You only have 2 languages to switch between.')
      Debug.info('What about using languageSwitcherButton in place of languageSwitcherMenu?')
    }

    this.dropdown_.setEntries(languages,
      languages.map(l => `${l.toUpperCase()} - ${this.getLocaliser().localiseUsingDictionary(l)}`))

    this.dropdown_.on('select', () => this.getLocaliser().setCurrentLang((this.dropdown_.getValue())))

    this.dropdown_.on('close', () => {
      this.setActive(false)
      $(this.getMap().getViewport()).focus()
    })

    this.on('change:active', () => this.handleActiveChange_())
  }

  /**
   * @param {G4UMap} map
   */
  setMap (map) {
    if (this.getMap()) {
      this.detachAllListeners()
      this.dropdown_.detach()

      this.setActive(false)
    }

    super.setMap(map)

    if (map) {
      this.dropdown_.setFastMode(map.get('mobile'))
      map.on('change:mobile', () => this.dropdown_.setFastMode(map.get('mobile')))

      this.listenAt(this.get$Element()).on('click', () => {
        this.collapse_ = false
        this.setActive(!this.getActive())
      })

      let curLang = this.getLocaliser().getCurrentLang()
      if (!this.showLongLanguage_) {
        this.$button_.html(curLang.toUpperCase())
      } else {
        this.$button_.html(curLang.toUpperCase() + ' - ' + this.getLocaliser().localiseUsingDictionary(curLang))
      }

      addTooltip(this.$button_, this.getLocaliser().localiseUsingDictionary('LanguageSwitcherMenu tipLabel'))

      this.activateOnMapChange()
    }
  }

  handleActiveChange_ () {
    this.dropdown_.setActivated(this.getLocaliser().getCurrentLang())
  }
}

import $ from 'jquery'

import { GroupLayer } from '../layers/GroupLayer'
import { ButtonBox } from '../html/ButtonBox'
import { Control } from './Control'
import { offset, mixin } from '../utilities'
import { Window } from '../html/Window'

import '../../less/layerselector.less'
import { ListenerOrganizerMixin } from '../ListenerOrganizerMixin'
import { URL } from '../URLHelper'
import { addTooltip, changeTooltip } from '../html/html'

/**
 * @typedef {g4uControlOptions} LayerSelectorOptions
 * @property {boolean} [toggle=true] if the layers are toggable
 * @property {boolean} [collapsible=true] if the menu should be collapsible
 * @property {boolean} [collapsed=false] if the menu starts collapsed
 * @property {number} [minVisibleEntries=6] amount of minimal visible elements
 * @property {string} layerGroupName the name of the layerGroup this selector is connected to. For example 'baseLayers'
 * @property {number} [minLayerAmount=1] the minimum number of layers which should be visible to show this selector
 */

/**
 * This control shows Buttons to let you select the layer you want to see on the map.
 * It supports categories and nested categories - each {GroupLayer}-Object will be interpreted as a category.
 */
export class LayerSelector extends mixin(Control, ListenerOrganizerMixin) {
  /**
   * @param {LayerSelectorOptions} options
   */
  constructor (options = {}) {
    options.className = options.className || 'g4u-layerselector'
    options.element = $('<div>')[0]
    options.singleButton = false

    super(options)

    /**
     * @type {String}
     * @private
     */
    this.layerGroupName_ = options.layerGroupName

    /**
     * @type {number}
     * @private
     */
    this.minLayerAmount_ = options.hasOwnProperty('minLayerAmount') ? options.minLayerAmount : 1

    /**
     * @type {boolean}
     * @private
     */
    this.collapsible_ = !options.hasOwnProperty('collapsible') || options.collapsible

    /**
     * @type {boolean}
     * @private
     */
    this.collapsed_ = options.collapsed || false

    /**
     * classNames
     * @type {object.<string, string>}
     * @protected
     */
    this.classNames_ = {
      menu: this.getClassName() + '-menu',
      layerButton: this.getClassName() + '-layerbutton',
      active: this.getClassName() + '-active',
      featureInfo: this.getClassName() + '-info',
      featureInfoActive: this.getClassName() + '-info-active'
    }

    /**
     * @type {ButtonBox}
     * @private
     */
    this.menu_ = new ButtonBox({
      element: this.get$Element(),
      className: this.getClassName(),
      title: this.getLocaliser().selectL10N(this.getTitle()),
      collapsible: this.collapsible_,
      collapsed: this.collapsed_
    })

    this.menu_.on('change:collapsed', () => this.dispatchEvent('change:size'))

    this.get$Element().append(this.menu_.get$Element())

    /**
     * @type {boolean}
     * @private
     */
    this.toggle_ = options.toggle || true

    /**
     * @type {number}
     * @private
     */
    this.minVisibleButtons_ = options.minVisibleEntries || 5

    /**
     * @type {boolean}
     * @private
     */
    this.visible_ = true
  }

  /**
   * @returns {boolean}
   */
  getCollapsible () {
    return this.collapsible_
  }

  /**
   * @returns {boolean}
   */
  getCollapsed () {
    return this.menu_.getCollapsed()
  }

  /**
   * @param {boolean} collapsed
   * @param {boolean} silent
   */
  setCollapsed (collapsed, silent) {
    if (collapsed !== this.menu_.getCollapsed()) {
      this.menu_.setCollapsed(collapsed, silent)
    }
  }

  addWindowToButton ($button, layer) {
    let windowConfig = layer.get('window')

    let window = new Window({
      map: this.getMap()
    })

    if (!this.$windowContainer_) {
      this.$windowContainer_ = $('<span>')
      this.get$Element().append(this.$windowContainer_)
    }

    this.$windowContainer_.append(window.get$Element())

    window.get$Element().attr('data-layer-0', layer.get('id'))

    let content

    let showWindow = () => {
      if (this.getMap().get('localiser').isRtl()) {
        window.get$Body().prop('dir', 'rtl')
      } else {
        window.get$Body().prop('dir', undefined)
      }
      window.get$Body().html(content)
      window.setVisible(true)
    }

    let hideWindow = () => {
      window.setVisible(false)
    }

    this.listenAt($button).on('click', () => {
      if (layer.getVisible()) {
        if (!content) {
          let url = URL.extractFromConfig(windowConfig, 'url', undefined, this.getMap())
          $.get(url.finalize(), data => {
            content = data
            showWindow()
          })
        } else {
          showWindow()
        }
      } else {
        hideWindow()
      }
    })
  }

  /**
   * this method builds a button for a layer. It toggles visibility if you click on it
   * @param {ol.layer.Base} layer
   * @param {jQuery} $target
   */
  buildLayerButton (layer, $target) {
    if (layer.get('available')) {
      let $button = $('<button>')
        .addClass(this.classNames_.layerButton)
        .attr('id', layer.get('id'))
        .html(layer.get('title'))

      if (this.getMap().get('localiser').isRtl()) {
        $button.prop('dir', 'rtl')
      }

      let activeClassName = this.classNames_.menu + '-active'

      this.listenAt($button).on('click', () => {
        if (this.toggle_) {
          layer.setVisible(!layer.getVisible())
        } else {
          layer.setVisible(true)
        }
        this.dispatchEvent({
          type: 'click:layer',
          layer: layer
        })
      })

      if (layer.getVisible()) {
        $button.addClass(activeClassName)
      }

      this.listenAt(layer).on('change:visible', () => {
        $button.toggleClass(activeClassName, layer.getVisible())
        if (!layer.getVisible()) {
          $button.removeClass('g4u-layer-loading')
        }
      })

      if (layer.get('window')) {
        this.addWindowToButton($button, layer)
      }

      this.listenAt(layer).on('loadcountstart', () => {
        $button.addClass('g4u-layer-loading')
      })

      this.listenAt(layer).on('loadcountend', () => {
        $button.removeClass('g4u-layer-loading')
      })

      $target.append($button)

      return $button
    }
  }

  /**
   * builds a category button which collapses on click
   * @param {GroupLayer} categoryLayer
   * @param {jQuery} $target
   */
  buildCategoryButton (categoryLayer, $target) {
    let $nextTarget = $target

    if (categoryLayer.get('available')) {
      let activateChildren = categoryLayer.get('activateChildren') !== false

      let collapsed = categoryLayer.get('collapsed')
      if (collapsed === undefined) {
        collapsed = !categoryLayer.countChildrenVisible() && (categoryLayer.get('collapsed') !== false)
        categoryLayer.set('collapsed', collapsed)
      }

      let menu = new ButtonBox({
        className: this.classNames_.menu,
        title: this.getLocaliser().selectL10N(categoryLayer.get('title')),
        rtl: this.getMap().get('localiser').isRtl(),
        titleButton: activateChildren,
        collapsed,
        id: categoryLayer.get('id')
      })

      let countChildren = categoryLayer.countChildren()
      let countVisibleChildren = categoryLayer.countChildrenVisible()

      let updateButtonActivities = () => {
        if (countVisibleChildren === 0) {
          menu.setCollapseButtonActive(false)
          if (activateChildren) {
            menu.setTitleButtonActive(false)
          }
        } else if (countVisibleChildren === countChildren) {
          menu.setCollapseButtonActive(true)
          if (activateChildren) {
            menu.setTitleButtonActive(true)
          }
        } else {
          menu.setCollapseButtonActive(true)
          if (activateChildren) {
            menu.setTitleButtonActive(false)
          }
        }
      }

      updateButtonActivities()

      let forEachChildLayer = childLayer => {
        this.listenAt(childLayer)
          .on(['change:visible', 'change:childVisible'], e => {
            let changedLayer = e.child || childLayer

            if (changedLayer.getVisible()) {
              countVisibleChildren++
            } else {
              countVisibleChildren--
            }

            updateButtonActivities()
          })
      }

      categoryLayer.getLayers().forEach(forEachChildLayer)

      this.listenAt(categoryLayer.getLayers())
        .on('add', e => forEachChildLayer(e.element))

      this.listenAt(menu)
        .on('title:click', () => {
          let visible = countVisibleChildren < countChildren
          categoryLayer.recursiveForEach(childLayer => {
            if (!(childLayer instanceof GroupLayer)) {
              childLayer.setVisible(visible)
            }
          })
          this.dispatchEvent({
            type: 'click:layer',
            layer: categoryLayer
          })
        })

      $target.append(menu.get$Element())

      $nextTarget = menu.get$Body()

      this.listenAt(menu)
        .on('change:collapsed', () => {
          categoryLayer.set('collapsed', menu.getCollapsed())
          this.dispatchEvent('change:size')
          this.changed()
        })

      for (let childLayer of categoryLayer.getLayers().getArray()) {
        this.chooseButtonBuilder(childLayer, $nextTarget)
      }

      return menu
    } else {
      for (let childLayer of categoryLayer.getLayers().getArray()) {
        this.chooseButtonBuilder(childLayer, $nextTarget)
      }
    }
  }

  buildWMSButton (wmsLayer, $target) {
    if (wmsLayer.get('available')) {
      let layerButtons = wmsLayer.get('buttons')

      if (layerButtons && layerButtons.length) {
        let countActiveButtons = 0
        let wmsSource = wmsLayer.getSource()

        let allLayersParams = []
        let allQueryLayersParams = []

        let featureInfoCheckable = wmsSource.isFeatureInfoCheckable()

        let menu
        if (layerButtons.length > 1) {
          menu = new ButtonBox({
            className: this.classNames_.menu,
            title: this.getLocaliser().selectL10N(wmsLayer.get('title')),
            titleButton: true,
            collapsed: wmsLayer.get('collapsed') !== false,
            id: wmsLayer.get('id')
          })

          $target.append(menu.get$Element())

          this.listenAt(menu)
            .on('change:collapsed', () => {
              this.dispatchEvent('change:size')
              this.changed()
            })

          $target = menu.get$Body()
        }

        let activeClassName = this.classNames_.menu + '-active'

        for (let layerButton of layerButtons) {
          allLayersParams = allLayersParams.concat(layerButton.LAYERS)
          allQueryLayersParams = allQueryLayersParams.concat(layerButton.QUERY_LAYERS)

          let $button = $('<span>')
            .addClass(this.classNames_.layerButton)
            .addClass('button')
            .html(this.getLocaliser().selectL10N(layerButton.title))
          let $toggleFeatureInfo = $('<span>')
            .addClass(this.classNames_.featureInfo)
          addTooltip($toggleFeatureInfo,
            this.getLocaliser().localiseUsingDictionary('LayerSelector featureInfo hide'))

          $target.append($button)

          let toggleButtonActive, setCheckboxActive

          toggleButtonActive = () => {
            let active = !wmsSource.getWMSLayersVisible(layerButton.LAYERS)

            if (active) {
              countActiveButtons++
            } else {
              countActiveButtons--
            }

            $button.toggleClass(activeClassName, active)

            wmsSource.toggleWMSLayers(layerButton.LAYERS, active)

            if (!active && featureInfoCheckable) {
              setCheckboxActive(false)
            }

            if (countActiveButtons === 0) {
              wmsLayer.setVisible(false)
              if (menu) {
                menu.setCollapseButtonActive(false)
                menu.setTitleButtonActive(false)
              }
            } else if (countActiveButtons === layerButtons.length) {
              wmsLayer.setVisible(true)
              if (menu) {
                menu.setCollapseButtonActive(true)
                menu.setTitleButtonActive(true)
              }
            } else {
              wmsLayer.setVisible(true)
              if (menu) {
                menu.setCollapseButtonActive(true)
                menu.setTitleButtonActive(false)
              }
            }
            this.dispatchEvent({
              type: 'click:layer',
              layer: wmsLayer,
              wmsLayer: true
            })
          }

          setCheckboxActive = checkboxActive => {
            if (checkboxActive && !wmsSource.getWMSLayersVisible(layerButton.LAYERS)) {
              toggleButtonActive()
            }
            wmsSource.toggleWMSQueryLayers(layerButton.QUERY_LAYERS, checkboxActive)
            $toggleFeatureInfo.toggleClass(this.classNames_.featureInfoActive, checkboxActive)
            if (checkboxActive) {
              changeTooltip($toggleFeatureInfo,
                this.getLocaliser().localiseUsingDictionary('LayerSelector featureInfo hide'))
            } else {
              changeTooltip($toggleFeatureInfo,
                this.getLocaliser().localiseUsingDictionary('LayerSelector featureInfo show'))
            }
          }

          if (layerButton.checked) {
            wmsSource.toggleWMSQueryLayers(layerButton.QUERY_LAYERS, true)
            $toggleFeatureInfo.addClass(this.classNames_.featureInfoActive)
          }

          this.listenAt($button)
            .on('click', () => {
              toggleButtonActive()
            })

          if (featureInfoCheckable) {
            $button.append($toggleFeatureInfo)
            this.listenAt($toggleFeatureInfo).on('click', e => {
              setCheckboxActive(!$toggleFeatureInfo.hasClass(this.classNames_.featureInfoActive))
              e.stopPropagation()
            })
          }
        }

        if (menu) {
          this.listenAt(menu).on('title:click', () => {
            let activateAll = countActiveButtons < layerButtons.length
            if (activateAll) {
              wmsSource.toggleWMSLayers(allLayersParams, true)
              countActiveButtons = layerButtons.length
            } else {
              wmsSource.toggleWMSLayers(allLayersParams, false)
              if (featureInfoCheckable) {
                wmsSource.toggleWMSQueryLayers(allQueryLayersParams, false)
                menu.get$Body().find('input[type=checkbox]').prop('checked', false)
              }
              countActiveButtons = 0
            }

            menu.get$Body().find('button').toggleClass(activeClassName, activateAll)
            wmsLayer.setVisible(activateAll)
            menu.setCollapseButtonActive(activateAll)
            menu.setTitleButtonActive(activateAll)

            this.dispatchEvent({
              type: 'click:category',
              layer: wmsLayer,
              wmsLayer: true
            })
          })

          return menu
        }
      } else {
        return this.buildLayerButton(wmsLayer, $target)
      }
    }
  }

  /**
   * This method chooses the right builder function
   * @param {ol.layer.Base} layer
   * @param {jQuery} $target
   */
  chooseButtonBuilder (layer, $target) {
    if (layer instanceof GroupLayer) {
      return this.buildCategoryButton(layer, $target)
    } else if (layer.getSource && layer.getSource() && layer.getSource().isFeatureInfoCheckable) {
      return this.buildWMSButton(layer, $target)
    } else {
      return this.buildLayerButton(layer, $target)
    }
  }

  /**
   * @param {G4UMap} map
   */
  setMap (map) {
    if (this.getMap()) {
      this.clear()
    }

    super.setMap(map)

    if (map) {
      this.build()
    }
  }

  clear () {
    this.detachAllListeners()
    this.menu_.get$Body().empty()
  }

  build () {
    this.layers_ = this.getMap().get(this.layerGroupName_).getLayers()
    if (this.layers_.getLength() >= this.minLayerAmount_) {
      this.setVisible(true)
      let menuFunctions = new ButtonBox({className: this.classNames_.menu})
      for (let layer of this.layers_.getArray()) {
        this.chooseButtonBuilder(layer, this.menu_.get$Body())
      }
      menuFunctions.giveLastVisible(this.get$Element().children(':last-child').children(':last-child'))
      this.listenAt(this.menu_).on('change:collapsed', () => this.dispatchEvent('change:size'))
    } else {
      this.setVisible(false)
    }
  }

  rebuild () {
    this.clear()
    this.build()
  }

  /**
   * Returns true if the control is squeezable in the given dimension. Used by Positioning.
   * @param {string} dimension
   * @returns {boolean}
   */
  isSqueezable (dimension) {
    return dimension === 'height'
  }

  /**
   * Squeezes the control in the given dimension by the provided value. Used by Positioning
   * Returns the value the control could get squeezed by.
   * @param {string} dimension
   * @param {number} value
   * @returns {number}
   */
  squeezeBy (dimension, value) {
    if (dimension === 'height') {
      let $contentBox = this.get$Element().find(`.${this.getClassName()}-content`)
      let $buttons = $contentBox.find('button:visible')
        .filter(`.${this.getClassName()}-layerbutton,.${this.getClassName()}-menu-titlebutton`)

      if ($buttons.length > 1) {
        let height = $contentBox.height()
        let buttonHeight = offset($buttons.eq(1), $buttons.eq(0)).top

        let newHeight = Math.max(buttonHeight * this.minVisibleButtons_, height - value)

        if (height > newHeight) {
          $contentBox.css('max-height', newHeight)
          return height - newHeight
        }
      }
    }

    return 0
  }

  beforePositioning () {
    this.scrolled_ = this.menu_.get$Body().scrollTop()
  }

  /**
   * used by positioning
   */
  afterPositioning () {
    this.menu_.get$Body().scrollTop(this.scrolled_)
  }

  /**
   * Removes the squeeze. Used by Positioning.
   * @param {string} dimension
   */
  release (dimension) {
    if (dimension === 'height') {
      this.get$Element().find(`.${this.getClassName()}-content`).css('max-height', '')
    }
  }
}

