import '../../less/mobilecontrols.less'

/**
 * This class represents the mobile menu containing all the controls.
 */
export class MobileControls extends ShiftableComposedControl {
  /**
   * @param {ShiftableComposedControlOptions} options
   */
  constructor (options = {}) {
    options.className = options.className || 'g4u-mobilecontrols'
    options.element = $('<div>').get(0)
    options.containerClassName = options.className + '-container'
    options.visibleControls = options.visibleControls || 6

    super(options)
  }

  /**
   * @param {Control} control
   * @param {g4uControlOptions} options
   */
  addControl (control, options) {
    if (control.isWindowed() || control.isSingleButton()) {
      super.addControl(control, options)
    } else {
      super.addControl(new WindowDecorator({
        component: control
      }), options)
    }
  }

  /**
   * @param {G4UMap} map
   */
  setMap (map) {
    if (this.getMap()) {
      this.getMap().un('change:mobile', this.handler_)
    }

    super.setMap(map)

    if (map) {
      this.handler_ = () => {
        if (!map.get('mobile')) {
          for (let control of this.getControls().filter(c => c.setWindowVisible)) {
            control.setWindowVisible(false)
          }
        }
      }
      map.on('change:mobile', this.handler_)
    }
  }
}

import ol from 'openlayers'

import {mixin} from '../utilities'
import {RewireMixin} from './RewireMixin'

import '../../less/mouseposition.less'

/**
 * @typedef {g4uControlOptions} MousePositionOptions
 * @property {function} [coordinateFormat]
 */

/**
 * @extends Control
 */
export class MousePosition extends mixin(ol.control.MousePosition, RewireMixin) {
  /**
   * @param {MousePositionOptions} [options={}]
   */
  constructor (options = {}) {
    options.className = (options.hasOwnProperty('className')) ? options.className : 'g4u-mouseposition'

    let truncateToString = (x, length) => {
      let lead = x.toString().match(/^[^.]*/)[ 0 ]
      if (lead.length >= length) {
        return lead
      } else {
        return x.toFixed(length - lead.length)
      }
    }

    let digits = options.digits || 8

    options.coordinateFormat = c => {
      return `${truncateToString(c[0], digits)}, ${truncateToString(c[1], digits)}`
    }

    let dashs = '&ndash;' + '.' + '&ndash;'.repeat(digits - 1)
    options.undefinedHTML = `${dashs}, ${dashs}`

    super(options)
  }
}

import ol from 'openlayers'
import {RewireMixin} from './RewireMixin'
import { ListenerOrganizerMixin } from '../ListenerOrganizerMixin'
import {mixin} from '../utilities'

import '../../less/overviewmap.less'

/**
 * @typedef {g4uControlOptions} OverviewMapOptions
 * @property {Localizable} [tipLabel]
 * @property {ol.layer.Group} [layerGroup]
 */

/**
 * @extends Control
 */
export class OverviewMap extends mixin(mixin(ol.control.OverviewMap, RewireMixin), ListenerOrganizerMixin) {
  /**
   * @param {OverviewMapOptions} [options={}]
   */
  constructor (options = {}) {
    if (options.hasOwnProperty('title')) {
      options.tipLabel = options.localiser.selectL10N(options.title)
    } else {
      options.tipLabel = (options.hasOwnProperty('tipLabel'))
        ? options.localiser.selectL10N(options.tipLabel)
        : options.localiser.localiseUsingDictionary('OverviewMap tipLabel')
    }

    options.layers = []

    super(options)
  }

  setMap (map) {
    if (this.getMap()) {
      this.detachAllListeners()
    }
    super.setMap(map)
    if (map) {
      const view = map.getView()

      this.getOverviewMap().setLayerGroup(map.get('baseLayers'))

      let $overviewmap = this.get$Element().find('.ol-overviewmap-map')

      $overviewmap = $overviewmap.add($overviewmap.find('.ol-overviewmap-box'))

      let dontClick = false

      this.listenAt($overviewmap).on('click', e => {
        if (!dontClick) {
          view.setCenter(view.constrainCenter(this.getOverviewMap().getEventCoordinate(e)))
        }
      })

      let mouseDown = false

      this.listenAt($overviewmap).on('mousedown', () => {
        dontClick = false
        mouseDown = true
      })

      this.listenAt($overviewmap).on('mouseup', e => {
        mouseDown = false
      })

      this.listenAt($overviewmap).on('mousemove', e => {
        if (mouseDown) {
          view.setCenter(view.constrainCenter(this.getOverviewMap().getEventCoordinate(e)))
        }
      })

      this.getOverviewMap().getView().on('change:center', () => {
        mouseDown = false
        dontClick = true
      })

      let $button = this.get$Element().find('button')
      this.listenAt($button).on('click', () => this.dispatchEvent('change:size'))
    }
  }

  afterPositioning () {
    this.getOverviewMap().updateSize()
  }
}

import $ from 'jquery'

import {Control} from './Control'
import { addTooltip } from '../html/html'
import {cssClasses} from '../globals'

import '../../less/printbutton.less'

export class PrintButton extends Control {
  /**
   * @param {g4uControlOptions} options
   */
  constructor (options = {}) {
    options.className = options.className || 'g4u-printbutton'
    options.element = $('<button>').get(0)

    super(options)

    this.get$Element().addClass(this.className_)
      .addClass(cssClasses.mainButton).html(this.getLocaliser().localiseUsingDictionary('PrintButton title'))

    addTooltip(this.get$Element(), this.getLocaliser().localiseUsingDictionary('PrintButton tipLabel'))

    this.get$Element().on('click', () => this.onClick_())
  }

  /**
   * @private
   */
  onClick_ () {
    let setDivSize = function (div, mapSize) {
      $(div).innerWidth(mapSize[ 0 ])
      $(div).innerHeight(mapSize[ 1 ])
    }

    //
    // Variant A: Create a new window and copy the canvas over there. This raises security issues in IE.
    //
    // Variant B: Apply a special css class to the body that hides everything except the map for @media print
    //    DOES NOT WORK
    //      -> doesn't work because hiding everything will hide the parents of the map, too
    //
    // Variant C: Create a new body and switching it with the old body for printing
    //      -> This heavily manipulates the existing DOM-Structure and it
    //        relies on the ol-viewport div being the only child of its parent.
    //        works in IE!
    //
    // Variant D: Convert everything to a pdf
    //    IMPLEMENTED IN MODULE
    //      -> jsPDF Print Module
    //
    // Variant E: Add a function to reproduce a map in its current state on another page
    //    NOT IMPLEMENTED
    //      -> some work, but maybe useful for other functions, too
    //

    let isMapCanvasTainted = function () { // Needs to be looked into
      try {
        let viewport = this.getMap().getViewport()
        let srcCanvas = viewport.getElementsByTagName('canvas')[ 0 ]
        srcCanvas.getContext('2d').getImageData(1, 1, 1, 1)
        return false
      } catch (e) {
        return true
      }
    }

    //
    // We check if we can use variant a, if we cant, we use variant c
    //

    if (!isMapCanvasTainted()) {
      //
      // Variant A: Creating a new window and copying the canvas over there. This raises security issues in IE.
      //

      // new window
      let newWindow = window.open('', '_blank')

      let jStyleDiv = $('<div>').append(
        $('style').clone()
      ).append(
        $('link[type="text/css"]').clone()
      )

      // copying the map
      let viewport = this.getMap().getViewport()
      let mapClone = viewport.parentNode.cloneNode(true)
      setDivSize(mapClone, this.getMap().getSize())
      let srcCanvas = viewport.getElementsByTagName('canvas')[ 0 ]
      let destCanvas = mapClone.getElementsByTagName('canvas')[ 0 ]
      let destContext = destCanvas.getContext('2d')
      destContext.drawImage(srcCanvas, 0, 0)
      let newdoc = newWindow.document

      // writing the document
      newdoc.open()
      newdoc.write('<!doctype html>')
      newdoc.write('<html>')
      newdoc.write('<head>')
      newdoc.write('<title>')
      newdoc.write(this.getLocaliser().localiseUsingDictionary('PrintButton windowTitle'))
      newdoc.write('</title>')
      newdoc.write(jStyleDiv.html())
      newdoc.write('<script type="text/javascript">')
      newdoc.write('window.onload = function () {\n')
      newdoc.write('\twindow.focus();\n')
      newdoc.write('\tsetTimeout( function () {\n') // we need to wait till the map is appended
      newdoc.write('\t\twindow.print();\n')
      newdoc.write('\t\twindow.close();\n')
      newdoc.write('\t}, 50);\n')
      newdoc.write('};')
      newdoc.write('</script>')
      newdoc.write('</head>')
      newdoc.write('</html>')
      newdoc.close()

      newWindow.onload = function () {
        newdoc.getElementsByTagName('body')[ 0 ].appendChild(mapClone)
      }

      newWindow.focus()
    } else {
      //
      // Variant C: Creating a new body and switching it with the old body for printing
      //

      let $mapViewport = $(this.getMap().getViewport())
      let $mapParent = $mapViewport.parent()
      let $mapContainer = $mapParent.clone().empty().append($mapViewport)
      setDivSize($mapContainer, this.getMap().getSize())
      let $oldbody = $('body')
      let $newbody = $('<body>').append($mapContainer)
      document.body = $newbody.get(0)
      window.print()
      document.body = $oldbody.get(0)
      $mapParent.append($mapViewport)
    }

    this.dispatchEvent('click')
  }
}

import $ from 'jquery'

import {ControlLogicMixin} from './ControlLogicMixin'
import {cssClasses} from '../globals'
import {recursiveSelect} from '../utilities'

/**
 * This class does all generic rewiring including catching the target and restyling the tooltips
 * It is important to provide a className in the control because else it will not be possible to find the control in
 * the ol-viewport
 */
export class RewireMixin extends ControlLogicMixin {
  /**
   * @param {g4uControlOptions} [options={}]
   */
  initialize (options = {}) {
    options.singleButton = false

    super.initialize(options)

    this.rewire()
  }

  /**
   * This function tweaks the control a little bit
   */
  rewire () {
    recursiveSelect(this.get$Element(), 'button[title]')
      .each(function () {
        let title = $(this).attr('title')
        $(this).addClass(cssClasses.hasTooltip).removeAttr('title').append(`<span role='tooltip'>${title}</span>`)
      })
  }
}

