import $ from 'jquery'

import {ComposedControl} from './ComposedControl'
import { cssClasses } from '../globals'

/**
 * @typedef {ComposedControlOptions} ShiftableComposedControlOptions
 * @property {number} visibleControls specifies amount visible controls
 */

/**
 * This class displays only a given amount of the containing controls and arrows to switch through these
 */
export class ShiftableComposedControl extends ComposedControl {
  /**
   * @param {ShiftableComposedControlOptions} options
   */
  constructor (options = {}) {
    super(options)

    /**
     * @type {number}
     * @private
     */
    this.visibleControls_ = options.visibleControls

    /**
     * @type {number}
     * @private
     */
    this.position_ = 1

    let $shiftContainer = $('<div>')
      .addClass(this.className_ + '-shift')
    this.get$Element()
      .append($shiftContainer.append(this.get$Container()))

    /**
     * @type {jQuery}
     * @private
     */
    this.$moveLeftButton_ = $('<button>')
      .addClass(this.className_ + '-move-left')
      .addClass(this.classNameItem_)
      .addClass(this.classNameItemFirst_)
      .addClass(cssClasses.hidden)
    this.$moveLeftButton_.on('click', () => this.shiftControls(-(this.visibleControls_ - 2)))

    $shiftContainer.prepend(this.$moveLeftButton_)
    this.$moveLeftButton_.addClass(cssClasses.hidden)

    /**
     * @type {jQuery}
     * @private
     */
    this.$moveRightButton_ = $('<button>')
      .addClass(`${this.className_}-move-right`)
      .addClass(this.classNameItem_)
      .addClass(this.classNameItemLast_)
    this.$moveRightButton_.on('click', () => this.shiftControls(this.visibleControls_ - 2))

    $shiftContainer.append(this.$moveRightButton_)
    this.$moveRightButton_.addClass(cssClasses.hidden)
  }

  /**
   * @param {Control} control
   * @param {g4uControlOptions} options
   */
  addControl (control, options) {
    super.addControl(control, options)

    if (this.controls_.length > this.visibleControls_) {
      let $lastInsertedControl = this.get$Container().children(':last-child')
      $lastInsertedControl.addClass(cssClasses.hidden)
      $(this.get$Container().children()[this.visibleControls_ - 1]).addClass(cssClasses.hidden)
      this.$moveRightButton_.removeClass(cssClasses.hidden)
    }
  }

  /**
   * Shift the controls the given amount of steps to the left. if the value is negative, the controls are shifted to
   * the right
   * @param shift
   */
  shiftControls (shift) {
    if (shift < 0) {
      if (this.position_ > 1) {
        // shift 'left'

        // show move right button
        if (this.$moveRightButton_.hasClass(cssClasses.hidden)) {
          this.get$Container().children(':last-child').addClass(cssClasses.hidden)
          this.$moveRightButton_.removeClass(cssClasses.hidden)
        }

        // shift visible controls
        this.position_--
        $(this.get$Container().children()[this.position_]).removeClass(cssClasses.hidden)
        $(this.get$Container().children()[this.position_ + this.visibleControls_ - 2]).addClass(cssClasses.hidden)

        // show first button instead of move left
        if (this.position_ === 1) {
          this.get$Container().children(':first-child').removeClass(cssClasses.hidden)
          this.$moveLeftButton_.addClass(cssClasses.hidden)
        }

        // recurse
        this.shiftControls(shift + 1)
      }
    } else if (shift > 0) {
      if (this.position_ + this.visibleControls_ < this.controls_.length + 1) {
        // shift 'right'

        // show move left button
        if (this.$moveLeftButton_.hasClass(cssClasses.hidden)) {
          this.get$Container().children(':first-child').addClass(cssClasses.hidden)
          this.$moveLeftButton_.removeClass(cssClasses.hidden)
        }

        // shift visible controls
        this.position_++
        $(this.get$Container().children()[this.position_ - 1]).addClass(cssClasses.hidden)
        $(this.get$Container().children()[this.position_ + this.visibleControls_ - 3]).removeClass(cssClasses.hidden)

        // show last button instead of move right
        if (this.position_ + this.visibleControls_ >= this.controls_.length) {
          this.get$Container().children(':last-child').removeClass(cssClasses.hidden)
          this.$moveRightButton_.addClass(cssClasses.hidden)
        }

        // recurse
        this.shiftControls(shift - 1)
      }
    }
  }
}

import ol from 'openlayers'
import $ from 'jquery'

import {Control} from './Control'
import { cssClasses, keyCodes } from '../globals'
import {VectorLayer} from '../layers/VectorLayer'

import '../../less/singledrawbutton.less'
import { mixin } from '../utilities'
import { Dropdown } from '../html/Dropdown'
import { ActivatableMixin } from './ActivatableMixin'

/**
 * @typedef {g4uControlOptions} DrawButtonsOptions
 * @property {StyleLike} [editStyle='#defaultStyle']
 * @property {StyleLike} [finishedStyle='#defaultStyle']
 */

/**
 * Enables the user to draw, edit and erase geometries on the map
 */
export class SingleDrawButton extends mixin(Control, ActivatableMixin) {
  /**
   * @param {DrawButtonsOptions} options
   */
  constructor (options = {}) {
    options.element = $('<div>').get(0)
    options.className = options.className || 'g4u-single-draw'
    options.singleButton = false

    super(options)

    /**
     * @type {StyleLike}
     * @private
     */
    this.editStyle_ = options.editStyle || '#defaultStyle'

    /**
     * @type {StyleLike}
     * @private
     */
    this.finishedStyle_ = options.finishedStyle || '#defaultStyle'

    this.setTitle(this.getTitle() ||
      this.getLocaliser().localiseUsingDictionary('SingleDrawButton title')
    )
    this.setTipLabel(this.getTipLabel() ||
      this.getLocaliser().localiseUsingDictionary('SingleDrawButton tipLabel')
    )

    this.createHTML()
  }

  createHTML () {
    const $button = $('<button>')
      .addClass(this.getClassName() + '-button')
      .addClass(cssClasses.mainButton)
      .text(this.getTitle())
      .on('click', () => {
        if (this.getActive()) {
          this.setActive(false)
        } else {
          this.dropdown_.slideDown()
        }
      })

    this.get$Element().append($button)

    this.dropdown_ = new Dropdown()

    this.dropdown_.setEntries([
      'Point',
      'LineString',
      'Polygon',
      'Circle',
      'Delete'
    ], [
      this.getLocaliser().localiseUsingDictionary('SingleDrawButton draw point'),
      this.getLocaliser().localiseUsingDictionary('SingleDrawButton draw line'),
      this.getLocaliser().localiseUsingDictionary('SingleDrawButton draw polygon'),
      this.getLocaliser().localiseUsingDictionary('SingleDrawButton draw circle'),
      this.getLocaliser().localiseUsingDictionary('SingleDrawButton delete all')
    ])

    this.dropdown_.on('select', () => {
      this.setActive(true)
    })

    this.get$Element().append(this.dropdown_.get$Element())
  }

  setActive (active) {
    if (active) {
      const mode = this.dropdown_.getValue()
      if (mode === 'Delete') {
        this.layer_.getSource().clear()
      } else {
        this.addInteraction(mode)
      }
      this.dropdown_.slideUp()
    } else if (this.interaction_) {
      this.removeInteraction()
    }

    super.setActive(active)

    this.get$Element().toggleClass(cssClasses.active, active)
  }

  addInteraction (mode) {
    const map = this.getMap()

    this.interaction_ = new ol.interaction.Draw({
      source: this.layer_.getSource(),
      type: mode,
      style: map.get('styling').getStyle(this.editStyle_)
    })

    $(map.getViewport()).addClass(cssClasses.crosshair)

    map.addSupersedingInteraction('singleclick dblclick pointermove', this.interaction_)

    this.interaction_.on('drawend', () => {
      this.setActive(false)
    })
  }

  removeInteraction () {
    const map = this.getMap()
    this.interaction_.setActive(false)
    map.removeInteraction(this.interaction_)
    this.interaction_ = undefined
    $(map.getViewport()).removeClass(cssClasses.crosshair)
  }

  /**
   * @param {G4UMap} map
   */
  setMap (map) {
    if (this.getMap()) {
      this.getMap().getLayers().remove(this.layer_)
      if (this.interaction_) {
        this.removeInteraction()
      }
    }

    super.setMap(map)

    if (map) {
      /**
       * @type {VectorLayer}
       * @private
       */
      this.layer_ = new VectorLayer({
        source: new ol.source.Vector(),
        visible: true
      })

      this.layer_.setStyle(map.get('styling').getStyle(this.finishedStyle_))
      map.get('styling').manageLayer(this.layer_)

      map.getLayers().insertAt(1, this.layer_) // at 0 the baselayers are

      $(map.getViewport()).parent().on('keydown', e => {
        if (e.which === keyCodes.ESCAPE && this.interaction_) {
          this.removeInteraction()
        }
      })
    }
  }

  getLayer () {
    return this.layer_
  }
}

import $ from 'jquery'

import {Control} from './Control'
import {Window} from '../html/Window'
import { addTooltip } from '../html/html'
import {cssClasses} from '../globals'

import '../../less/windowdecorator.less'

/**
 * @typedef {g4uControlOptions} WindowDecoratorOptions
 * @property {Control} component
 */

/**
 * This class provides an easy wrap around an control to provide a button to open the control inside of a window.
 * If the child control has a setActive method it is called upon opening and closing the window.
 */
export class WindowDecorator extends Control {
  /**
   * @param {WindowDecoratorOptions} options
   */
  constructor (options = {}) {
    options.element = $('<div>').addClass('g4u-window-decorator').get(0)
    options.singleButton = false
    options.className = options.component.getClassName()
    options.windowed = true

    super(options)

    /**
     * @type {jQuery}
     * @private
     */
    this.$button_ = $('<button>')
      .addClass(cssClasses.mainButton)

    this.get$Element()
      .append(this.$button_)

    /**
     * @type {Control}
     * @private
     */
    this.component_ = options.component
    this.component_.get$Element().addClass('g4u-window-component')

    this.$button_
      .addClass(cssClasses.hasTooltip)
      .html(this.component_.getTitle())
    addTooltip(this.$button_, this.component_.getTipLabel())
  }

  /**
   * @returns {Control}
   */
  getComponent () {
    return this.component_
  }

  /**
   * @param {G4UMap} map
   */
  setMap (map) {
    super.setMap(map)

    if (this.getMap()) {
      this.getMap().removeControl(this.component_)
    }

    if (map) {
      map.addControl(this.component_)

      /**
       * @type {Window}
       * @private
       */
      this.window_ = new Window({ map: map })

      this.component_.on('change', () => {
        this.window_.updateSize()
        this.window_.getInFront()
      })

      this.window_.get$Body().append(this.component_.get$Element())

      if (this.component_.setActive) {
        if (this.component_.getActive()) {
          setTimeout(() => this.setWindowVisible(true), 0)
          this.$button_.addClass(cssClasses.active)
        }

        this.$button_.on('click touch', () => {
          this.component_.setActive(!this.component_.getActive())
        })

        this.component_.on('change:active', () => {
          let active = this.component_.getActive()
          setTimeout(() => this.setWindowVisible(active), 0)
          this.$button_.toggleClass(cssClasses.active, active)
        })

        this.window_.on('change:visible', e => {
          if (!this.window_.getVisible()) {
            this.component_.setActive(false)
          }
          this.dispatchEvent({
            type: 'change:windowVisible',
            oldValue: e.oldValue
          })
        })
      } else {
        this.$button_.on('click touch', () => {
          this.setWindowVisible(!this.window_.getVisible())
        })

        this.window_.on('change:visible', e => {
          this.dispatchEvent({
            type: 'change:windowVisible',
            oldValue: e.oldValue
          })
        })
      }
      this.get$Element().append(this.window_.get$Element())
    }
  }

  setActive (active) {
    if (this.component_.setActive) {
      this.component_.setActive(active)
    } else {
      this.window_.setVisible(active)
    }
  }

  /**
   * @param {boolean} visible
   */
  setWindowVisible (visible) {
    this.window_.setVisible(visible)
  }

  /**
   * @returns {boolean}
   */
  getWindowVisible () {
    return this.window_.getVisible()
  }

  /**
   * @returns {Float}
   */
  getFloat () {
    return this.component_.getFloat()
  }
}

/**
 * This class provides different debug behaviour based on the SWITCH_DEBUG build flag
 */
export class Debug {
  /**
   * if in release mode all thrown errors in the callback are catched and the program is continued
   * @param {function} cb
   */
  static tryOrThrow (cb) {
    if (SWITCH_DEBUG === 'DEBUG') { // eslint-disable-line
      cb()
    } else if (SWITCH_DEBUG === 'PRODUCTION') { // eslint-disable-line
      try {
        cb()
      } catch (e) {
        this.error(e)
      }
    }
  }

  /**
   * Prints an info note if in debug mode
   * @param {string} msg
   */
  static info (msg) {
    if (SWITCH_DEBUG === 'DEBUG') { // eslint-disable-line
      console.log(msg) // eslint-disable-line
    }
  }

  /**
   * prints a warning
   * @param {string} msg
   */
  static warn (msg) {
    console.warn(msg) // eslint-disable-line
  }

  /**
   * prints an error
   * @param {string} msg
   */
  static error (msg) {
    console.error(msg) // eslint-disable-line
  }

  /**
   * a default error handler for .catch functions
   * @param {string} message
   */
  static defaultErrorHandler (message) {
    Debug.error(message)
    if (message.stack) {
      Debug.error(message.stack)
    }
  }
}

