import ol from 'openlayers'

/**
 * returns an extent with the same center but width and height miltiplied by the given value
 * @param {ol.Extent} extent
 * @param {number} value
 * @returns {ol.Extent}
 */
export function multiplyExtent (extent, value) {
  let size = ol.extent.getSize(extent)
  let addX = size[0] * (value - 1) / 2
  let addY = size[1] * (value - 1) / 2
  return [extent[0] - addX, extent[1] - addY, extent[2] + addX, extent[3] + addY]
}

import ol from 'openlayers'
import $ from 'jquery'
import flatten from 'lodash/flatten'

import {ListenerOrganizerMixin} from './ListenerOrganizerMixin'
import {Window} from './html/Window'
import {cssClasses} from './globals'
import { finishAllImages, mixin } from './utilities'

import '../less/featurepopup.less'

/**
 * @typedef {object} FeaturePopupOptions
 * @property {string} [className='g4u-featurepopup']
 * @property {number[]} [offset=[0,0]]
 * @property {ol.OverlayPositioning} [positioning='center-center']
 * @property {number[]} [iconSizedOffset=[0,0]]
 * @property {boolean} [centerOnPopup=false]
 * @property {boolean} [animated=true]
 * @property {string[]} [popupModifier] default popupModifiers to use
 */

/**
 * Displays a Popup bound to a geographical position via an ol.Overlay
 */
export class FeaturePopup extends mixin(ol.Object, ListenerOrganizerMixin) {
  /**
   * @param {FeaturePopupOptions} options
   */
  constructor (options = {}) {
    super()

    /**
     * @type {string}
     * @private
     */
    this.className_ = (options.hasOwnProperty('className')) ? options.className : 'g4u-featurepopup'

    /**
     * @type {string}
     * @private
     */
    this.classNameFeatureName_ = this.className_ + '-feature-name'

    /**
     * @type {string}
     * @private
     */
    this.classNameFeatureDescription_ = this.className_ + '-feature-description'

    /**
     * @type {jQuery}
     * @private
     */
    this.$name_ = $('<h3>').addClass(this.classNameFeatureName_)

    /**
     * @type {jQuery}
     * @private
     */
    this.$description_ = $('<p>').addClass(this.classNameFeatureDescription_)

    /**
     * @type {null|ol.Feature}
     * @private
     */
    this.feature_ = null

    /**
     * @type {boolean}
     * @private
     */
    this.visible_ = false

    /**
     * @type {VectorLayer[]}
     * @private
     */
    this.referencingVisibleLayers_ = []

    /**
     * @type {number[]}
     * @private
     */
    this.pixelOffset_ = options.hasOwnProperty('offset') ? options.offset : [ 0, 0 ]

    /**
     * @type {number[]}
     * @private
     */
    this.iconSizedOffset_ = options.hasOwnProperty('iconSizedOffset') ? options.iconSizedOffset : [ 0, 0 ]

    /**
     * @type {boolean}
     * @private
     */
    this.centerOnPopup_ = options.hasOwnProperty('centerOnPopup') ? options.centerOnPopup : true

    /**
     * @type {boolean}
     * @private
     */
    this.centerOnPopupInitial_ = this.centerOnPopup_

    /**
     * @type {boolean}
     * @private
     */
    this.animated_ = options.hasOwnProperty('animated') ? options.animated : true

    /**
     * @type {jQuery}
     * @private
     */
    this.$element_ = $('<div>').addClass(this.className_).addClass(cssClasses.hidden)

    /**
     * @type {ol.Overlay}
     * @private
     */
    this.overlay_ = new ol.Overlay({
      element: this.$element_.get(0),
      offset: this.pixelOffset_,
      positioning: options.hasOwnProperty('positioning') ? options.positioning : 'center-center',
      stopEvent: true
    })

    /**
     * @type {string[]}
     * @private
     */
    this.defaultPopupModifiers_ = options.popupModifier || []

    /**
     * @type {string[]}
     * @private
     */
    this.currentPopupModifiers_ = []

    /**
     * @type {?Window}
     * @private
     */
    this.window_ = null

    /**
     * @type {?G4UMap}
     * @private
     */
    this.map__ = null
  }

  /**
   * @param {ol.Feature} feature
   * @returns {boolean}
   */
  static canDisplay (feature) {
    if (feature.get('features') && feature.get('features').length === 1) {
      feature = feature.get('features')[0]
    }
    return !feature.get('disabled') &&
      (feature.get('name') || (feature.get('description') && $(feature.get('description')).text().match(/\S/)))
  }

  /**
   * @param {G4UMap} map
   */
  setMap (map) {
    if (this.getMap()) {
      this.detachAllListeners()
      this.getMap().removeOverlay(this.overlay_)
    }

    if (map) {
      this.window_ = new Window({ draggable: false, fixedPosition: true, map: map })

      this.window_.get$Body().append(this.$name_).append(this.$description_)

      this.listenAt(this.window_).on('change:visible', () => {
        if (!this.window_.getVisible()) {
          this.setVisible(false) // notifying the featurepopup about the closing of the window
        }
      })

      this.$element_.append(this.window_.get$Element())

      // feature click

      this.listenAt(map.getDefaultInteractions('singleclick')[ 0 ]).on('select', e => {
        let selected = e.selected.filter(FeaturePopup.canDisplay)
        if (selected.length) {
          this.onFeatureClick_(selected[ 0 ], e.mapBrowserEvent.coordinate)
          e.target.getFeatures().remove(selected[ 0 ]) // remove feature to be able to select feature again
          e.target.changed()
        }
      })

      // feature hover

      this.listenAt(map.getDefaultInteractions('pointermove')[ 0 ]).on('select', e => {
        let selected = e.selected.filter(FeaturePopup.canDisplay)
        let deselected = e.deselected.filter(FeaturePopup.canDisplay)
        if (selected.length) {
          $(map.getViewport()).addClass(cssClasses.clickable)
        } else if (deselected.length) {
          $(map.getViewport()).removeClass(cssClasses.clickable)
        }
      })

      // hiding feature Popup if the layer gets hidden or the feature gets removed

      let forEachSource = (layer, source) => {
        if (source.getFeatures) {
          this.listenAt(source).on('removefeature', e => {
            if (e.feature === this.getFeature()) {
              this.referencingVisibleLayers_.forEach(rLay => {
                if (rLay.getSource() === source) {
                  this.removeReferencingLayer_(rLay)
                }
              })
            }
          })
        }
      }

      let forEachLayer = layer => {
        if (layer.getSource) {
          let source = layer.getSource()

          if (source) {
            forEachSource(layer, source)
          }

          this.listenAt(layer).on('change:source', e => {
            this.detachFrom(e.oldValue)
            forEachSource(layer, layer.getSource())
          })

          this.listenAt(layer).on('change:visible', () => {
            let source = layer.getSource()
            if (source && source.getFeatures) {
              if (layer.getVisible() === false) {
                if (source.getFeatures().indexOf(this.getFeature()) > -1) {
                  this.removeReferencingLayer_(layer)
                }
              } else {
                if (source.getFeatures().indexOf(this.getFeature()) > -1) {
                  this.referencingVisibleLayers_.push(layer)
                }
              }
            }
          })
        }

        if (layer.getLayers) {
          layer.getLayers().forEach(forEachLayer)
          this.listenAt(layer.getLayers())
            .on('add', e => {
              forEachLayer(e.element)
            })
            .on('remove', e => {
              if (e.element.getSource && e.element.getSource()) {
                this.detachFrom(e.element.getSource())
              }
              this.detachFrom(e.element)
            })
        }
      }

      forEachLayer(map.getLayerGroup())

      map.addOverlay(this.overlay_)

      this.$element_.parent().addClass(this.className_ + '-container')

      let onMapChangeMobile = () => {
        if (map.get('mobile')) {
          this.centerOnPopup_ = false
        } else {
          this.centerOnPopup_ = this.centerOnPopupInitial_
        }
      }

      onMapChangeMobile()
      this.listenAt(map).on('change:mobile', onMapChangeMobile)

      // limiting size

      map.once('postrender', () => {
        this.window_.updateSize()
      })
    }

    this.map__ = map
  }

  /**
   * @param {ol.Feature} feature
   * @param {ol.Coordinate} coordinate
   * @private
   */
  onFeatureClick_ (feature, coordinate = null) {
    if (feature.get('features')) {
      feature = feature.get('features')[0]
    }

    this.referencingVisibleLayers_ = []

    this.getMap().getLayerGroup().recursiveForEach(layer => {
      if (layer.getVisible() && layer.getSource && layer.getSource().getFeatures) {
        if (layer.getSource().getFeatures().indexOf(feature) > -1) {
          this.referencingVisibleLayers_.push(layer)
        }
      }
    })

    this.setFeature(feature, coordinate)
    this.setVisible(true)

    if (this.centerOnPopup_) {
      this.centerMapOnPopup()
    }
  }

  /**
   * @param {VectorLayer} layer
   * @private
   */
  removeReferencingLayer_ (layer) {
    this.referencingVisibleLayers_.splice(this.referencingVisibleLayers_.indexOf(layer), 1)
    if (this.referencingVisibleLayers_.length === 0) {
      this.setVisible(false)
    }
  }

  /**
   * @returns {G4UMap}
   */
  getMap () {
    return this.map__
  }

  /**
   * @returns {null|ol.Feature}
   */
  getFeature () {
    return this.feature_
  }

  /**
   * @returns {Array|VectorLayer[]}
   */
  getLayers () {
    return this.referencingVisibleLayers_
  }

  updateContent () {
    if (this.getMap().get('localiser').isRtl()) {
      this.window_.get$Body().prop('dir', 'rtl')
    } else {
      this.window_.get$Body().prop('dir', undefined)
    }

    this.getMap().get('popupModifiers').apply({
      name: this.getFeature().get('name'),
      description: this.getFeature().get('description')
    }, this.currentPopupModifiers_)
      .then(result => {
        if (result.name) {
          this.$name_.removeClass(cssClasses.hidden)
          this.$name_.html(result.name)
        } else {
          this.$name_.addClass(cssClasses.hidden)
        }

        if (result.description) {
          this.$description_.removeClass(cssClasses.hidden)
          this.$description_.html(result.description)
        } else {
          this.$description_.addClass(cssClasses.hidden)
        }

        this.updateSize()

        this.dispatchEvent('update:content')
      })
  }

  /**
   * Update the Popup. Call this if something in the feature has changed
   */
  update () {
    if (this.getFeature()) {
      const feature = this.getFeature()

      this.$name_.empty()
      this.$description_.empty()

      this.updateContent() // this produces one unnecessary call to window.updateSize()

      if (!feature.get('observedByPopup')) {
        feature.on('change:name', () => this.updateContent())
        feature.on('change:description', () => this.updateContent())
        feature.set('observedByPopup', true)
      }

      this.once('change:feature', () => {
        feature.un('change:name', () => this.updateContent())
        feature.un('change:description', () => this.updateContent())
        feature.set('observedByPopup', false)
      })

      if (!this.getMap().get('mobile')) {
        let resolution = this.getMap().getView().getResolution()

        this.addIconSizedOffset(this.getFeature(), resolution)
      }

      if (this.referencingVisibleLayers_.length) {
        this.window_.get$Element().attr('data-layer-0', this.referencingVisibleLayers_[0].get('id'))
      }

      // apply default offset

      if (this.getVisible()) {
        setTimeout(() => this.window_.updateSize(), 0)
      }
    }
  }

  updateSize () {
    if (this.getVisible() && this.window_ && this.window_.updateSize) {
      this.window_.updateSize()
    }
  }

  /**
   * The feature should have a property 'name' and/or 'description' to be shown inside of the popup.
   * @param {ol.Feature} feature
   * @param {ol.Coordinate} coordinate
   * @param {string[]} [optPopupModifiers=[]]
   */
  setFeature (feature, coordinate = null, optPopupModifiers = []) {
    let oldValue = this.feature_
    if (feature) {
      this.overlay_.setPosition(coordinate || ol.extent.getCenter(feature.getGeometry().getExtent()))
    }

    if (oldValue !== feature) {
      if (this.feature_) {
        this.feature_.un('change:geometry', this.geometryChangeHandler_)
      }
      this.feature_ = feature
      this.currentPopupModifiers_ = [ ...this.defaultPopupModifiers_, ...optPopupModifiers ]
      for (let layer of this.referencingVisibleLayers_) {
        this.currentPopupModifiers_ = this.currentPopupModifiers_.concat(flatten(layer.get('popupModifiers')))
      }
      this.geometryChangeHandler_ = () => {
        this.overlay_.setPosition(coordinate || ol.extent.getCenter(this.feature_.getGeometry().getExtent()))
        if (this.getVisible()) {
          this.update()
        }
      }
      this.feature_.on('change:geometry', this.geometryChangeHandler_)
      this.dispatchEvent({
        type: 'change:feature',
        oldValue: oldValue,
        key: 'feature'
      })

      this.update()
    }
  }

  /**
   * @returns {boolean}
   */
  getVisible () {
    return this.visible_
  }

  /**
   * @param {boolean} visible
   */
  setVisible (visible) {
    let oldValue = this.visible_
    if (oldValue !== visible) {
      if (visible === true && this.getFeature()) {
        this.$element_.removeClass(cssClasses.hidden)
        this.window_.setVisible(true)
      } else {
        this.$element_.addClass(cssClasses.hidden)
        this.window_.setVisible(false)
      }

      this.visible_ = visible

      this.dispatchEvent({
        type: 'change:visible',
        oldValue: oldValue,
        key: 'visible'
      })
    }

    if (visible) {
      setTimeout(() => this.window_.updateSize(), 0)
    }
  }

  /**
   * calculates iconSized Offset and applies it
   * @param {ol.Feature} feature
   * @param {number} resolution
   */

  addIconSizedOffset (feature, resolution) {
    if (this.iconSizedOffset_[ 0 ] !== 0 || this.iconSizedOffset_[ 1 ] !== 0) {
      let featureStyleFunction = feature.getStyleFunction()
      if (featureStyleFunction) {
        let style = featureStyleFunction.call(feature, resolution)[ 0 ]
        if (style) {
          let imageStyle = style.getImage()
          if (imageStyle instanceof ol.style.Icon) {
            (new Promise(resolve => {
              let img = imageStyle.getImage()
              if (img.complete && img.src) {
                resolve()
              } else {
                img.addEventListener('load', () => {
                  this.getMap().render() // initiate styles with size and anchor
                  this.getMap().once('postcompose', resolve)
                })
                imageStyle.load()
              }
            })).then(() => {
              let iconSize = imageStyle.getSize()

              let totalOffset = [
                this.pixelOffset_[ 0 ] + this.iconSizedOffset_[ 0 ] * iconSize[ 0 ] * (imageStyle.getScale() || 1),
                this.pixelOffset_[ 1 ] + this.iconSizedOffset_[ 1 ] * iconSize[ 1 ] * (imageStyle.getScale() || 1)
              ]

              this.overlay_.setOffset(totalOffset)
            })
          }
        }
      }
    }
  }

  /**
   * Centers the map on the popup after all images have been loaded
   */
  centerMapOnPopup (animated) {
    animated = animated === undefined ? this.animated_ : animated

    const _centerMap = () => {
      this.window_.updateSize()
      this.getMap().get('move').toPoint(this.getCenter(), { animated })
    }

    finishAllImages(this.window_.get$Body()).then(() => {
      // we need to do this trick to find out if map is already visible/started rendering
      if (this.getMap().getPixelFromCoordinate([ 0, 0 ])) {
        _centerMap()
      } else {
        this.getMap().once('postrender', _centerMap)
      }
    })
  }

  /**
   * calculates Center of the Popup. Be careful, this calculation repositions the popup to calculate the center
   * properly and repostions to the initial Position again.
   * This does only work if the popup is already visible!
   * @returns {ol.Coordinate}
   */
  getCenter () {
    let offset = this.overlay_.getOffset()

    let pixelPosition = this.getMap().getPixelFromCoordinate(this.overlay_.getPosition())

    // apply offset
    pixelPosition[ 0 ] += offset[ 0 ]
    pixelPosition[ 1 ] += offset[ 1 ]

    // applay width/height depending on positioning
    let positioning = this.overlay_.getPositioning().split('-')

    let width = this.$element_.outerWidth()
    let height = this.$element_.outerHeight()

    if (positioning[ 1 ] === 'left') {
      pixelPosition[ 0 ] += width / 2
    }
    if (positioning[ 1 ] === 'right') {
      pixelPosition[ 0 ] -= width / 2
    }

    if (positioning[ 0 ] === 'top') {
      pixelPosition[ 1 ] += height / 2
    }
    if (positioning[ 0 ] === 'bottom') {
      pixelPosition[ 1 ] -= height / 2
    }

    return this.getMap().getCoordinateFromPixel(pixelPosition)
  }
}

import ol from 'openlayers'
import $ from 'jquery'

import { cssClasses } from './globals'

import '../less/tooltip.less'
import {html2Text} from './utilities'

/**
 * @typedef {object} FeatureTooltipOptions
 * @property {string} [className='g4u-featuretooltip']
 * @property {number[]} [offset=[0,0]]
 * @property {ol.OverlayPositioning} [positioning='center-center']
 * @property {string[]} [popupModifier=[]]
 */

/**
 * Displays a tooltip if a feature with a name is hovered.
 */
export class FeatureTooltip {
  /**
   * @param {FeatureTooltipOptions} [options={}]
   */
  constructor (options = {}) {
    /**
     * @type {string}
     * @private
     */
    this.className_ = (options.hasOwnProperty('className')) ? options.className : 'g4u-featuretooltip'

    /**
     * @type {jQuery}
     * @private
     */
    this.$element_ = $('<span></span>').addClass(this.className_).addClass(cssClasses.hidden)

    /**
     * @type {ol.Overlay}
     * @private
     */
    this.overlay_ = new ol.Overlay({
      element: this.$element_.get(0),
      offset: (options.hasOwnProperty('offset')) ? options.offset : [0, 0],
      positioning: (options.hasOwnProperty('positioning')) ? options.positioning : 'center-center',
      stopEvent: false
    })

    /**
     * @type {string[]}
     * @private
     */
    this.defaultPopupModifiers_ = options.popupModifier || []

    /**
     * @type {?ol.Feature}
     * @private
     */
    this.feature_ = null

    this.$element_.parent().addClass(this.className_ + '-container')
  }

  static canDisplay (feature) {
    if (feature.get('features') && feature.get('features').length === 1) {
      feature = feature.get('features')[0]
    }
    return !feature.get('disabled') && feature.get('name')
  }

  /**
   * @param {G4UMap} map
   */
  setMap (map) {
    if (this.getMap()) {
      this.getMap().removeOverlay(this.overlay_)
    }

    if (map) {
      map.addOverlay(this.overlay_)

      let interaction = map.getDefaultInteractions('pointermove')[0]
      interaction.on('select', e => {
        let selected = e.selected.filter(FeatureTooltip.canDisplay)
        if (selected.length) {
          let feature = selected[0]
          if (feature.get('features')) {
            feature = feature.get('features')[0]
          }
          this.setFeature(feature, e.mapBrowserEvent.coordinate)
        } else if (e.deselected.length && e.deselected.some(f => f === this.getFeature())) {
          this.setFeature(null)
        }
      })

      /**
       * @type {?G4UMap}
       * @private
       */
      this.map_ = map
    }
  }

  /**
   * @returns {?ol.Feature}
   */
  getFeature () {
    return this.feature_
  }

  /**
   * @param {?ol.Feature} feature
   * @param {ol.Coordinate} coordinate
   * @param {string[]} [optPopupModifiers=[]]
   */
  setFeature (feature, coordinate = null, optPopupModifiers = []) {
    if (feature) {
      let currentPopupModifiers = [ ...this.defaultPopupModifiers_, ...optPopupModifiers ]
      this.getMap().get('popupModifiers').apply({
        name: feature.get('name'),
        description: feature.get('description')
      }, currentPopupModifiers)
        .then(result => {
          if (result.name) {
            this.$element_.html(html2Text(result.name))
            this.$element_.removeClass(cssClasses.hidden)
          }
        })
      if (!coordinate) {
        let geometry = feature.getGeometry()
        coordinate = ol.extent.getCenter(geometry.getExtent())
      }
      this.overlay_.setPosition(coordinate)
    } else {
      this.$element_.addClass(cssClasses.hidden)
    }
    this.feature_ = feature
  }

  /**
   * @returns {G4UMap}
   */
  getMap () {
    return this.map_
  }
}

import ol from 'openlayers'
import $ from 'jquery'

import stripJsonComments from 'strip-json-comments'

import {MapConfigurator} from './configurators/MapConfigurator'
import './openlayersInjections'
import {L10N} from './L10N'

import {mergeWithDefaults} from './utilitiesObject'
import {cssClasses} from './globals'

import {defaults} from './defaultconfig'

import {Debug} from './Debug'

import '../less/map.less'
import { getRegisteredModules } from './moduleRegistration'
import { PopupModifierManager } from './PopupModifierManager'

/**
 * @typedef {object} G4UMapOptions
 * @property {L10N} [localiser]
 * @property {object.<string, PopupModifier>} [popupModifiers]
 */

/**
 * Definition of the map-object
 * Main task of the constructor is to load and read out the configuration.
 * Uses the functions makeMapLayers, makeMapUI to create the map.
 *
 * Custom properties accessible via method .get('propertyName')
 *
 * @fires 'resize'
 * @fires 'userActionTracking'
 * @fires 'beforeConfigLoad'
 * @fires 'afterConfigLoad'
 * @fires 'afterConfiguring'
 * @fires 'change:ready'
 * @fires 'change:ready:ui'
 * @fires 'change:ready:layers'
 */
export class G4UMap extends ol.Map {
  /**
   * @param {HTMLElement|jQuery|string} target element or id of an element
   * @param {MapConfig|string} configOrFileName
   * @param {LayerConfig|string} layerConfigOrFileName
   * @param {G4UMapOptions} [options={}]
   */
  constructor (target, configOrFileName, layerConfigOrFileName, options = {}) {
    let config = {}
    let layerConfig = {}

    // //////////////////////////////////////////////////////////////////////////////////////// //
    //                     Call of the Parents Class Constructor                                //
    // //////////////////////////////////////////////////////////////////////////////////////// //

    super({
      controls: [],
      interactions: [],
      view: null
    })

    this.set('guide4youVersion', GUIDE4YOU_VERSION) // eslint-disable-line

    this.set('options', options)

    /**
     * @type {Map.<string, ol.interaction.Interaction[]>}
     * @private
     */
    this.defaultInteractions_ = new Map()

    /**
     * @type {Map.<string, ol.interaction.Interaction[]>}
     * @private
     */
    this.supersedingInteractions_ = new Map()

    /**
     * @type {Module[]}
     * @private
     */
    this.modules_ = []

    this.addModules(getRegisteredModules())

    this.set('ready', false)

    this.on([ 'change:ready', 'change:ready:ui', 'change:ready:layers' ], /** ol.ObjectEvent */ e => {
      if (this.get(e.key)) {
        this.dispatchEvent(e.key)
      }
    })

    // popupModifiers

    let popupModifiers = new PopupModifierManager()
    this.set('popupModifiers', popupModifiers)

    if (options.popupModifiers) {
      this.on('change:featurePopup', () => {
        for (let name of Object.keys(options.popupModifiers)) {
          let popupModifier = options.popupModifiers[name]
          popupModifier.setMap(this)
          popupModifiers.register(name, popupModifier)
        }
      })
    }

    // Setting the target of the map

    if (typeof target === 'string' && target[ 0 ] !== '#') {
      this.setTarget($('#' + target).get(0))
    } else {
      this.setTarget($(target).get(0))
    }

    // set the display mode to desktop initially to render overviewmpa correctly
    $(this.getTarget()).children().addClass(cssClasses.desktop)

    // check type of mapConfig
    if (typeof configOrFileName === 'object') {
      config = configOrFileName
    } else if (typeof configOrFileName === 'string') {
      this.set('configFileName', configOrFileName)
      this.set('defaultLayerConfigFileName', layerConfigOrFileName)
    } else {
      throw new Error('Unrecognised type for parameter configOrFileName!')
    }

    // check type of layerConfig
    if (typeof layerConfigOrFileName === 'object') {
      layerConfig = layerConfigOrFileName
    } else if (typeof layerConfigOrFileName === 'string') {
      this.set('layerConfigFileName', layerConfigOrFileName)
    } else {
      throw new Error('Unrecognised type for parameter layerConfigOrFileName!')
    }

    // //////////////////////////////////////////////////////////////////////////////////////////
    //                            Load config files if needed                                 //
    // //////////////////////////////////////////////////////////////////////////////////////////

    this.dispatchEvent('beforeConfigLoad')

    this.set('mapConfigReady', false)
    this.set('layerConfigReady', false)

    function loadConfigFile (fileName) {
      return $.ajax({
        url: fileName,
        dataType: 'text'
      }).then((data) => {
        try {
          return JSON.parse(stripJsonComments(data))
        } catch (err) {
          Debug.error(`The config file ${fileName} couldn't be parsed.`)
          Debug.error(err)
        }
      }).fail((err) => {
        Debug.error(`The config file ${fileName} couldn't be loaded.`)
        Debug.error(err)
      })
    }

    let filesToLoad = []

    let configFileName = this.get('configFileName')
    if (configFileName) {
      filesToLoad.push(loadConfigFile(configFileName).then((data) => {
        config = data
      }))
    }

    let layerConfigFileName = this.get('layerConfigFileName')
    if (layerConfigFileName) {
      filesToLoad.push(loadConfigFile(layerConfigFileName).then((data) => {
        layerConfig = data
      }))
    }

    // wait for all promises in filesToLoad to resolve
    Promise.all(filesToLoad).then(() => {
      this.set('mapConfigReady', true)
      this.set('layerConfigReady', true)

      // Merging the custom config with the default config
      config = mergeWithDefaults(config, defaults.config)

      this.set('mapConfig', config)
      this.set('layerConfig', layerConfig)

      let loading = 0

      // issue reload of mapConfig if the name was changed
      this.on('change:configFileName', /** ol.ObjectEvent */ e => {
        this.set('ready', false)
        loading++

        this.oldMapConfigs_ = this.oldMapConfigs_ || {}
        this.oldMapConfigs_[ e.oldValue ] = this.get('mapConfig')

        if (this.oldMapConfigs_.hasOwnProperty(this.get('configFileName'))) {
          this.set('mapConfig', this.oldMapConfigs_[ this.get('configFileName') ])
        } else {
          this.set('mapConfigReady', false)
          loadConfigFile(this.get('configFileName')).then((data) => {
            this.set('mapConfigReady', true)
            this.set('mapConfig', data)
          }).always(() => {
            loading--
            if (loading === 0) {
              this.set('ready', true)
            }
          })
        }
      })

      // issue reload of layerConfig if the name was changed
      this.on('change:layerConfigFileName', /** ol.ObjectEvent */ e => {
        this.set('ready', false)
        loading++

        this.oldLayerConfigs_ = this.oldLayerConfigs_ || {}
        this.oldLayerConfigs_[ e.oldValue ] = this.get('layerConfig')

        if (this.oldLayerConfigs_.hasOwnProperty(this.get('layerConfigFileName'))) {
          this.set('layerConfig', this.oldLayerConfigs_[ this.get('layerConfigFileName') ])
        } else {
          this.set('layerConfigReady', false)
          loadConfigFile(this.get('layerConfigFileName'))
            .then((data) => {
              this.set('layerConfigReady', true)
              this.set('layerConfig', data)
            })
            .always(() => {
              loading--
              if (loading === 0) {
                this.set('ready', true)
              }
            })
        }
      })

      this.set('proxy', config.proxy)

      // //////////////////////////////////////////////////////////////////////////////////////// //
      //                                     Localization                                         //
      // //////////////////////////////////////////////////////////////////////////////////////// //

      let asyncLanguageFilePromise

      if (!options.localiser) {
        let localiserOptions = {}

        if (config.hasOwnProperty('languageSettings')) {
          let l10nconf = config.languageSettings

          localiserOptions.currentLanguage = l10nconf.currentLanguage

          if (l10nconf.hasOwnProperty('defaultLanguage')) {
            localiserOptions.defaultLanguage = l10nconf.defaultLanguage
          }

          if (l10nconf.hasOwnProperty('languageFile')) {
            localiserOptions.languageFile = l10nconf.languageFile
          }

          if (l10nconf.hasOwnProperty('availableLanguages')) {
            localiserOptions.availableLanguages = l10nconf.availableLanguages
          }
        }

        let localiser = new L10N(localiserOptions)
        this.set('localiser', localiser)

        asyncLanguageFilePromise = localiser.ajaxGetLanguageFile()
      } else {
        this.set('localiser', options.localiser)
      }

      this.asSoonAs('ready', true, () => {
        this.get('localiser').on('change:language', () => {
          let visibilities = this.getLayerGroup().getIdsVisibilities()

          this.get('configurator').configureLayers()
          this.get('configurator').configureUI()

          this.getLayerGroup().setIdsVisibilities(visibilities)
        })
      })

      this.dispatchEvent('afterConfigLoad')

      return asyncLanguageFilePromise
    })
      .then(() => {
        // //////////////////////////////////////////////////////////////////////////////////////// //
        //                                    Configurator                                          //
        // //////////////////////////////////////////////////////////////////////////////////////// //

        this.set('configurator', new MapConfigurator(this))

        this.dispatchEvent('afterConfiguring')

        if (this.get('ready:ui') && this.get('ready:layers')) {
          this.set('ready', true)
        }

        this.on([ 'change:ready:ui', 'change:ready:layers' ], /** ol.ObjectEvent */ e => {
          if (!this.get(e.key)) {
            this.set('ready', false)
          }

          if (this.get('ready:ui') && this.get('ready:layers')) {
            this.set('ready', true)
          }
        })
      })
      .catch(Debug.defaultErrorHandler)
  }

  /**
   * Searches all controls of the specified name
   * @param {string} name
   * @returns {Control[]}
   */
  getControlsByName (name) {
    return this.controlsByName[ name ] || []
  }

  /**
   * @param {Module} module
   */
  addModule (module) {
    module.setMap(this)
    this.modules_.push(module)
  }

  /**
   * @param {Module[]} modules
   */
  addModules (modules) {
    for (let module of modules) {
      this.addModule(module)
    }
  }

  /**
   * The listener is called once immediately after the next postrender event
   * @param listener
   */
  afterPostrender (listener) {
    this.once('postrender', () => setTimeout(listener, 0))
  }

  /**
   * @returns {Module[]}
   */
  getModules () {
    return this.modules_
  }

  /**
   * @param {ol.interaction.Interaction} interaction
   */
  removeInteraction (interaction) {
    let index

    this.defaultInteractions_.forEach(interactions => {
      index = interactions.indexOf(interaction)
      if (index > -1) {
        interactions.splice(index, 1)
      }
    })

    this.supersedingInteractions_.forEach(interactions => {
      index = interactions.indexOf(interaction)
      if (index > -1) {
        interactions.splice(index, 1)
      }
    })

    super.removeInteraction(interaction)
  }

  /**
   * Remove all interactions
   */
  removeInteractions () {
    while (this.getInteractions() && this.getInteractions().getLength()) {
      for (let interaction of this.getInteractions().getArray()) {
        this.removeInteraction(interaction)
      }
    }
  }

  /**
   * overwrite base method to notify developer about differing api
   */
  addInteraction () {
    throw new Error('Use addDefaultInteraction or addSupersedingInteraction')
  }

  /**
   * Add an interaction that should be active by default (i.e. in the normal state of the map)
   * @param {string} eventTypes a list of space separated eventtypes this interaction reacts on
   * @param {ol.interaction.Interaction} interaction
   */
  addDefaultInteraction (eventTypes, interaction) {
    for (let eventtype of eventTypes.split(' ')) {
      if (this.defaultInteractions_.has(eventtype)) {
        this.defaultInteractions_.get(eventtype).push(interaction)
      } else {
        this.defaultInteractions_.set(eventtype, [ interaction ])
      }
    }

    super.addInteraction(interaction)
  }

  /**
   * This deactivates all interactions which use a given event type
   * @param {string} eventType
   */
  deactivateInteractions (eventType) {
    for (let defInteraction of this.defaultInteractions_.get(eventType)) {
      defInteraction.setActive(false)
    }
    for (let supInteraction of this.supersedingInteractions_.get(eventType)) {
      supInteraction.setActive(false)
    }
  }

  /**
   * Reactivates all default interactions which use a specified event type
   * @param {string} eventType
   */
  activateInteractions (eventType) {
    for (let defInteraction of this.getDefaultInteractions(eventType)) {
      defInteraction.setActive(true)
    }
  }

  /**
   * Gets all interactions which use the specified event type
   * @param {string} eventType
   * @returns {ol.interaction.Interaction[]}
   */
  getDefaultInteractions (eventType) {
    return this.defaultInteractions_.get(eventType)
  }

  /**
   * This adds an interaction to the map which prohibits other interactions which use the same eventtype to be active
   * at the same time. When the superseding interaction is activated all affected ones get deactivated and vice versa
   * @param {string} eventTypes a list of space separated eventtypes this interaction reacts on
   * @param {ol.interaction.Interaction} interaction
   */
  addSupersedingInteraction (eventTypes, interaction) {
    let eventTypes_ = eventTypes.split(' ')

    let onActivation = () => {
      // deactivation of all other interactions with the same eventtypes

      for (let eventType of eventTypes_) {
        for (let supersedingInteraction of this.supersedingInteractions_.get(eventType)) {
          if (interaction !== supersedingInteraction) {
            supersedingInteraction.setActive(false)
          }
        }

        if (this.defaultInteractions_.get(eventType)) {
          for (let defaultInteraction of this.defaultInteractions_.get(eventType)) {
            defaultInteraction.setActive(false)
          }
        }
      }
    }

    let onDeactivation = () => {
      // reactivation of the default interactions
      // NOTE: if a superseding turned off another superseding interactions it won't reactivate it
      for (let eventType of eventTypes_) {
        if (this.defaultInteractions_.get(eventType)) {
          for (let defaultInteraction of this.defaultInteractions_.get(eventType)) {
            defaultInteraction.setActive(true)
          }
        }
      }
    }

    for (let eventType of eventTypes_) {
      if (this.supersedingInteractions_.has(eventType)) {
        this.supersedingInteractions_.get(eventType).push(interaction)
      } else {
        this.supersedingInteractions_.set(eventType, [ interaction ])
      }
    }

    if (interaction.getActive()) {
      onActivation()
    }

    interaction.on('change:active', /** ol.ObjectEvent */ e => {
      if (e.oldValue !== interaction.getActive()) {
        if (interaction.getActive()) {
          this.activating_ = true
          onActivation()
          this.activating_ = false
        } else {
          if (this.interactionDecativationTimeout_) {
            clearTimeout(this.interactionDecativationTimeout_)
          }

          if (!this.activating_) {
            this.interactionDecativationTimeout_ = setTimeout(onDeactivation, 500)
          }
        }
      }
    })

    super.addInteraction(interaction)
  }

  /**
   * @param {GroupLayer} groupLayer
   * @param {boolean} [silent=false] provide map to layers
   */
  setLayerGroup (groupLayer, silent = false) {
    if (!silent) {
      groupLayer.provideMap(this)
    }
    super.setLayerGroup(groupLayer)
  }

  /**
   * Remove all controls
   */
  removeControls () {
    let controls = this.getControls()

    if (controls) {
      // its neccessary to loop, problem caused by the nesting?
      while (controls.getLength() > 0) {
        controls.forEach(control => this.removeControl(control))
        controls = this.getControls()
      }
    }
  }
}

import $ from 'jquery'
import ol from 'openlayers'

import { cssClasses } from '../globals'

/**
 * @typedef {object} ButtonBoxOptions
 * @property {string} [className='g4u-menu']
 * @property {HTMLElement|jQuery} [content] the content of the body of the button box
 * @property {boolean} [collapsible]
 * @property {boolean} [collapsed]
 * @property {boolean} [rtl]
 * @property {boolean} [titleButton=false] displays an extra button with a title firing an 'title:click' event
 * @property {string} [title] the title appearing on the button
 */

/**
 * An Element to easily construct nested HTML Menus
 * It contains of a button followed by a body below the button. The button can toggle the visibility of the body.
 * If multiple elements with the same classname are nested it gives the last visible element a special class name.
 * It can also marks one or multiple element in the tree to be active (same classname is needed, too).
 * After a ButtonBox has been added all child elements the method finish should be called.
 */
export class ButtonBox extends ol.Object {
  /**
   * @param {ButtonBoxOptions} [options={}]
   */
  constructor (options = {}) {
    super()

    /**
     * @type {string}
     * @private
     */
    this.className_ = options.className || 'g4u-menu'

    this.classNames_ = {
      body: this.className_ + '-content',
      collapseButton: this.className_ + '-collapsebutton',
      titleButton: this.className_ + '-titlebutton',
      title: this.className_ + '-title',
      active: this.className_ + '-active',
      collapsed: this.className_ + '-collapsed',
      lastVisible: this.className_ + '-last-visible'
    }

    /**
     * @type {jQuery}
     * @private
     */
    this.$element_ = $('<div>')
      .addClass(this.className_)

    if (options.hasOwnProperty('id')) {
      this.$element_.attr('id', options.id)
    }

    /**
     * @type {boolean}
     * @private
     */
    this.titleButton_ = options.titleButton || false

    /**
     * @type {boolean}
     * @private
     */
    this.collapsible_ = (options.hasOwnProperty('collapsible')) ? options.collapsible : true

    /**
     * @type {boolean}
     * @private
     */
    this.collapsed_ = (options.hasOwnProperty('collapsed')) ? options.collapsed : false

    /**
     * @type {jQuery}
     * @private
     */
    this.$body_ = $('<div>')
      .addClass(this.classNames_.body)

    if (options.hasOwnProperty('content')) {
      this.$body_.append(options.content)
    }

    if (options.hasOwnProperty('title')) {
      /**
       * @type {jQuery}
       * @private
       */
      this.$title_ = $('<div>')
        .addClass(this.classNames_.title)

      this.$element_.append(this.$title_)

      if (this.collapsible_) {
        let $collapseButton = $('<button>')
          .addClass(this.classNames_.collapseButton)

        $collapseButton.on('click', () => {
          this.setCollapsed(!this.collapsed_)
          $collapseButton.blur()
        })

        if (this.titleButton_) {
          let $titleButton = $('<button>')
            .addClass(this.classNames_.titleButton)
            .on('click', () => {
              this.dispatchEvent('title:click')
              $titleButton.blur()
            })
            .html(options.title)

          if (options.rtl) {
            $titleButton.prop('dir', 'rtl')
          }

          this.$title_
            .append($collapseButton)
            .append($titleButton)
        } else {
          $collapseButton
            .addClass(this.classNames_.titleButton)
            .html(options.title)

          if (options.rtl) {
            $collapseButton.prop('dir', 'rtl')
          }

          this.$title_
            .append($collapseButton)
        }
      } else {
        this.$title_
          .html(options.title)

        if (options.rtl) {
          this.$title_.prop('dir', 'rtl')
        }
      }
    } else {
      this.collapsible_ = false
      this.collapsed_ = false
    }

    this.$element_.append(this.$body_)

    // this.collapsed_ will be setted in the following functions
    if (!this.collapsed_) {
      this.setCollapsed(false)
    } else {
      this.setCollapsed(true)
    }
  }

  /**
   * @param {boolean} collapsed
   * @param {boolean} silent
   */
  setCollapsed (collapsed, silent) {
    if (collapsed) {
      this.$body_.addClass(cssClasses.hidden)
      this.$element_.addClass(this.classNames_.collapsed)
    } else {
      this.$body_.removeClass(cssClasses.hidden)
      this.$element_.removeClass(this.classNames_.collapsed)
    }

    this.collapsed_ = collapsed

    this.lastVisibleClass_()
    if (!silent) {
      this.dispatchEvent('change:collapsed')
    }
  }

  /**
   * @returns {boolean}
   */
  getCollapsed () {
    return this.collapsed_
  }

  /**
   * distribute the last visible class to the correct element
   * @private
   */
  lastVisibleClass_ () {
    // first take the last visible class name away
    if (this.takeLastVisible(this.$element_)) {
      // if the last visible class name was taken, distribute it to the correct element
      this.giveLastVisible(this.$element_)
    }
  }

  /**
   * Returns the body element
   * @returns {jQuery}
   */
  get$Body () {
    return this.$body_
  }

  /**
   * Returns the element itself
   * @returns {jQuery}
   */
  get$Element () {
    return this.$element_
  }

  /**
   * finds and returns the last child in the body of the given element if it is of the same classname as this ButtonBox
   * @param {jQuery} $element
   * @returns {jQuery}
   * @private
   */
  get$LastChild_ ($element) {
    return $element.children('.' + this.classNames_.body).children(':last-child')
  }

  setCollapseButtonActive (active) {
    this.$title_.children('.' + this.classNames_.collapseButton).toggleClass(this.classNames_.active, active)
  }

  setTitleButtonActive (active) {
    this.$title_.children('.' + this.classNames_.titleButton).toggleClass(this.classNames_.active, active)
  }

  /**
   * Gives the last element in the element the last visible class
   * @param $element
   */
  giveLastVisible ($element) {
    if ($element.hasClass(this.classNames_.collapsed)) {
      // if the element is collapsed itself is the last visible element
      $element.addClass(this.classNames_.lastVisible)
    } else {
      if ($element.hasClass(this.className_)) {
        // the element is a button box (with the same className) and not collapsed
        let $lastChild = this.get$LastChild_($element)
        if ($lastChild) {
          // -> recursively call this method on the last element
          this.giveLastVisible($lastChild)
        } else {
          // no last element exists
          $element.addClass(this.classNames_.lastVisible)
        }
      } else {
        // the element is no buttonbox with the same classname it is declared the last visible element
        $element.addClass(this.classNames_.lastVisible)
      }
    }
  }

  /**
   * Searches for the last visible class in its child elements and removes it.
   * Returns true if it was removed.
   * @param {jQuery} $element
   * @returns {boolean}
   */
  takeLastVisible ($element) {
    if ($element.hasClass(this.classNames_.lastVisible)) {
      $element.removeClass(this.classNames_.lastVisible)
      return true
    } else if ($element.hasClass(this.className_)) {
      let $lastChild = this.get$LastChild_($element)
      // reverse call
      if ($lastChild) {
        return this.takeLastVisible($lastChild)
      } else {
        return false
      }
    } else {
      return false
    }
  }

  /**
   * This method should be called after all child elements have been added to the button box
   */
  finish () {
    this.takeLastVisible(this.$element_)
    this.giveLastVisible(this.$element_)
  }
}

import ol from 'openlayers'
import $ from 'jquery'
import { cssClasses, keyCodes } from '../globals'

import 'polyfill!Array.prototype.findIndex,Array.prototype.find'

import '../../less/dropdown.less'
import { ListenerOrganizerMixin } from '../ListenerOrganizerMixin'
import { mixin } from '../utilities'

/**
 * @typedef {object} DropdownOptions
 * @property {string} [className='g4u-dropdown']
 * @property {string} [ghostentry='no entries'] This text is shown if the dropdown has no entries
 * @property {number} [slideDuration=400] standard slideDuration
 */

/**
 * @typedef {Object} Entry
 * @property {jQuery} $element
 * @property {*} value
 */

$.extend($.easing, {
  easeOutCirc: function (x, t, b, c, d) {
    return c * Math.sqrt(1 - (t = t / d - 1) * t) + b
  },
  easeInCirc: function (x, t, b, c, d) {
    return -c * (Math.sqrt(1 - (t /= d) * t) - 1) + b
  }
})

/**
 * A HTML Dropdown select.
 * The text entries in the list can be setted and changed and given a click handler.
 * @fires 'leave:backwards' This event is raised if the dropdown is left via the up arrow or shift+tab
 * @fires 'leave:forwards' This event is raised if the dropdown is left via the down arrow or tab
 * @fires 'close' dropdown closing without select
 * @fires 'select' dropdown closing with select
 */
export class Dropdown extends mixin(ol.Object, ListenerOrganizerMixin) {
  /**
   * @param {DropdownOptions} [options={}]
   */
  constructor (options = {}) {
    super()

    /**
     * @type {string}
     * @private
     */
    this.className_ = options.className || 'g4u-dropdown'

    /**
     * @type {Object.<string, string>}
     * @private
     */
    this.classNames_ = {
      entry: this.className_ + '-entry',
      selected: this.className_ + '-selected',
      ghost: this.className_ + '-ghostentry'
    }

    /**
     * @type {jQuery}
     * @private
     */
    this.$element_ = $('<div>')
      .addClass(this.className_)

    /**
     * @type {jQuery}
     */
    this.$ghostentry = $('<button tabindex="-1">')
      .addClass(this.classNames_.ghost)
      .html(options.ghostentry || 'no entries')

    /**
     * @type {number}
     * @private
     */
    this.slideDuration_ = options.slideDuration || 400

    /**
     * @type {Entry[]}
     * @private
     */
    this.entriesArray_ = []

    /**
     * @type {number}
     * @private
     */
    this.selectedIndex_ = -1

    // key handling

    this.setUpKeyboardHandling_()

    this.listenAt(this.$element_).on('focus', () => {
      if (this.selectedIndex_ > -1) {
        this.entriesArray_[this.selectedIndex_].$element.focus()
      }
    })

    this.listenAt(this.$element_).on('mousemove', e => {
      e.stopPropagation()
    })

    this.slideUp(true)

    let skipCollapse

    this.listenAt(this.$element_).on('click', () => {
      skipCollapse = true
    })

    this.listenAt([
      // $(this.getMap().getViewport()).find('.ol-overlaycontainer-stopevent'),
      document
    ]).on('click', () => {
      if (skipCollapse) {
        skipCollapse = false
      } else if (this.collapse_) {
        this.slideUp()
      }
    })
  }

  detach () {
    this.detachAllListeners()
  }

  /**
   * returns the value of the selected list element
   * @returns {*}
   */
  getValue () {
    if (this.selectedIndex_ >= 0) {
      return this.entriesArray_[this.selectedIndex_].value
    }
  }

  /**
   * returns the text of the current selected list element
   */
  getText () {
    if (this.selectedIndex_ >= 0) {
      return this.entriesArray_[this.selectedIndex_].text
    }
  }

  /**
   * @private
   */
  setUpKeyboardHandling_ () {
    this.listenAt(this.$element_).on('keydown', e => {
      switch (e.which) {
        case keyCodes.ARROW_DOWN:
          e.preventDefault()
          e.stopPropagation()
          if (this.selectedIndex_ < this.entriesArray_.length - 1) {
            this.entriesArray_[this.selectedIndex_ + 1].$element.addClass(this.classNames_.selected)
            this.entriesArray_[this.selectedIndex_ + 1].$element.focus()
            this.entriesArray_[this.selectedIndex_].$element.removeClass(this.classNames_.selected)
            this.selectedIndex_ += 1
          }
          break
        case keyCodes.ARROW_UP:
          e.preventDefault()
          e.stopPropagation()
          if (this.selectedIndex_ > 0) {
            this.entriesArray_[this.selectedIndex_ - 1].$element.addClass(this.classNames_.selected)
            this.entriesArray_[this.selectedIndex_ - 1].$element.focus()
            this.entriesArray_[this.selectedIndex_].$element.removeClass(this.classNames_.selected)
            this.selectedIndex_ -= 1
          } else {
            this.entriesArray_[this.selectedIndex_].$element.removeClass(this.classNames_.selected)
            this.selectedIndex_ = -1
            this.dispatchEvent({
              type: 'leave:backwards',
              originalEvent: e
            })
          }
          break
        case keyCodes.TAB:
          if (!e.shiftKey) {
            this.dispatchEvent({
              type: 'leave:forwards',
              originalEvent: e
            })
          } else {
            this.dispatchEvent({
              type: 'leave:backwards',
              originalEvent: e
            })
          }
          break
        case keyCodes.ENTER:
          e.stopPropagation()
          e.preventDefault()
          this.select$Entry_(this.entriesArray_[this.selectedIndex_].$element)
      }
    })
    this.listenAt(window).on('keydown', e => {
      if (this.collapse_ && e.which === keyCodes.ESCAPE) {
        this.slideUp()
        this.dispatchEvent('close')
      }
    })
  }

  /**
   * return the whole element
   * @returns {jQuery}
   */
  get$Element () {
    return this.$element_
  }

  /**
   * Returns the amount of dropdown entries
   * @returns {Number}
   */
  getLength () {
    return this.entriesArray_.length
  }

  /**
   * Adds an entry to the end of the dropdown list
   * @param {*} value
   * @param {string} [text=value]
   * @param {boolean} [optSelected=false]
   */
  addEntry (value, text, optSelected = false) {
    text = text || value

    let index = this.getLength()
    this.setLength(index + 1)

    let entry = this.entriesArray_[index]
    entry.text = text
    entry.$element.html(text)
    entry.value = value

    if (optSelected) {
      entry.$element.addClass(this.classNames_.selected)
      this.selectedIndex_ = index
    }
  }

  setActivated (value, active) {
    this.entriesArray_.find(o => o.value === value).$element.toggleClass(cssClasses.active, active)
  }

  setFastMode (value) {
    this.fastMode_ = value
  }

  /**
   * This function takes an array of entries (strings).
   * The length of the dropdown is set to the length of the arrays (they have to have the same length).
   * @param {any[]} values
   * @param {string[]} [texts=values]
   */
  setEntries (values, texts) {
    texts = texts || values
    this.setLength(values.length)

    for (let i = 0, ii = values.length; i < ii; i++) {
      this.entriesArray_[i].text = texts[i]
      this.entriesArray_[i].$element.html(texts[i])
      this.entriesArray_[i].value = values[i]
    }
  }

  select$Entry_ ($entry) {
    if (!$entry.hasClass(this.classNames_.selected)) {
      $entry.addClass(this.classNames_.selected)
      if (this.selectedIndex_ > -1) {
        this.entriesArray_[this.selectedIndex_].$element.removeClass(this.classNames_.selected)
      }
    }
    this.selectedIndex_ = this.entriesArray_.findIndex(el => el.$element === $entry)
    this.slideUp()
    this.dispatchEvent('select')
  }

  /**
   * This function corrects the number of entries in the dropdown. The content of the entries is not respected.
   * New entries are slided down, to be removed entries are slided up then removed.
   * @param {number} length
   */
  setLength (length) {
    if (this.selectedIndex_ > -1) {
      this.entriesArray_[this.selectedIndex_].$element.removeClass(this.classNames_.selected)
      this.selectedIndex_ = -1
    }

    let i, ii

    if (this.entriesArray_.length === 0) { // removing ghost entry
      this.$element_.empty()
    }

    if (length > this.entriesArray_.length) { // adding entries and dropdown handlers
      for (i = this.entriesArray_.length, ii = length; i < ii; i++) {
        let $entry = $('<button tabindex="-1">')
          .addClass(this.classNames_.entry)
          .hide()

        $entry.on('click', () => this.select$Entry_($entry))
        $entry.focus()

        this.$element_.append($entry)

        this.entriesArray_.push({
          $element: $entry
        })

        $entry.slideDown({ duration: this.slideDuration_ })
      }
    } else if (length < this.entriesArray_.length) { // removing entries
      for (i = this.entriesArray_.length - 1, ii = length; i >= ii; i--) {
        this.entriesArray_[i].$element.slideUp({
          duration: this.slideDuration_,
          complete: () => {
            this.entriesArray_.pop()
            this.$element_.children().last().remove()
          }
        })
      }
      if (this.selectedIndex_ >= length) { // correcting selected element
        this.selectedIndex_ = length - 1
      }
    }
  }

  showGhostEntry () {
    this.setLength(0)
    this.$element_.append(this.$ghostentry)
    this.slideDown()
  }

  focus () {
    if (this.entriesArray_.length >= 0) {
      if (this.selectedIndex_ < 0) {
        this.selectedIndex_ = 0
        this.entriesArray_[0].$element.addClass(this.classNames_.selected)
      }
      this.entriesArray_[this.selectedIndex_].$element.focus()
    }
  }

  /**
   * @param {boolean} [immediately=false] if setted to true the animation is skipped
   * @returns {Promise}
   */
  slideUp (immediately = false) {
    return new Promise(resolve => {
      this.collapse_ = false
      let duration = this.slideDuration_
      if (immediately || this.fastMode_) {
        duration = 0
      }
      this.$element_.slideUp({
        duration: duration,
        complete: () => {
          this.$element_.addClass(cssClasses.hidden)
          resolve()
        },
        easing: 'easeInCirc'
      })
    })
  }

  /**
   * @param {boolean} [immediately=false] if setted to true the animation is skipped
   * @returns {Promise}
   */
  slideDown (immediately = false) {
    return new Promise(resolve => {
      if (this.$element_.children().length > 0) {
        let duration = this.slideDuration_
        if (immediately || this.fastMode_) {
          duration = 0
        }
        this.$element_.removeClass(cssClasses.hidden)
        this.$element_.slideDown({
          easing: 'easeOutCirc',
          complete: () => {
            this.collapse_ = true
            resolve()
          },
          duration: duration
        })
      }
    })
  }

  /**
   * Removes all entries
   */
  clear () {
    this.slideUp()
    this.$element_.empty()
    this.entriesArray_ = []
    this.selectedIndex_ = -1
  }

  /**
   * Returns true if the dropdown has selectable elements
   * @returns {boolean}
   */
  isSelectable () {
    return (this.entriesArray_.length > 0)
  }
}

export class History {
  constructor () {
    this.callbacks_ = []

    if (window.history.state !== 'future') {
      window.history.replaceState('base', '')
      window.history.pushState('future', '')
    }

    window.addEventListener('popstate', e => {
      if (e.state === 'base') {
        let cb = this.callbacks_.pop()
        if (cb) {
          cb.call()
          window.history.forward()
        } else {
          window.history.back()
        }
      }
    })
  }

  push (callback) {
    this.callbacks_.push(callback)
  }

  pop () {
    this.callbacks_.pop()()
  }

  flush () {
    while (this.callbacks_.length) {
      this.pop()
    }
  }
}

/**
 * @module html
 * html helper functions
 */

import $ from 'jquery'
import {cssClasses} from '../globals'

/**
 * This function gets an $element inside a $context in fornt of all of them
 * @param {jQuery} $element
 * @param {jQuery} $context
 */
export function getInFront ($element, $context) {
  let highest = 0

  $context = $context || $(document)

  let foundAbsolute = false

  $element.parents().each((i, el) => {
    if (!$(el).is($context)) {
      if ($(el).css('position') === 'absolute') {
        foundAbsolute = true
        getInFront($(el), $context)
        getInFront($element, $(el))
      }
    }
  })

  if (!foundAbsolute) {
    $context
      .find('*:visible')
      .not((i, el) => el !== $element[0] && $(el).parents().is($element))
      .each(function () {
        let current = parseInt($(this).css('z-index'), 10)
        if (current && highest < current) {
          highest = current
        }
      })

    $element.css('z-index', highest + 1)
  }
}

/**
 * @param {jQuery} $element
 * @param {string} text
 */
export function addTooltip ($element, text) {
  $element
    .addClass(cssClasses.hasTooltip)
    .append($('<span role="tooltip">')
      .html(text))
}

/**
 * @param {jQuery} $element
 * @param {string} text
 */
export function changeTooltip ($element, text) {
  $element
    .find('[role=tooltip]')
    .html(text)
}

import ol from 'openlayers'
import $ from 'jquery'

import { cssClasses, keyCodes } from '../globals'
import { getInFront } from './html'

import '../../less/shield.less'

/**
 * @typedef {object} ShieldOptions
 * @property {G4UMap} map
 * @property {string} [className='g4u-shield']
 */

/**
 * @typedef {object} ElementPosition
 * @property {jQuery} $actualElement
 * @property {jQuery} $oldParent
 * @property {number} oldIndex
 */

/**
 * @typedef {object} OnTopOptions
 * @property {boolean} [findParentWindow=false]
 */

/**
 * A shield that sets itself in front of all other elements in a context if activated, hides itself if deactivated.
 * It can get another element in front of it (Attention: it gets removed from its context temporarly)
 */
export class Shield extends ol.Object {
  /**
   * @param {ShieldOptions} options
   */
  constructor (options) {
    super()

    /**
     * @type {string}
     * @private
     */
    this.className_ = options.className || 'g4u-shield'

    /**
     * @type {G4UMap}
     * @private
     */
    this.map_ = options.map

    /**
     * @type {jQuery}
     * @private
     */
    this.$context_ = $(this.map_.getTarget())

    /**
     * @type {jQuery}
     * @private
     */
    this.$element_ = $('<div>')
      .addClass(this.className_)

    this.$context_.append(this.$element_)

    this.setActive(options.hasOwnProperty('active') ? options.active : false)

    this.$element_.on('keydown', e => {
      if (e.which === keyCodes.ESCAPE) {
        if (this.getActive()) {
          this.setActive(false)
        }
      }
    })

    /**
     * @type {Map<jQuery, ElementPosition>}
     * @private
     */
    this.elementsOnTop_ = new Map()
  }

  /**
   * @param {boolean} active
   */
  setActive (active) {
    let oldValue = this.active_
    if (oldValue !== active) {
      if (active) {
        this.$element_.removeClass(cssClasses.hidden)
        getInFront(this.$element_, this.$context_)
      } else {
        this.$element_.addClass(cssClasses.hidden)
      }
      this.active_ = active
      this.dispatchEvent({
        type: 'change:active',
        oldValue: oldValue,
        key: 'active'
      })
    }
  }

  /**
   * @returns {boolean}
   */
  getActive () {
    return this.active_
  }

  /**
   * Gets the given element in front of the shield. The element is removed from its context temporarily
   * @param {jQuery} $element
   * @param {OnTopOptions} [options]
   */
  add$OnTop ($element, options = {}) {
    let $actualElement = $element

    if (!options.hasOwnProperty('findParentWindow') || options.findParentWindow) {
      let $window = $element.parents().filter('.g4u-window')
      if ($window.length > 0) {
        $actualElement = $window
      }
    }

    let $oldParent = $actualElement.parent()

    this.elementsOnTop_.set($element[0], {
      $actualElement,
      $oldParent,
      oldIndex: $oldParent.children().index($actualElement)
    })

    this.$element_.append($actualElement)
    getInFront($actualElement, this.$element_)

    for (let className of Array.from($oldParent[0].classList)) {
      this.$element_.addClass(className)
    }
  }

  /**
   * Returns the given element in front of the shield to the previous context
   * @param {jQuery} $element
   * @returns {Promise}
   */
  remove$OnTop ($element) {
    let element = $element[0]

    let {$actualElement, $oldParent, oldIndex} = this.elementsOnTop_.get(element)

    if (oldIndex === 0) {
      $oldParent.prepend($actualElement)
    } else {
      $oldParent.children().eq(oldIndex - 1).after($actualElement)
    }

    for (let className of Array.from($oldParent[0].classList)) {
      this.$element_.removeClass(className)
    }

    this.elementsOnTop_.delete(element)
  }

  /**
   * Returns all children in front of the shield
   * @returns {jQuery}
   */
  get$ElementsInFront () {
    return this.$element_.children()
  }
}

import ol from 'openlayers'
import $ from 'jquery'

import {offset} from '../utilities'
import {getInFront} from './html'
import {cssClasses} from '../globals'

import BScroll from 'better-scroll'

import '../../less/window.less'

/**
 * @typedef {object} WindowOptions
 * @property {boolean} [draggable=false]
 * @property {string} [id] an html id for the outer window element
 * @property {string} [className] an alternative html class name to use instead of 'g4u-window', not recommended
 * @property {G4UMap} [map] the map the window should be shown on. The viewport will be set as the context of the window
 * @property {jQuery} [$context] context the window is shown in
 * @property {boolean} [visible=false] the visibility of the window on start up
 * @property {boolean} [fixedPosition=false] if the window is movable or not
 */

/**
 * A HTML Window
 */
export class Window extends ol.Object {
  /**
   * @param {WindowOptions} options
   */
  constructor (options = {}) {
    super()

    /**
     * @type {string}
     * @private
     */
    this.className_ = (options.hasOwnProperty('className')) ? options.className : 'g4u-window'

    /**
     * @type {string}
     * @private
     */
    this.classNameBody_ = this.className_ + '-content'

    /**
     * @type {string}
     * @private
     */
    this.classNameButtonClose_ = this.className_ + '-button-close'

    /**
     * @type {jQuery}
     * @private
     */
    this.$element_ = $('<div>').addClass(this.className_)
      .on('click', e => e.stopPropagation())

    /**
     * @type {jQuery}
     * @private
     */
    this.$positionWrapper_ = $('<div>').addClass(this.className_ + '-inner-position')

    /**
     * @type {jQuery}
     * @private
     */
    this.$scrollWrapper_ = $('<div>').addClass(this.className_ + '-scroll-wrap')

    /**
     * @type {jQuery}
     * @private
     */
    this.$body_ = $('<div>').addClass(this.classNameBody_)

    /**
     * @type {jQuery}
     * @private
     */
    this.$button_ = $('<button>').addClass(this.classNameButtonClose_)

    this.$element_
      .append(this.$button_)
      .append(this.$positionWrapper_
        .append(this.$scrollWrapper_.append(this.$body_)
        )
      )
      .addClass(cssClasses.hidden)

    if (options.hasOwnProperty('id')) {
      this.$element_.attr('id', options.id)
    }

    this.$button_.on('click', () => this.setVisible(false))

    /**
     * @type {boolean}
     * @private
     */
    this.draggable_ = options.hasOwnProperty('draggable') ? options.draggable : true

    /**
     * @type {boolean}
     * @private
     */
    this.fixedPosition_ = options.fixedPosition === true

    /**
     * @type {G4UMap}
     * @private
     */
    this.map_ = options.map

    /**
     * @type {jQuery}
     * @private
     */
    this.$context_ = $(this.map_.getTarget())
    let initialDraggable = this.draggable_

    this.map_.asSoonAs('ready', true, () => {
      this.map_.on('resize', () => this.updateSize(true))

      let onChangeMobile = () => {
        if (this.map_.get('mobile')) {
          this.draggable_ = false
          if (this.getVisible()) {
            this.setEnableMobileScrolling_(true)
          }
        } else {
          this.draggable_ = initialDraggable
          if (this.scroll_ && this.scroll_.enabled) {
            this.setEnableMobileScrolling_(false)
          }
        }
        this.updateSize()
      }

      onChangeMobile()
      this.map_.on('change:mobile', onChangeMobile)
    })

    this.makeWindowDraggable_()

    // get window in front if clicked

    this.$element_.on('mousedown', () => {
      this.getInFront()
    })

    /**
     * @type {boolean}
     * @private
     */
    this.visible_ = false
    this.setVisible(options.hasOwnProperty('visible') ? options.visible : false)

    /**
     * @type {boolean}
     * @private
     */
    this.shieldActivated_ = false
  }

  /**
   * Makes the Window draggable
   * @private
   */
  makeWindowDraggable_ () {
    let clickPosX = 0
    let clickPosY = 0

    let preventClick = false
    let moving = false

    this.$context_.on('mousemove', e => {
      if (moving) {
        if (e.clientY - clickPosY !== this.$element_.offset().top ||
          e.clientX - clickPosX !== this.$element_.offset().left) {
          preventClick = true
          this.$element_.css('position', 'absolute')
          this.$element_.offset({ top: e.clientY - clickPosY, left: e.clientX - clickPosX })
          e.stopPropagation()
        }
      }
    })

    this.$button_.on('mousedown', e => {
      if (!$(e.target).is('input, textarea') && this.draggable_) {
        clickPosX = e.clientX - this.$element_.offset().left
        clickPosY = e.clientY - this.$element_.offset().top
        moving = true
      }
    })

    this.$element_.get(0).addEventListener('click', e => {
      if (preventClick && this.draggable_) {
        preventClick = false
        e.preventDefault()
        e.stopPropagation()
      }
    }, true)

    this.$context_.on('mouseup', () => {
      moving = false
    })
  }

  /**
   * Enables/Disables the scroll lib needed for mobile scrolling
   * @param {boolean} scrollable
   * @private
   */
  setEnableMobileScrolling_ (scrollable) {
    if (scrollable) {
      if (!this.scroll_) {
        this.scroll_ = new BScroll(this.$scrollWrapper_.get(0), {
          scrollbar: true,
          bounce: false
          // mouseWheel: true,
          //
          // momentum: false,
          // interactiveScrollbars: true,
          // // bounce: true,
          // click: true,
          // keyBindings: true,
          // disablePointer: true,
          // disableTouch: false,
          // disableMouse: true,
          // eventPassthrough: false
        })

        window.addEventListener('wheel', e => {
          if (this.getVisible() && this.scroll_.enabled) {
            this.scroll_.scrollTo(0, Math.max(this.scroll_.maxScrollY, Math.min(0, this.scroll_.y - e.deltaY)), 100)
          }
        })
      } else {
        this.scroll_.enable()
      }
    } else {
      if (this.scroll_) {
        this.scroll_.scrollTo(0, 0, 0)
        this.scroll_.disable()
      }
    }
  }

  /**
   * Moves the window in front of all other elements inside its context
   */
  getInFront () {
    getInFront(this.$element_, this.$context_)
  }

  /**
   * @param {boolean} visible
   * @param {boolean} [popHistory=true]
   */
  setVisible (visible, popHistory = true) {
    let oldValue = this.visible_
    if (oldValue !== visible) {
      if (visible) {
        this.$element_.removeClass(cssClasses.hidden)
        if (this.map_.get('mobile')) {
          this.setEnableMobileScrolling_(true)
          this.map_.get('shield').setActive(true)
          this.map_.get('shield').add$OnTop(this.$element_, {
            findParentWindow: false
          })
          this.shieldActivated_ = true
          this.map_.get('history')
            .push(() => this.setVisible(false, false))
        } else if (!this.map_.get('shield').getActive()) {
          this.getInFront()
        }
      } else {
        if (this.map_.get('mobile')) {
          this.setEnableMobileScrolling_(false)
        }
        if (this.shieldActivated_) {
          if (popHistory) {
            this.map_.get('history').pop()
          } else {
            this.map_.get('shield').setActive(false)
            this.map_.get('shield').remove$OnTop(this.$element_)
            this.shieldActivated_ = false
          }
        }
        this.$element_.addClass(cssClasses.hidden)
        this.$element_.css('top', '')
        this.$element_.css('left', '')
      }
      this.visible_ = visible
      this.updateSize(true)
      this.dispatchEvent({ type: 'change:visible', oldValue: oldValue, newValue: visible })
    }
  }

  /**
   * @returns {boolean}
   */
  getVisible () {
    return this.visible_
  }

  /**
   * @returns {jQuery}
   */
  get$Body () {
    return this.$body_
  }

  /**
   * @returns {jQuery}
   */
  get$Element () {
    return this.$element_
  }

  /**
   * Fixes width and height depending on the current content on the popup.
   * needs to be called when the window is visible.
   * @param {boolean} [initialize=false]
   */
  updateSize (initialize = false) {
    let margin = 0

    if (this.getVisible()) {
      if (!this.map_.get('mobile')) {
        // desktop
        margin = 50

        let maxWidth = this.$context_.innerWidth() - 2 * margin
        let maxHeight = this.$context_.innerHeight() - 2 * margin

        // storing values
        let position = this.$element_.css('position')
        let top = this.$element_.css('top')
        let left = this.$element_.css('left')

        // reset position to get default value
        this.$element_.css('position', '')

        // resetting all directly setted values
        this.$element_.css('top', '')
        this.$element_.css('left', '')
        this.$element_.css('width', '')
        this.$element_.css('height', '')

        this.get$Body().css('max-height', '')

        // position element so it can be measured
        this.$element_.css('position', 'fixed')
        this.$element_.css('top', '0px')
        this.$element_.css('left', '0px')

        // clearing fixed size and height

        this.$element_.css('width', 'auto')
        this.$element_.css('height', 'auto')

        // calculate width & height

        let calcWidth = this.$element_.outerWidth()
        let calcHeight = this.$element_.outerHeight()

        let width = Math.min(calcWidth, maxWidth)
        let height = Math.min(calcHeight, maxHeight)

        this.$element_.css('width', width)
        this.$element_.css('height', height)

        // setting max-height for the scroll bar
        // i assume here there is no margin and no padding on the parent element
        let padding = parseInt(this.get$Body().css('padding-top').split('px')[ 0 ]) +
          parseInt(this.get$Body().css('padding-bottom').split('px')[ 0 ])
        this.get$Body().css('max-height', this.get$Element().innerHeight() - this.$button_.outerHeight() - padding)

        this.$element_.css('position', position)

        if (initialize && !this.fixedPosition_) {
          // getting initial values
          top = this.$element_.css('top')
          left = this.$element_.css('left')

          // initialize_ at top middle
          let off = offset(this.$context_, this.$element_)

          let sideDist = (this.$context_.width() - width) / 2
          let topDist = margin

          let topPixel = (top === 'auto') ? 0 : parseInt(top)
          let leftPixel = (left === 'auto') ? 0 : parseInt(left)

          this.$element_.css('top', topPixel + off.top + topDist)
          this.$element_.css('left', leftPixel + off.left + sideDist)
        } else {
          // move back to old position
          this.$element_.css('top', top)
          this.$element_.css('left', left)
        }
      } else {
        // mobile
        margin = 10

        let maxWidth = this.$context_.innerWidth() - 2 * margin
        let maxHeight = this.$context_.innerHeight() - 2 * margin

        this.get$Body().css('max-height', '')
        this.$element_.css('width', maxWidth)
        this.$element_.css('height', maxHeight)

        let contextOff = this.$context_.offset()

        this.$element_.css('left', margin + contextOff.left)
        this.$element_.css('top', margin + contextOff.top)
      }

      if (this.get$Body().children(`:not(.${cssClasses.hidden})`).length === 0) {
        this.$element_.addClass(cssClasses.hidden)
      }

      if (this.scroll_) {
        this.scroll_.refresh()
      }
    }
  }
}

import ol from 'openlayers'
import sortBy from 'lodash/sortBy'

/**
 * This interaction is a little further customized select interaction.
 * Selected features get sorted by geometry type.
 */
export class FeatureSelect extends ol.interaction.Select {
  /**
   * @param {object} [options={}]
   */
  constructor (options = {}) {
    super(options)

    let sort = f => {
      if ((f.getGeometry() instanceof ol.geom.Point) || (f.getGeometry() instanceof ol.geom.MultiPoint)) {
        return 1
      } else if ((f.getGeometry() instanceof ol.geom.LineString) ||
        (f.getGeometry() instanceof ol.geom.MultiLineString)) {
        return 2
      } else {
        return 3
      }
    }

    this.on('select', e => {
      e.selected = sortBy(e.selected, sort)
      e.deselected = sortBy(e.deselected, sort)
    })
  }
}

import ol from 'openlayers'
import $ from 'jquery'

export class MapEventInteraction extends ol.interaction.Interaction {
  constructor (options = {}) {
    let type = options.type || 'singleclick'
    super({
      handleEvent: e => {
        if (this.getActive() && e.type === type && $(e.originalEvent.target).is('.ol-viewport > canvas')) {
          this.dispatchEvent({
            type: 'mapevent',
            mapEvent: e
          })
        }
        return true
      }
    })
  }
}

import ol from 'openlayers'
import $ from 'jquery'

import stripJsonComments from 'strip-json-comments'
import {Debug} from './Debug'
import { URL } from './URLHelper'

/**
 * @typedef {Object.<string, string>|string} Localizable
 */

/**
 * @typedef {object} L10NOptions
 * @property {string} [defaultLanguage='en']
 * @property {string} [currentLanguage]
 * @property {string} [languageFile='files/l10n.commented.json']
 * @property {string[]} [availableLanguages]
 */

/**
 * This class localizes texts by either selecting one from a dictionary (asynchron loaded JSON File) or choosing the
 * right string from a selection.
 */
export class L10N extends ol.Observable {
  /**
   * @param {L10NOptions} options
   */
  constructor (options = {}) {
    super()

    /**
     * @type {string}
     * @private
     */
    this.defaultLang_ = options.defaultLanguage || 'en'

    /**
     * @type {string}
     * @private
     */
    this.currentLang_ = options.currentLanguage || this.defaultLang_

    /**
     * @type {URL}
     * @private
     */
    this.languageFileUrl_ = URL.extractFromConfig(options, 'languageFile', 'files/l10n.commented.json')

    if (options.availableLanguages) {
      /**
       * @type {string[]}
       * @private
       */
      this.availableLanguages_ = options.availableLanguages
    } else {
      this.availableLanguages_ = [this.currentLang_]
      if (this.currentLang_ !== this.defaultLang_) {
        this.availableLanguages_.push(this.defaultLang_)
      }
    }
  }

  /**
   * @returns {string[]}
   */
  getAvailableLanguages () {
    return this.availableLanguages_
  }

  /**
   * @param {string[]} languages
   */
  setAvailableLanguages (languages) {
    this.availableLanguages_ = languages
  }

  /**
   * Loads the language file. This function is called manually from outside to be abel to pass in a callback.
   */
  ajaxGetLanguageFile () {
    let finalUrl = this.languageFileUrl_.finalize()
    return $.ajax({
      type: 'GET',
      url: finalUrl,
      dataType: 'text',
      success: data => {
        this.dictionary = JSON.parse(stripJsonComments(data))
      },
      error: () => { // The arguments are ignored
        Debug.error(`The language file ${finalUrl} couldn't be loaded or parsed`)
      }
    })
  }

  /**
   * Select language string. Note that this routine throws an error if there is no fitting string. Method to obtain the
   *    fitting string:
   * 1. If there is no data, an 'Unable to obtain localization' error is thrown.
   * 2. If data is a string, that string is returned.
   * 3. If language is given and present in data, the string value for langage is returned.
   * 4. If the default language is given and present in data, that value is returned.
   * 5. If the special tag '*' is present in data, that value is returned.
   * 6. If still no string was found at this point, an 'Unable to obtain localization' error is thrown.
   * @property {Localizable} data
   * @returns {string} a (presumably localised) string
   */
  selectL10N (data) {
    if (data) {
      if (typeof data === 'string') { // Only a generic string is available
        return data
      } else { // an object is available
        if (data[this.currentLang_] !== null) { // current language available
          return data[this.currentLang_]
        } else if (data[this.defaultLang_] !== null) { // default language as a last resort
          return data[this.defaultLang_]
        } else if (data['*'] !== null) {
          return data['*']
        } else {
          Debug.error('Unable to obtain localization')
        }
      }
    }
  }

  /**
   * Localise identifier using given dictionary. Throws an error if identifier is not in dictionary. Relies on
   * selectL10N to select actual localisation.
   * @param {string} identifier
   * @param {boolean} silent if an error message should be printed if the text is not found in the dictionary
   * @returns {string} a (presumably localised) string
   */
  localiseUsingDictionary (identifier, silent = false) {
    if (identifier in this.dictionary) {
      return this.selectL10N(this.dictionary[identifier])
    } else if (!silent) {
      Debug.error(`Cannot localise '${identifier}': Not in dictionary`)
    }
  }

  /**
   * @param {string} lang
   */
  setCurrentLang (lang) {
    this.currentLang_ = lang
    this.dispatchEvent('change:language', lang)
  }

  /**
   *
   * @returns {string|*}
   */
  getCurrentLang () {
    return this.currentLang_
  }

  getDefaultLang () {
    return this.defaultLang_
  }

  /**
   * @returns {bool}
   */
  isRtl () {
    return this.dictionary[this.currentLang_].rtl || false
  }
}

import {GroupLayer} from './GroupLayer'

/**
 * @typedef {object} BaseLayerMixinOptions
 * @property {GroupLayer} groupLayer the group this baseLayer belongs to
 */

/**
 * This mixin provides baselayer functionality to any layer
 */
export class BaseLayerMixin {
  /**
   * @param {BaseLayerMixinOptions} options
   */
  initialize (options) {
    /**
     * @type {GroupLayer}
     * @private
     */
    this.groupLayer_ = options.groupLayer

    this.on('change:available', this.onChangeAvailable.bind(this))
    this.on('change:visible', this.onChangeVisible.bind(this))

    this.isBaseLayer = true
  }

  /**
   * This method sets the next available layer visible if this layer gets unavailable and was the visible baselayer
   */
  onChangeAvailable () {
    if (!this.get('available') && this.getVisible()) {
      this.groupLayer_.recursiveForEach(layer => {
        if (!(layer instanceof GroupLayer) && layer.get('available')) {
          layer.setVisible(true)
        }
      })
    }
  }

  /**
   * This method sets all other baselayers not visible if set true.
   */
  onChangeVisible () {
    if (this.getVisible()) {
      this.groupLayer_.recursiveForEach(layer => {
        if (!(layer instanceof GroupLayer) && layer !== this) {
          layer.setVisible(false)
        }
      })
    } else {
      let found = false
      this.groupLayer_.recursiveForEach(layer => {
        if (!(layer instanceof GroupLayer) && layer.getVisible()) {
          found = true
        }
      })

      if (!found) {
        this.setVisible(true)
      }
    }
  }
}

