import ol from 'openlayers'

/**
 * @typedef {object} MoveOptions
 * @property {G4UMap} map
 * @property {number} [pixelPadding=0] a default padding around the target extent in pixels
 * @property {number} [meterMinSize=0] the minimal size of the target extent in meters
 * @property {number} [animationDuration=4000]
 * @property {boolean} [animations=true]
 * @property {boolean} [bouncing=false] if the animation should bounce or not
 */

/**
 * @typedef {olx.view.FitOptions} SingleMoveOptions
 * @property {boolean} [animated] if specified overwrites the default settings
 * @property {number[]|string} [padding] can be set to 'default' to use the default settings
 */

/**
 * Moves the map. Uses animations if desired.
 */
export class Move {
  /**
   * @param {MoveOptions} options
   */
  constructor (options) {
    /**
     * @type {G4UMap}
     * @private
     */
    this.map_ = options.map

    /**
     * @type {number}
     * @private
     */
    this.pixelPadding_ = options.meterMinSize !== undefined ? 50 : options.pixelPadding

    /**
     * @type {number}
     * @private
     */
    this.meterMinSize_ = options.meterMinSize !== undefined ? options.meterMinSize : 500

    /**
     * @type {number}
     * @private
     */
    this.animationDuration_ = options.animationDuration || 2000

    /**
     * @type {boolean}
     * @private
     */
    this.animations_ = options.animations !== undefined ? options.animations : true

    /**
     * @type {boolean}
     * @private
     */
    this.bouncing_ = options.bouncing !== false
  }

  /**
   * Turns animations on or off
   * @param {boolean} animations
   */
  setAnimations (animations) {
    this.animations_ = animations
  }

  /**
   * @returns {boolean}
   */
  getAnimations () {
    return this.animations_
  }

  /**
   * @param {ol.Coordinate} point
   * @param {SingleMoveOptions} [options={}]
   */
  toPoint (point, options = {}) {
    // calculate extent
    let tmpView = new ol.View({
      projection: this.map_.getView().getProjection(),
      center: point,
      resolution: this.map_.getView().getResolution()
    })
    let extent = tmpView.calculateExtent(this.map_.getSize().map(s => s - 2))

    options.padding = [0, 0, 0, 0] // no padding around this extent

    this.toExtent(extent, options)
  }

  /**
   * @param {ol.Extent} extent
   * @param {SingleMoveOptions} [options={}]
   */
  toExtent (extent, options = {}) {
    let newExtent = extent
    if (this.meterMinSize_) {
      newExtent = this.bufferUpToMinSize_(extent)
    }
    if (options.animated === undefined ? this.animations_ : options.animated) {
      this.animationZoomToExtent_(newExtent, options)
    } else {
      this.fit_(newExtent, options)
    }
  }

  /**
   * @param {ol.Point} point
   * @param {SingleMoveOptions} [options={}]
   */
  zoomToPoint (point, options = {}) {
    this.toExtent(ol.extent.boundingExtent([point]), options)
  }

  /**
   * @param {ol.Extent} extent
   * @returns {ol.Extent}
   * @private
   */
  bufferUpToMinSize_ (extent) {
    // TODO: maybe use something more precise than transforming into 3857 to get meter size.
    let extentInMeters = ol.proj.transformExtent(extent, this.map_.getView().getProjection(), 'EPSG:3857')
    let smallerSize = Math.min(ol.extent.getWidth(extentInMeters), ol.extent.getHeight(extentInMeters))
    if (smallerSize < this.meterMinSize_) {
      extentInMeters = ol.extent.buffer(extentInMeters, this.meterMinSize_ - smallerSize / 2)
      return ol.proj.transformExtent(extentInMeters, 'EPSG:3857', this.map_.getView().getProjection())
    } else {
      return extent
    }
  }

  /**
   * @param {ol.Extent} extent
   * @param {SingleMoveOptions} options
   * @private
   */
  fit_ (extent, options) {
    if (options.padding === 'default') {
      options.padding = [this.pixelPadding_, this.pixelPadding_, this.pixelPadding_, this.pixelPadding_]
    }

    // options.constrainResolution = false

    // using fit's padding option
    this.map_.getView().fit(ol.geom.Polygon.fromExtent(extent), this.map_.getSize(), options)
  }

  /**
   * This function glides or bounces to an extent
   * @param {ol.Extent} endExtent
   * @param {SingleMoveOptions} options
   * @private
   */
  animationZoomToExtent_ (endExtent, options) {
    let map = this.map_
    let view = map.getView()
    let size = map.getSize()

    let startExtent = view.calculateExtent(size)

    let moveExtent = ol.extent.extend(startExtent.slice(0), endExtent) // a extent where both extents are contained.

    if (this.bouncing_ && !ol.extent.intersects(startExtent, endExtent)) {
      let moveOptions = Object.assign({
        duration: this.animationDuration_ / 2
      }, options)
      view.fit(moveExtent, moveOptions)
      setTimeout(() => {
        let endOptions = Object.assign({
          duration: this.animationDuration_ / 2
        }, options)
        view.fit(endExtent, endOptions)
      }, this.animationDuration_ / 2)
    } else {
      let endOptions = Object.assign({
        duration: this.animationDuration_ / 2
      }, options)
      view.fit(endExtent, endOptions)
    }
  }

  /**
   * Easing function based on a circle function
   * @param {number} t
   * @returns {number}
   * @private
   */
  earlyFastRiseEasing_ (t) {
    return Math.sqrt(2 * t - Math.pow(t, 2))
  }

  /**
   * Easing function based on a circle function
   * @param {number} t
   * @returns {number}
   * @private
   */
  lateFastRiseEasing_ (t) {
    return 1 - Math.sqrt(1 - Math.pow(t, 2))
  }

  /**
   * Easing function based on a parabolic function
   * @param {number} t
   * @returns {number}
   * @private
   */
  earlyFastRiseEasing2_ (t) {
    return -Math.pow((t - 1), 2) + 1
  }

  /**
   * Easing function based on a parabolic function
   * @param {number} t
   * @returns {number}
   * @private
   */
  lateFastRiseEasing2_ (t) {
    return Math.pow(t, 2)
  }
}

import ol from 'openlayers'
import $ from 'jquery'

/**
 * extends the openlayers ol.Object class. Waits till a property is set to a specific value and calls the given
 * callback function. If the value already has the desired value the callback is called immediately.
 * @param {string} propName
 * @param {*} value
 * @param {function} cb
 */
ol.Object.prototype.asSoonAs = function (propName, value, cb) {
  if (!$.isFunction(cb)) {
    throw new Error('You need to provide a callback to asSoonAs.')
  }
  if (this.get(propName) === value) {
    // run callback
    cb()
  } else {
    this.once('change:' + propName, () => {
      // recursive call
      this.asSoonAs(propName, value, cb)
    })
  }
}

/**
 * @typedef {object} PopupContent
 * @property {string} name
 * @property {string} description
 */

export class PopupModifier {
  /**
   * @param {G4UMap} map
   */
  setMap (map) {
    /**
     * @type {G4UMap}
     * @private
     */
    this.map_ = map
  }

  /**
   * @returns {G4UMap}
   */
  getMap () {
    return this.map_
  }

  /**
   * overwrite this method
   * @param {PopupContent} popupContent
   * @returns {PopupContent|Promise.<PopupContent>}
   */
  modifyPopupContent (popupContent) {
    return popupContent
  }
}

import { Debug } from './Debug'

export class PopupModifierManager {
  constructor () {
    /**
     * @type {Map.<string,PopupModifier>}
     * @private
     */
    this.popupModifiers_ = new Map()
  }

  /**
   * register a text mutator with a name. Which mutator is finally used can be adjusted via the config files.
   * @param {string} name
   * @param {PopupModifier} popupModifier
   */
  register (name, popupModifier) {
    this.popupModifiers_.set(name, popupModifier)
  }

  /**
   * @param {PopupContent} popupContent
   * @param {string[]} popupModifiers
   * @returns {Promise.<PopupContent>}
   */
  apply (popupContent, popupModifiers) {
    let promiseContent = Promise.resolve(popupContent)

    for (let m of popupModifiers) {
      let popupModifier = this.popupModifiers_.get(m)
      if (popupModifier) {
        promiseContent = promiseContent.then(result => popupModifier.modifyPopupContent(result))
      } else {
        Debug.error(`Trying to use unregistered modifier ${m}`)
      }
    }

    return promiseContent
  }
}

import ol from 'openlayers'
import $ from 'jquery'

import { VectorLayer } from './layers/VectorLayer'
import { MapEventInteraction } from './interactions/MapEventInteraction'
import { FeaturePopup } from './FeaturePopup'
import { cssClasses } from './globals'

/**
 * @typedef {Object} ShowWMSFeatureInfoOptions
 * @property {StyleLike} [style='#defaultStyle']
 * @property {String} [seperator='<br>']
 * @property {String[]} [popupModifiers=[]]
 * @property {boolean} [animated]
 */

export class ShowWMSFeatureInfo {
  /**
   * @param {ShowWMSFeatureInfoOptions} [options={}]
   */
  constructor (options = {}) {
    this.separator_ = options.hasOwnProperty('seperator') ? options.seperator : '<br>'
    this.style_ = options.style || '#defaultStyle'

    this.animated_ = options.animated
    this.centerOnPopup_ = options.hasOwnProperty('centerOnPopup') ? options.centerOnPopup : true
    this.centerIfNoData_ = this.centerOnPopup_ && options.hasOwnProperty('centerIfNoData')
      ? options.centerIfNoData : false

    this.centerOnPopupInitial_ = this.centerOnPopup_

    this.popupModifiers_ = options.popupModifiers || []
  }

  handleClickEvent (e) {
    let map = this.getMap()
    let featurePopup = map.get('featurePopup')
    let projection = map.getView().getProjection()

    let layers = this.layers_.filter(l => l.getVisible())

    let shouldShow = map.forEachLayerAtPixel(e.mapEvent.pixel, layer => layers.indexOf(layer) > -1) &&
      !map.forEachFeatureAtPixel(e.mapEvent.pixel, FeaturePopup.canDisplay)

    if (shouldShow) {
      let coordinate = e.mapEvent.coordinate
      this.utilitySource_.clear()
      let feature
      for (let layer of layers) {
        layer.getSource().getFeatureInfo(coordinate, map.getView().getResolution(), projection)
          .then(data => {
            if (data !== '') {
              if (!feature) {
                this.utilitySource_.clear()
                feature = new ol.Feature({
                  geometry: new ol.geom.Point(coordinate),
                  description: data,
                  style: map.get('styling').getStyle(this.style_)
                })
                map.get('styling').manageFeature(feature)
                this.utilitySource_.addFeature(feature)
                featurePopup.setFeature(feature, coordinate,
                  [...this.popupModifiers_, ...layer.getSource().getFeatureInfoMutators()])
                featurePopup.setVisible(true)
                this.setPointVisible(true)
                if (this.centerOnPopup_) {
                  featurePopup.centerMapOnPopup(this.animated_)
                }
                featurePopup.once('change:visible', () => this.setPointVisible(false))
              } else {
                feature.set('description', feature.get('description') + this.separator_ + data)
              }
            }
            layer.on('change:visible', () => {
              featurePopup.setVisible(false)
            })
          })
      }
    }
  }

  layerQueryable (layer) {
    return this.layers_.some(l => {
      return l.getVisible() &&
        this.layers_.indexOf(layer) > -1 &&
        layer.getSource().getQueryable()
    })
  }

  handlePointerMoveEvent (e) {
    let map = this.getMap()
    if (!map.forEachFeatureAtPixel(e.mapEvent.pixel, FeaturePopup.canDisplay, {hitTolerance: 5})) {
      let featureTooltip = map.get('featureTooltip')
      this.lastTooltipPixel_ = e.pixel

      if (map.forEachLayerAtPixel(e.mapEvent.pixel, layer => this.layerQueryable(layer))) {
        clearTimeout(this.tooltipTimeout_)
        $(map.getViewport()).addClass(cssClasses.clickable)
        this.tooltipTimeout_ = setTimeout(() => {
          if (e.pixel === this.lastTooltipPixel_) {
            let projection = map.getView().getProjection()
            let coordinate = e.mapEvent.coordinate
            let feature
            map.forEachLayerAtPixel(e.mapEvent.pixel, layer => {
              if (this.layerQueryable(layer)) {
                layer.getSource().getFeatureInfo(coordinate, map.getView().getResolution(), projection)
                  .then(data => {
                    if (data !== '') {
                      if (!feature) {
                        feature = new ol.Feature({
                          geometry: new ol.geom.Point(coordinate),
                          description: data
                        })
                        featureTooltip.setFeature(feature, coordinate,
                          [...this.popupModifiers_, ...layer.getSource().getFeatureInfoMutators()])
                      } else {
                        feature.set('description', feature.get('description') + this.separator_ + data)
                      }
                      this.shouldHide_ = true
                    }
                    layer.on('change:visible', () => {
                      featureTooltip.setFeature(null)
                    })
                  })
              }
            })
          }
        }, 200)
      } else {
        if (this.shouldHide_) {
          featureTooltip.setFeature(null)
        }
        $(map.getViewport()).removeClass(cssClasses.clickable)
      }
    }
  }

  setMap (map) {
    let onMapChangeMobile = () => {
      if (map.get('mobile')) {
        this.centerOnPopup_ = false
      } else {
        this.centerOnPopup_ = this.centerOnPopupInitial_
      }
    }

    if (this.getMap()) {
      this.getMap().un('change:mobile', onMapChangeMobile)
      ol.Observable.unByKey(this.listenerKey_)
    }

    this.map_ = map
    if (map) {
      this.utilitySource_ = new ol.source.Vector()
      this.utilityLayer_ = new VectorLayer({
        visible: false,
        source: this.utilitySource_
      })
      map.addLayer(this.utilityLayer_)

      this.layers_ = []

      let interaction = new MapEventInteraction({type: 'singleclick'})
      interaction.on('mapevent', e => this.handleClickEvent(e))

      map.addDefaultInteraction('singleclick', interaction)

      let hoverInteraction = new MapEventInteraction({type: 'pointermove'})
      hoverInteraction.on('mapevent', e => this.handlePointerMoveEvent(e))

      map.addDefaultInteraction('pointermove', hoverInteraction)

      onMapChangeMobile()
      map.on('change:mobile', onMapChangeMobile)
    }
  }

  getPointVisible () {
    return this.utilityLayer_.getVisible()
  }

  setPointVisible (visible) {
    this.utilityLayer_.setVisible(visible)
  }

  getMap () {
    return this.map_
  }

  addLayer (layer) {
    this.layers_.push(layer)
  }
}

import ol from 'openlayers'
import { Debug } from '../Debug'

export class ClusterSource extends ol.source.Cluster {
  /**
   * @param {ol.source.Vector} source
   * @param {olx.} options
   */
  constructor (source, options) {
    options.source = source
    options.geometryFunction = options.geometryFunction || ClusterSource.defaultGeometryFunction
    super(options)
  }

  static defaultGeometryFunction (feature) {
    let geom = feature.getGeometry()
    if (geom instanceof ol.geom.Point) {
      return geom
    } else if (geom instanceof ol.geom.LineString) {
      return new ol.geom.Point(ol.extent.getCenter(geom.getExtent()))
    } else if (geom instanceof ol.geom.Polygon) {
      return new ol.geom.Point(ol.extent.getCenter(geom.getExtent()))
    } else if (geom instanceof ol.geom.Circle) {
      return new ol.Point(geom.getCenter())
    } else {
      Debug.warn('Trying to cluster unsupported geometry type.')
      return null
    }
  }
}

import ol from 'openlayers'
import $ from 'jquery'

import { asyncImageLoad, mixin } from '../utilities'
import { Debug } from '../Debug'

export class WMSFeatureInfoMixin {
  initialize (options) {
    this.featureInfo_ = options.featureInfo !== undefined

    if (this.featureInfo_) {
      if (options.featureInfoProxy !== undefined) {
        this.switchedUrl = options.originalUrl.clone()
        this.switchedUrl.proxy = options.featureInfo.proxy
      }
      this.featureInfoParams_ = options.featureInfo.params || {}
      this.featureInfoCheckable_ = options.featureInfo.checkable
      this.featureInfoMutators_ = options.featureInfo.mutators
    }
  }

  getQueryable () {
    return this.featureInfoParams_['QUERY_LAYERS'] && this.featureInfoParams_['QUERY_LAYERS'].length
  }

  getFeatureInfoMutators () {
    return this.featureInfoMutators_ || []
  }

  hasFeatureInfo () {
    return this.featureInfo_
  }

  isFeatureInfoCheckable () {
    return this.featureInfoCheckable_
  }

  updateFeatureInfoParams (newParams) {
    Object.assign(this.featureInfoParams_, newParams)
  }

  getFeatureInfo (coordinate, resolution, projection) {
    return new Promise((resolve, reject) => {
      let params = this.featureInfoParams_
      if (!params['QUERY_LAYERS'] || params['QUERY_LAYERS'].length === 0) {
        resolve('')
      } else {
        let normalUrls
        if (this.switchedUrl) {
          normalUrls = this.getUrls()
          this.setUrl(this.switchedUrl.finalize())
        }
        $.ajax({
          url: this.getGetFeatureInfoUrl(coordinate, resolution, projection, params),
          success: resolve,
          error: reject,
          dataType: 'text'
        })
        if (this.switchedUrl) {
          this.setUrls(normalUrls)
        }
      }
    })
  }

  toggleArrayEntries_ (obj, prop, names, toggle) {
    let arr = obj[prop] || []
    if (toggle) {
      // the call of toggleWMSLayers in TemplatePrintControl causes an polyfill error in
      // internet explorer / microsoft edge related to Symbol.iterator which is used for
      // the for-of loop
      names.forEach(name => {
        if (arr.indexOf(name) < 0) {
          arr.push(name)
        }
      })
      // for (let name of names) {
      //   if (arr.indexOf(name) < 0) {
      //     arr.push(name)
      //   }
      // }
    } else {
      for (let name of names) {
        let index = arr.indexOf(name)
        if (index >= 0) {
          arr.splice(index, 1)
        }
      }
    }
    obj[prop] = arr
    return obj
  }

  toggleWMSLayers (names, toggle) {
    this.updateParams(this.toggleArrayEntries_(this.getParams(), 'LAYERS', names, toggle))
  }

  toggleWMSQueryLayers (names, toggle) {
    this.updateFeatureInfoParams(this.toggleArrayEntries_(this.featureInfoParams_, 'QUERY_LAYERS', names, toggle))
  }

  arrayContainsAll (arr, contains) {
    return contains.every(needle => arr.indexOf(needle) >= 0)
  }

  getWMSLayersVisible (names) {
    return this.arrayContainsAll(this.getParams().LAYERS || [], names)
  }

  getWMSQueryLayersVisible (names) {
    return this.arrayContainsAll(this.featureInfoParams_.QUERY_LAYERS || [], names)
  }
}

export class ImageWMSSource extends mixin(ol.source.ImageWMS, WMSFeatureInfoMixin) {
