import ol from 'openlayers'
import $ from 'jquery'

import { copy, take } from '../utilitiesObject'
import {Debug} from '../Debug'

/**
 * @typedef {olx.source.VectorOptions} SourceServerVectorOptions
 * @property {string} type the format to use
 * @property {URLLike} url
 * @property {StyleLike} [defaultStyle] a default style to fallback to
 * @property {boolean} [extractStyles=true] if styles should get extracted from the KML
 * @property {string} [loadingStrategy='ALL'] Either 'BBOX' or 'ALL' (Synonym: 'FIXED').
 *    If BBOX the given url has to contain the parameters {bboxleft}, {bboxbottom}, {bboxright}, {bboxtop}.
 * @property {number} [bboxRatio=1] If set the bbox loading strategy will increase the load extent by this factor
 * @property {ol.ProjectionLike} [bboxProjection] coordinates will be inserted into the url in this format. defaults to
 *    the interfaceProjection
 * @property {boolean} [cache=] true, false for dataType 'script' and 'jsonp'
 * @property {number} [refresh] if set the layer will refresh itself in the specified time (in ms)
 * @property {L10N} localiser
 * @property {boolean} localised
 * @property {Styling} styling
 */

/**
 * A custom Source class handling Vector Sources.
 *
 * Let you set the loading loadingStrategy, if a proxy is used and you should specify in which format it comes in.
 *
 * **IMPORTANT:** You can't set the projection of the source! This is **always** determined by the data received. If you
 * set projection here you force the source to assume that this is the projection of the view.
 *
 * This class defines a custom loader function which makes it possible to use different loading strategies.
 */
export class SourceServerVector extends ol.source.Vector {
  /**
   * @param {SourceServerVectorOptions} [options={}]
   */
  constructor (options = {}) {
    const parentOptions = copy(options)

    let urlTemplate = take(options, 'url')

    const type = take(options, 'type') || ''

    parentOptions.loader = (...args) => this.loader(...args)

    const loadingStrategy = options.loadingStrategy || 'ALL'

    if (loadingStrategy === 'BBOX') {
      let bboxRatio = options.bboxRatio || 1

      if (bboxRatio < 1) {
        throw new Error('The bboxRatio should not be smaller than 1')
      }

      let lastScaledExtent = [0, 0, 0, 0]

      parentOptions.strategy = (extent) => {
        if (ol.extent.containsExtent(lastScaledExtent, extent)) {
          return [extent]
        } else {
          let deltaX = ((extent[2] - extent[0]) / 2) * (bboxRatio - 1)
          let deltaY = ((extent[3] - extent[1]) / 2) * (bboxRatio - 1)

          lastScaledExtent = [
            extent[0] - deltaX,
            extent[1] - deltaY,
            extent[2] + deltaX,
            extent[3] + deltaY
          ]

          return [lastScaledExtent]
        }
      }
    } else {
      parentOptions.strategy = ol.loadingstrategy.all
    }

    super(parentOptions)

    /**
     * @type {L10N}
     * @private
     */
    this.localiser_ = options.localiser

    this.localised_ = options.localised || false

    /**
     * @type {string}
     * @private
     */
    this.loadingStrategy_ = loadingStrategy

    /**
     * @type {URL}
     * @protected
     */
    this.urlTemplate = urlTemplate

    /**
     * @type {string}
     * @private
     */
    this.type_ = type

    let formatOptions = {}

    if (options.hasOwnProperty('defaultStyle')) {
      formatOptions.defaultStyle = options.defaultStyle
    }

    if (options.hasOwnProperty('extractStyles')) {
      formatOptions.extractStyles = options.extractStyles
    }

    switch (this.type_) {
      case 'KML':
        formatOptions.showPointNames = false
        this.format_ = new ol.format.KML(formatOptions)
        this.dataType_ = 'text xml' // for $.ajax (GET-request)
        break
      case 'GeoJSON':
        this.format_ = new ol.format.GeoJSON(formatOptions)
        this.dataType_ = 'text json' // for $.ajax (GET-request)
        break
      default:
        throw new Error(`${this.type_} is not supported by SourceServerVector!`)
    }

    /**
     * @type {number}
     * @private
     */
    this.refresh_ = 0

    if (options.hasOwnProperty('refresh')) {
      this.setRefresh(options.refresh)
    }

    this.refreshTimeoutId_ = null

    /**
     * indicates if the source needs to be emptied
     * @type {boolean}
     * @private
     */
    this.doClear_ = false

    /**
     * @type {ol.ProjectionLike}
     * @private
     */
    this.bboxProjection_ = options.bboxProjection
  }

  /**
   * This method returns a promise which is triggered after the loader successfully loaded a source.
   * @param {ol.Extent} extent
   * @param {number} resolution
   * @param {ol.ProjectionLike} projection
   */
  loader (extent, resolution, projection) {
    // Problem with BBOX: if features are already in the layer, they shouldn't be added. Not trivial

    let url = this.urlTemplate.clone()

    if (this.loadingStrategy_ === 'BBOX') {
      let transformedExtent = ol.proj.transformExtent(extent, projection, this.bboxProjection_)

      url.expandTemplate('bboxleft', transformedExtent[0].toString())
        .expandTemplate('bboxbottom', transformedExtent[1].toString())
        .expandTemplate('bboxright', transformedExtent[2].toString())
        .expandTemplate('bboxtop', transformedExtent[3].toString())
        .expandTemplate('resolution', resolution.toString(), false)
    }

    if (this.refresh_) {
      if (this.refreshTimeoutId_) {
        clearTimeout(this.refreshTimeoutId_)
      }
      this.refreshTimeoutId_ = setTimeout(() => {
        this.doClear_ = true // clears the source
        this.loader(extent, resolution, projection) // calls the loader recursively
      }, this.refresh_)
    }

    if (this.localised_) {
      url.cache = false
    }

    let finalUrl = url.finalize()

    $.ajax({
      url: finalUrl,
      dataType: this.dataType_,
      beforeSend: () => this.dispatchEvent('vectorloadstart'),
      success: (response) => {
        // processing urls in the xml-Data (e.g. for images)
        if (url.useProxy && /xml$/.test(this.dataType_)) {
          response = this.addProxyToHrefTags(response)
        }

        if (this.doClear_) {
          this.clear()
          this.doClear_ = false
        }

        let features = this.format_.readFeatures(response, { featureProjection: projection })

        this.addFeatures(features)

        this.dispatchEvent('vectorloadend')
      },
      error: () => {
        Debug.error(`Getting Feature resource failed with url ${finalUrl}`)
        this.dispatchEvent('vectorloaderror')
      },
      headers: this.localiser_ ? {
        'Accept-Language': this.localiser_.getCurrentLang()
      } : {}
    })
  }

  /**
   * This sets the refresh rate. A value of 0 or smaller turns refresh off.
   * @param {number} refresh
   */
  setRefresh (refresh) {
    this.refresh_ = (refresh > 0) ? refresh : 0
  }

  /**
   * makes all urls in href-tags inside of a xmlDocument use the proxy address.
   * this function needs to extended with all Tags which could contain urls
   * @param {HTMLElement} text an xml-Document
   * @returns {HTMLElement} the xmlDocument
   */
  addProxyToHrefTags (text) {
    let hrefTags = text.getElementsByTagName('href') // not working in IE11

    let i, ii
    for (i = 0, ii = hrefTags.length; i < ii; i++) {
      if (hrefTags[i].textContent) {
        hrefTags[i].textContent = this.urlTemplate.useProxyFor(hrefTags[i].textContent.trim()).finalize()
      } else if (hrefTags[i].innerHTML) {
        hrefTags[i].innerHTML = this.urlTemplate.useProxyFor(hrefTags[i].innerHTML.trim()).finalize()
      } else {
        throw new Error("Can't prepend proxy inside KML (textContent and innerHTML missing)")
      }
    }

    return text
  }

  /**
   * @returns {URL}
   */
  getUrlTemplate () {
    return this.urlTemplate
  }

  /**
   * @param {URL} urlTemplate
   */
  setUrlTemplate (urlTemplate) {
    this.urlTemplate = urlTemplate
  }
}

import ol from 'openlayers'
import $ from 'jquery'

import { copyDeep, copy } from './utilitiesObject'
import { checkFor } from './utilities'
import { Debug } from './Debug'

import { parseCSSColor } from 'csscolorparser'

/**
 * @typedef {string|StyleObject|ol.style.Style} StyleLike
 */

/**
 * @typedef {Object} StyleObject
 */

/**
 * merges two style configs
 * @param {StyleObject} configTarget
 * @param {StyleObject} configSource
 * @returns {StyleObject}
 */
function mergeStyleConfigs (configTarget, configSource) {
  let mergedConf = copyDeep(configTarget)
  if (configSource) {
    for (let k of Object.keys(configSource)) {
      let sourceProp = configSource[k]

      if (configTarget.hasOwnProperty(k)) {
        let targetProp = configTarget[k]

        if (typeof targetProp === 'object' && !(targetProp instanceof Array)) {
          // if it is another object, merge recursively
          let targetProp = configTarget[k]

          if (targetProp.hasOwnProperty('type')) {
            if (sourceProp.hasOwnProperty('type')) {
              if (configTarget[k].type === sourceProp.type) {
                mergedConf[k] = mergeStyleConfigs(targetProp, sourceProp)
              }
            }
          } else {
            mergedConf[k] = mergeStyleConfigs(targetProp, sourceProp)
          }
        }
      } else {
        // copy over if it doesn't exist in the target
        if (typeof sourceProp === 'object' && !(sourceProp instanceof Array)) {
          mergedConf[k] = copyDeep(sourceProp)
        } else {
          mergedConf[k] = sourceProp
        }
      }
    }
  }
  return mergedConf
}

/**
 * This class coordinates the styling.
 */
export class Styling {
  /**
   * @param {Object} [options]
   * @param {Object} [options.styleConfigMap]
   * @param {number} [options.scaleIcons]
   * @param {boolean} [options.manageStyles=true]
   */
  constructor (options = {}) {
    /**
     * @type {Map.<string,StyleObject>}
     * @private
     */
    this.styleConfigMap_ = new Map()

    if (!this.styleConfigMap_.has('#defaultStyle')) {
      // FallbackStyle
      this.styleConfigMap_.set('#defaultStyle', {
        'stroke': {
          'color': 'rgba(0,0,0,0.9)',
          'width': 2
        },
        'fill': {
          'color': 'rgba(0,0,0,0.3)'
        },
        'image': {
          'type': 'circle',
          'stroke': {
            'color': 'rgba(0,0,0,0.9)',
            'width': 2
          },
          'fill': {
            'color': 'rgba(0,0,0,0.3)'
          }
        }
      })
    }

    if (options.styleConfigMap) {
      for (let k of Object.keys(options.styleConfigMap)) {
        this.styleConfigMap_.set(k, options.styleConfigMap[k])
      }
    }

    /**
     * @type {Map.<string,ol.style.Style>}
     * @private
     */
    this.styleMap_ = new Map()

    /**
     * @type {Set.<ol.style.Style>}
     * @private
     */
    this.allStyles_ = new Set()

    if (options.scaleIcons) {
      this.setGlobalIconScale(options.scaleIcons)
    }

    let _this = this

    /**
     * @param resolution
     * @returns {*}
     * @private
     */
    this.managingFeatureStyle_ = function (resolution) {
      let style = this.get('managedStyle')
      if ($.isFunction(style)) {
        style = style.call(this, resolution)
      }
      if (!style) {
        style = _this.getStyle('#defaultStyle')
      }
      if ($.isArray(style)) {
        return style.map(s => _this.adjustStyle_(this, s))
      } else {
        return _this.adjustStyle_(this, style)
      }
    }

    this.nullStyle_ = new ol.style.Style({
      image: null
    })

    this.manageStyles_ = options.manageStyles !== false
  }

  /**
   * @param {number} scale
   */
  setGlobalIconScale (scale) {
    /**
     * @type {number}
     * @private
     */
    this.globalIconScale_ = scale
  }

  /**
   * @returns {number}
   */
  getGlobalIconScale () {
    return this.globalIconScale_ || 1
  }

  /**
   * @param {StyleObject} styleConf
   * @returns {ol.style.Style}
   */
  getStyleFromConfig (styleConf) {
    let filledStyleConf = mergeStyleConfigs(styleConf, this.styleConfigMap_.get('#default'))

    function addFillsAndStrokes (subStyleConf) {
      subStyleConf = subStyleConf || {}
      let preparedOptions = copy(subStyleConf)

      if (checkFor(subStyleConf, 'fill')) {
        preparedOptions.fill = new ol.style.Fill(mergeStyleConfigs(subStyleConf.fill, filledStyleConf.fill))
      } else {
        preparedOptions.fill = new ol.style.Fill(filledStyleConf.fill)
      }

      if (checkFor(subStyleConf, 'stroke')) {
        preparedOptions.stroke = new ol.style.Stroke(mergeStyleConfigs(subStyleConf.stroke, filledStyleConf.stroke))
      } else {
        preparedOptions.stroke = new ol.style.Stroke(filledStyleConf.stroke)
      }

      return preparedOptions
    }

    let styleOptions = addFillsAndStrokes(filledStyleConf)

    styleOptions.text = new ol.style.Text(addFillsAndStrokes(filledStyleConf.text))

    let scalable = false

    if (filledStyleConf.hasOwnProperty('image')) {
      if (filledStyleConf.image.type === 'icon' &&
        (filledStyleConf.image.hasOwnProperty('src')) && filledStyleConf.image.src) {
        styleOptions.image = new ol.style.Icon(filledStyleConf.image)
        scalable = true
      } else if (filledStyleConf.image.type === 'circle') {
        styleOptions.image = new ol.style.Circle(addFillsAndStrokes(filledStyleConf.image))
        scalable = true
      } else if (filledStyleConf.image.type === 'regularShape') {
        styleOptions.image = new ol.style.RegularShape(addFillsAndStrokes(filledStyleConf.image))
        scalable = true
      }

      if (scalable) {
        styleOptions.image.setScale((styleOptions.image.getScale() || 1) * this.getGlobalIconScale())
      }
    }

    return new ol.style.Style(styleOptions)
  }

  getConfigFromStyle (style) {
    throw new Error('Not implemented yet')
  }

  /**
   * @param {string} id
   * @returns {ol.style.Style}
   */
  getStyleById (id) {
    if (!this.styleMap_.has(id)) {
      if (this.styleConfigMap_.has(id)) {
        this.styleMap_.set(id, this.getStyleFromConfig(this.getConfigById(id)))
      } else {
        Debug.error('No style found for id ' + id + '. Using default style.')
        return this.styleMap_.get('#defaultStyle')
      }
    }
    return this.styleMap_.get(id)
  }

  /**
   * @param {string} id
   * @returns {StyleObject}
   */
  getConfigById (id) {
    if (this.styleConfigMap_.has(id)) {
      return this.styleConfigMap_.get(id)
    } else {
      Debug.error('No style config found for id ' + id + '. Using default style.')
      return this.styleConfigMap_.get('#defaultStyle')
    }
  }

  /**
   * @param {StyleLike} data
   * @returns {ol.style.Style}
   */
  getStyle (data) {
    if (data === undefined) {
      return this.getStyleById('#defaultStyle')
    } else if (data instanceof ol.style.Style || $.isFunction(data)) {
      return data
    } else if (typeof data === 'object') {
      return this.getStyleFromConfig(data)
    } else {
      return this.getStyleById(data)
    }
  }

  /**
   * converts ol.StyleFunction to ol.FeatureStyleFunction
   * @param {ol.style.StyleFunction} styleFunction
   * @returns {ol.FeatureStyleFunction}
   */
  convertStyleFunction (styleFunction) {
    return function (resolution) { return styleFunction(this, resolution) }
  }

  /**
   * This internal method is called to adjust each style to current global and feature settings
   * @param feature
   * @param style
   * @returns {ol.style.Style}
   * @private
   */
  adjustStyle_ (feature, style) {
    if (!feature.get('hidden')) {
      let clone = style.clone()
      this.scaleStyle_(clone)
      if (feature.get('opacity') !== undefined) {
        this.changeColorOpacity_(clone, feature.get('opacity'))
      }
      return clone
    } else {
      return this.nullStyle_
    }
  }

  /**
   * This method adjusts the scale of a style
   * @param style
   * @private
   */
  scaleStyle_ (style) {
    let image = style.getImage()
    if (image) {
      let origScale = style.getImage().getScale() || 1
      image.setScale(origScale * this.getGlobalIconScale())
    }
  }

  /**
   * adjust the styles opacity by a given value
   * @param {ol.style.Style} style
   * @param {number} opacity between 0 and 1
   * @returns {ol.style.Style}
   */
  changeColorOpacity_ (style, opacity) {
    let adjustColor = (style, opacity) => {
      let color = style.getColor()
      if (color !== null) {
        if (!(color instanceof Array)) {
          if (typeof color === 'string') {
            color = parseCSSColor(color)
          } else {
            throw new Error('Type not supported')
          }
        }

        color[3] = color[3] * opacity
      }
      style.setColor(color)
    }

    if (style.getImage()) {
      style.getImage().setOpacity(opacity)
    }
    if (style.getFill()) {
      adjustColor(style.getFill(), opacity)
    }
    if (style.getStroke()) {
      adjustColor(style.getStroke(), opacity)
    }
    if (style.getText()) {
      if (style.getText().getFill()) {
        adjustColor(style.getText().getFill(), opacity)
      }
      if (style.getText().getStroke()) {
        adjustColor(style.getText().getStroke(), opacity)
      }
    }
  }

  manageFeature (feature) {
    if (this.manageStyles_) {
      let style = feature.getStyle()
      if (style && !feature.get('managedStyle')) {
        feature.set('managedStyle', style)
        feature.setStyle(this.managingFeatureStyle_)
      }
    }
  }

  manageFeatureCollection (collection) {
    if (this.manageStyles_) {
      collection.forEach(feature => {
        this.manageFeature(feature)
      })

      collection.on('add', e => {
        this.manageFeature(e.element)
      })
    }
  }

  manageLayer (layer) {
    if (this.manageStyles_) {
      let style = layer.getStyle()

      if (style && !layer.get('managedStyle')) {
        layer.set('managedStyle', layer.getStyle())

        layer.setStyle((feature, resolution) => {
          let style = layer.get('managedStyle')
          if ($.isFunction(style)) {
            style = style(feature, resolution)
          }
          if (!style) {
            style = this.getStyle('#defaultStyle')
          }
          if ($.isArray(style)) {
            return style.map(s => this.adjustStyle_(feature, s))
          } else {
            return this.adjustStyle_(feature, style)
          }
        })
      }

      layer.getSource().getFeatures().forEach(feature => {
        this.manageFeature(feature)
      })

      layer.getSource().on('addfeature', e => {
        this.manageFeature(e.feature)
      })
    }
  }
}

import $ from 'jquery'
import { Debug } from './Debug'

/**
 * @typedef {object} URLConfig
 * @property {Localizable} url
 * @property {boolean} [useProxy]
 * @property {string} [proxy]
 * @property {boolean} [cache=true]
 * @property {string} [username] only implemented for wms at the moment
 * @property {string} [password] only implemented for wms at the moment
 */

/**
 * @typedef {URLConfig|Localizable|URL} URLLike
 */

export class URL {
  /**
   * @param {URLLike} urlLike
   * @param {G4UMap|null} map
   */
  constructor (urlLike, map) {
    if ($.type(urlLike) === 'string' || !urlLike.hasOwnProperty('url')) {
      /**
       * @type {string}
       */
      this.url = urlLike
      /**
       * @type {boolean}
       */
      this.useProxy = false
      /**
       * @type {boolean}
       */
      this.cache = true
      this.params = []
      this.expand = []
    } else {
      /**
       * @type {Localizable}
       */
      this.url = urlLike.url
      /**
       * @type {boolean}
       */
      this.useProxy = urlLike.useProxy
      /**
       * @type {string}
       */
      this.proxy = urlLike.proxy
      /**
       * @type {boolean}
       */
      this.cache = urlLike.cache === undefined ? true : urlLike.cache
      /**
       * @type {string}
       */
      this.username = urlLike.username
      /**
       * @type {string}
       */
      this.password = urlLike.password
      if (urlLike.params) {
        this.params = urlLike.params.slice(0)
      } else {
        this.params = []
      }
      if (urlLike.expand) {
        this.expand = urlLike.expand.slice(0)
      } else {
        this.expand = []
      }

      /**
       * @type {string}
       */
      this.globalProxy = urlLike.globalProxy
      /**
       * {L10N}
       */
      this.localiser = urlLike.localiser
    }

    if (map) {
      this.extractParamsFromMap(map)
    }
  }

  /**
   * @param {G4UMap} map
   */
  extractParamsFromMap (map) {
    /**
     * @type {string}
     */
    this.globalProxy = this.globalProxy || map.get('proxy')
    /**
     * {L10N}
     */
    this.localiser = this.localiser || map.get('localiser')
  }

  /**
   * @param {object} config
   * @param {string} paramName
   * @param {string} [defaultValue]
   * @param {G4UMap} [map]
   * @returns {URL}
   */
  static extractFromConfig (config, paramName, defaultValue, map) {
    if (!config.hasOwnProperty(paramName)) {
      return null
    }
    if (config.hasOwnProperty('useProxy') || config.hasOwnProperty('proxy') || config.hasOwnProperty('cache')) {
      Debug.warn('Using the parameters \'useProxy\' \'proxy\' \'cache\' directly inside a config object is considered' +
        ' deprecated. Please use a URLConfig object')
      return new URL({
        url: config[paramName] || defaultValue,
        useProxy: config.useProxy,
        proxy: config.proxy,
        cache: config.cache
      }, map)
    } else if ($.isPlainObject(config[paramName]) && !config[paramName].hasOwnProperty('url')) {
      Debug.warn('The url config object is missing an "url" parameter. The software is assuming the' +
        ' parameter is a Localizable.')
      return new URL({
        url: config[paramName]
      }, map)
    } else {
      return new URL(config[paramName], map)
    }
  }

  /**
   * @returns {URL}
   */
  clone () {
    return new URL(this)
  }

  /**
   * @returns {string}
   */
  finalize () {
    let url = this
    if (!this.cache) {
      url = this.clone().addParam(Math.random().toString(36).substring(7))
    }

    let urlAsString = this.localiser ? this.localiser.selectL10N(url.url) : url.url

    if (url.params.length) {
      if (urlAsString.search(/\?/) === -1) {
        urlAsString += '?'
      } else {
        urlAsString += '&'
      }
      urlAsString += url.params.join('&')
    }

    for (let expand of url.expand) {
      urlAsString = URL.expandTemplate_(urlAsString, expand)
    }

    if (url.useProxy === true || (url.useProxy === undefined && !!url.proxy)) {
      let proxy = url.proxy || this.globalProxy
      if (!proxy) {
        throw new Error('No proxy configured. Either configure a local or global proxy if you want to use the option' +
          ' useProxy.')
      }

      return URL.expandTemplate_(proxy,
        { paramName: 'url', paramValue: URL.encodeTemplate_(urlAsString), required: true })
    } else {
      return urlAsString
    }
  }

  /*
 * this function will add an parameter to the url
 * @param {string} param
 * @returns {URL)
 */
  addParam (param) {
    this.params.push(param)
    return this
  }

  /**
   * replaces a param enclosed in {} in a (url) template with a value. If the value is an array it will take any string
   * not containing a '}' after the paramName to join the array, default ','.
   * @param {string} template an (url) template
   * @param {object} expand
   * @param {string} expand.paramName the parameter that will be replaced (given without {}) f.e. 'example' will
   *    replace any occurancy of '{example}' (after the word 'example' there might be given a string join an
   *    array value i.e. '{example+}')
   * @param {string|string[]|number} expand.paramValue the value(s) which will be inserted
   * @param {boolean} expand.required
   * @returns {string} the expanded string
   */
  static expandTemplate_ (template, expand) {
    let regexp = new RegExp('{' + expand.paramName + '([^}]*)}')
    let match = template.match(regexp)
    if (match) {
      if ($.type(expand.paramValue) === 'string') {
        return template.replace(regexp, expand.paramValue)
      } else if ($.type(expand.paramValue) === 'array') {
        let joinString = match[1] || ','
        return template.replace(regexp, expand.paramValue.join(joinString))
      } else if ($.type(expand.paramValue) === 'number') {
        let valReg = new RegExp('(?::|,)([^,])', 'g')
        let nextMatch = valReg.exec(match[1])
        for (let i = 0; i < expand.paramValue; i++) {
          nextMatch = valReg.exec(match[1])
        }
        return template.replace(regexp, nextMatch[1])
      }
    } else if (expand.required) {
      throw new Error('required parameter ' + expand.paramName + ' (enclosed in {}) not found in string ' + template)
    } else {
      return template
    }
  }

  /**
   * expand the target template. automatically encodes the value
   * @param {string} paramName
   * @param paramValue
   * @param {boolean} [required=true]
   * @returns {URL}
   */
  expandTemplate (paramName, paramValue, required = true) {
    let index = this.expand.findIndex(e => e.paramName === paramName)
    if (index >= 0) {
      this.expand.splice(index, 1)
    }
    let encode = val => {
      if ($.type(val) === 'string') {
        return encodeURIComponent(val)
      } else if ($.type(paramValue) === 'array') {
        return val.map(v => encode(v))
      } else {
        return val
      }
    }
    paramValue = encode(paramValue)
    this.expand.push({ paramName, paramValue, required })
    return this
  }

  /**
   * @param {string} otherUrl
   * @returns {URL}
   */
  useProxyFor (otherUrl) {
    return new URL({
      useProxy: this.useProxy,
      proxy: this.proxy,
      url: otherUrl
    })
  }

  /**
   * this function takes an (url) template and encodes everything except for the templated elements.
   * @param {string} template an (url) template
   * @returns {string} the encoded (url) template
   */
  static encodeTemplate_ (template) {
    let parts = template.split('}')

    let encodedTemplate = ''

    let i
    for (i = 0; i < parts.length - 1; i += 1) {
      let partedParts = parts[i].split('{')
      encodedTemplate += encodeURIComponent(partedParts[0]) + '{' + partedParts[1] + '}'
    }

    encodedTemplate += encodeURIComponent(parts[i])

    return encodedTemplate
  }
}

/**
 * @module utilities
 * Helper and misc functions
 */

import $ from 'jquery'

import 'polyfill!Object.defineProperty'
import 'polyfill!Object.getOwnPropertyNames'
import 'polyfill!Object.getOwnPropertyDescriptor'
import 'polyfill!Object.getPrototypeOf'

import {Debug} from './Debug'

/**
 * Checks whether an argument can be interpreted as an even integer
 * @param   {Object}  value A value of any type
 * @returns {Boolean} True when value is numeric, parses as an integer (no matter if decimal, octal or sexadecimal)
 */
export function even (value) {
  if ($.isNumeric(value)) {
    let valueAsInteger = parseInt(value)
    if ((value === valueAsInteger) && (valueAsInteger % 2 === 0)) {
      return true
    }
  }
  return false
}

/**
 * Checks whether an argument can be interpreted as an even integer
 * @param   {Object}  value A value of any type
 * @returns {Boolean} True when value is numeric, parses as an integer (no matter if decimal, octal or sexadecimal)
 */
export function odd (value) {
  if ($.isNumeric(value)) {
    let valueAsInteger = parseInt(value)
    if ((value === valueAsInteger) && (valueAsInteger % 2 === 1)) {
      return true
    }
  }
  return false
}

/**
 * Check for a label in a configuration object
 * @param   {Object}  configurationObject a configuration object
 * @param   {String}  label               a label to check for
 * @returns {boolean} true if label present and true, false otherwise
 */
export function checkFor (configurationObject, label) {
  return ((label in configurationObject) && (configurationObject[label]))
}

/**
 * return argument as object (if it is no object the value is {})
 * @returns {Object}
 */
export function asObject (argument) {
  return (typeof argument === 'object') ? argument : {}
}

/**
 * Gets the subconfig or an empty object
 * @param {object} config
 * @param {string} name
 * @returns {object}
 */
export function getConfig (config, name) {
  if (config.hasOwnProperty(name) && config[name]) {
    return asObject(config[name])
  }
}

// //////////////////////////////////////////////////////////////////////////////////////// //
//                      async image load                                                    //
// //////////////////////////////////////////////////////////////////////////////////////// //

/**
 * @param {HTMLImageElement} image
 * @param {URL} origUrl
 * @param {string} [finalUrl]
 * @returns {Promise}
 */
export function asyncImageLoad (image, origUrl, finalUrl) {
  if (!finalUrl) {
    finalUrl = origUrl.finalize()
  }
  return new Promise((resolve, reject) => {
    image.addEventListener('load', resolve)
    image.addEventListener('error', reject)
    if (!origUrl.username || !origUrl.password) {
      image.src = finalUrl
    } else {
      let xhr = new XMLHttpRequest() // eslint-disable-line no-undef
      // xhr.open('GET', url, true, username, password)
      xhr.open('GET', finalUrl, true)
      xhr.responseType = 'blob'
      xhr.withCredentials = true

      xhr.setRequestHeader(origUrl.useProxy ? 'X-Proxy-Forward-Authorization' : 'Authorization',
        'Basic ' + btoa(origUrl.username + ':' + origUrl.password)) // eslint-disable-line no-undef

      xhr.addEventListener('load', function (e) {
        if (this.status === 200) {
          let urlCreator = window.URL || window.webkitURL
          image.src = urlCreator.createObjectURL(this.response)
        } else {
          reject(e)
        }
      })

      xhr.send()
    }
  })
}

// //////////////////////////////////////////////////////////////////////////////////////// //
//                      finish all ajax requests then continue                              //
// //////////////////////////////////////////////////////////////////////////////////////// //

/**
 * finishs loading all images contained in the given jQuery object.
 * @param {jQuery} $object
 * @returns {Promise}
 */
export function finishAllImages ($object) {
  let imagePromises = []

  let $images = recursiveSelect($object, 'img')

  $images.each(function () {
    let image = this

    if (!image.complete) {
      imagePromises.push(new Promise(resolve => {
        $(image).on('load', resolve)
        $(image).on('error', resolve)
      }))
    }
  })

  return Promise.all(imagePromises)
}

/**
 * calculates the distance between one and another jQuery element
 * @param {jQuery} $one
 * @param {jQuery} $other
 * @returns {{top: number, left: number}}
 */
export function offset ($one, $other) {
  let oneOff = $one.offset()
  let otherOff = $other.offset()
  return { top: oneOff.top - otherOff.top, left: oneOff.left - otherOff.left }
}

// //////////////////////////////////////////////////////////////////////////////////////////
//                                   jQuery Extensions                                    //
// //////////////////////////////////////////////////////////////////////////////////////////

/**
 * selects all matching elements and child elements
 * @param {jQuery} $elem
 * @param {string} query
 * @returns {jQuery}
 */
export function recursiveSelect ($elem, query) {
  return $elem.filter(query).add($elem.find(query))
}

// //////////////////////////////////////////////////////////////////////////////////////// //
//                                Structural Functions                                      //
// //////////////////////////////////////////////////////////////////////////////////////// //

export function showInteractionActivity (map) {
  Debug.info('superseding interactions:')
  let k, i
  let total
  let amountActive

  for (k in map.supersedingInteractions_) {
    total = map.supersedingInteractions_[k].length
    amountActive = 0
    for (i = 0; i < total; i++) {
      if (map.supersedingInteractions_[k][i].getActive()) {
        amountActive += 1
      }
    }

    Debug.info('  ' + k + ': total: ' + total + ' active: ' + amountActive)
  }

  Debug.info('default interactions:')

  for (k in map.defaultInteractions_) {
    total = map.defaultInteractions_[k].length
    amountActive = 0
    for (i = 0; i < total; i++) {
      if (map.defaultInteractions_[k][i].getActive()) {
        amountActive += 1
      }
    }

    Debug.info('  ' + k + ': total: ' + total + ' active: ' + amountActive)
  }
}

// //////////////////////////////////////////////////////////////////////////////////////// //
//                                     URL Functions                                        //
// //////////////////////////////////////////////////////////////////////////////////////// //

// the functions are designed to mimic the behaviour of the node path module.
// differences: dirURLs will end in /'s
// the are in no way complete and don't claim to be complete
// NOTE: another way to solve this would be using String.split instead of regular expressions

/**
 * A function that tries to get the dir url of an url
 * @param {string} url
 * @returns {string}
 */
export function urlDirname (url) {
  return url.replace(/\/([^/]*)(\?.*)?$/, '/')
}

/**
 * A function that normalizes a url
 * @param {string} url
 * @returns {string}
 */
export function urlNormalize (url) {
  if (url.match(/^\.\//)) {
    return urlNormalize(url.replace(/^\.\//, ''))
  } else {
    return url
  }
}

/**
 * A function that adds urls
 * @param {string} urlRoot
 * @param {string} urlExt
 * @returns {string}
 */
export function urlJoin (urlRoot, urlExt) {
  let normPathRoot = urlDirname(urlNormalize(urlRoot))
  let normPathExt = urlNormalize(urlExt)

  let lastPart = /[^/]+\/$/
  let leadingDoubleDots = /^\.\.\//

  while (normPathRoot.match(lastPart) && normPathExt.match(leadingDoubleDots)) {
    normPathRoot = normPathRoot.replace(lastPart, '')
    normPathExt = normPathExt.replace(leadingDoubleDots, '')
  }

  return normPathRoot + normPathExt
}

/**
 * A function that tries to get the relative url between to urles
 * @param {string} source
 * @param {string} target
 * @returns {string}
 */
export function urlRelative (source, target) {
  let sourceNorm = urlDirname(urlNormalize(source))
  let targetNorm = urlNormalize(target)

  let urlRelative = ''

  let firstPart = /^\/?((\/\/)|[^/])+\//

  let firstSourcePart
  let firstTargetPart

  while (sourceNorm.match(firstPart) && targetNorm.match(firstPart)) {
    firstSourcePart = sourceNorm.match(firstPart)[0]
    firstTargetPart = targetNorm.match(firstPart)[0]

    if (firstSourcePart.toUpperCase() !== firstTargetPart.toUpperCase()) {
      break
    }

    sourceNorm = sourceNorm.replace(firstPart, '')
    targetNorm = targetNorm.replace(firstPart, '')
  }

  while (sourceNorm.match(firstPart)) {
    firstSourcePart = sourceNorm.match(firstPart)[0]
    sourceNorm = sourceNorm.replace(firstPart, '')
    urlRelative += '../'
  }

  return urlRelative + targetNorm
}

/**
 * @param {string} url
 * @returns {boolean}
 */
export function urlIsAbsolute (url) {
  return !!url.match(/^\/|.*:\/\//)
}

